[7491] = {"Para que todos lo vean", {"Busca al señor supremo Runthak en el Valle de la Fuerza."}},
[7492] = {"Campamento Mojache", {"Ve al Campamento Mojache de Feralas y habla con Talo Pezuñahendida."}},
