[7701] = {"SE BUSCA: Sobrestante Maltorius", {"El traidor Hierro Negro llamado sobrestante Maltorius está en La Fosa de la Escoria de La Caldera, en La Garganta de Fuego.","","Mátalo y lleva su cabeza al capitán de vigía Lolo Zancalarga a La Garganta de Fuego para recibir tu recompensa."}},
[7703] = {"Asunto Gordok sin finiquitar", {"Encuentra el guante del Poderío de Gordok y llévaselo al capitán Kromcrush a La Masacre.","","Según Kromcrush, una \"historia muy, muy vieja\" dice que Tortheldrin, un elfo \"asqueroso\" que se llamaba a sí mismo príncipe, robó el guantelete a uno de los reyes de Gordok."}},
[7704] = {"¡Mira el tamaño de esto!", {"Lleva la almohada de la ayudante de cámara Pilaprieta a Evonis Fumollín a La Garganta de Fuego."}},
[7721] = {"Combustible para el Ultra-reductor", {"Reúne 10 núcleos de elemental de agua de los elementales marinos y de los rocíos marinos que hay en La Costa Olvidada de Feralas, y llévaselos a Zorbin Hipnoviento, que está cerca del embarcadero."}},
[7722] = {"¿Qué pasa con el flujo?", {"Busca los diseños secretos: flujo ígneo robados por el sobrestante Maltorius en La Fosa de la Escoria de La Caldera. Llévaselos al herrero maestro Burninate a La Garganta de Fuego."}},
[7723] = {"¡Malditos dedos gordos!", {"Hansel Manospesadas quiere que mates 20 gólems de guerra pesados.","","Suelen estar cerca de La Caldera en La Garganta de Fuego. Después ve a ver a Hansel a La Garganta de Fuego."}},
[7724] = {"Amenaza abrasadora", {"Hansel Manospesadas quiere que mates 20 arañas de lava superiores en La Garganta de Fuego. Las encontrarás en las mesetas y los estanques de lava de La Garganta de Fuego.","","Cuando hayas matado suficientes arañas de lava superiores ve a ver a Hansel Manospesadas a La Garganta de Fuego."}},
[7725] = {"Encoge más gigantes", {"Utiliza el Ultra-reductor de Zorbin para encoger a cualquier gigante que encuentres en Feralas a un tamaño más manejable. Lleva 10 residuos de miniaturización encontrados en las versiones encogidas de los gigantes a Zorbin Hipnoviento, al embarcadero de La Costa Olvidada de Feralas."}},
[7726] = {"Más combustible para el Ultra-reductor", {"Reúne 6 núcleos de elemental de agua de los elementales marinos y los rocíos marinos que hay en La Costa Olvidada de Feralas, y llévaselos a Zorbin Hipnoviento, que está cerca del embarcadero."}},
[7727] = {"¿Incendosaurio? Loqueseasaurio más bien", {"Hansel Manospesadas quiere que mates 20 incendosaurios.","","Suelen estar en los estanques de lava de La Fosa de la Escoria. La Fosa de la Escoria está en La Caldera de La Garganta de Fuego.","","Cuando hayas matado suficientes incendosaurios ve a ver a Hansel Manospesadas a La Garganta de Fuego."}},
[7728] = {"OBJETOS ROBADOS: Tobera y catalejo", {"Encuentra la tobera y el catalejo y llévaselos al capataz Scrange a La Garganta de Fuego.","","Lo único que sabes es que fueron robados por enanos Hierro Negro. La tobera es una herramienta de herrero y el catalejo es un instrumento muy valioso para los oteadores."}},
[7729] = {"OPORTUNIDAD LABORAL: Acabar con la competencia", {"Mata a 15 Capataces y 15 esclavistas Hierro Negro y ve a ver al capataz Scrange a La Garganta de Fuego."}},
[7730] = {"Infestación de Zukk'ash", {"Lleva 20 caparazones Zukk'ash a Hadoken Zanco Veloz al Campamento Mojache en Feralas."}},
[7731] = {"Latigador con aguijón", {"Derrota al latigador con aguijón en Las Galerías Retorcidas y lleva sus glándulas a Hadoken Zanco Veloz al Campamento Mojache en Feralas."}},
[7732] = {"El informe Zukk'ash", {"Entrega a Zilzibin Tambor Sabio el informe del campamento Mojache Zukk'ash. Reside en La Calle Mayor de Orgrimmar."}},
[7733] = {"Calidad mejorada", {"Lleva 10 pellejos de yeti Cicatriz de Rabia a Pratt McGrubben al Bastión Plumaluna en Feralas."}},
[7734] = {"Calidad mejorada", {"Lleva 10 pellejos de yeti Cicatriz de Rabia a Jangdor Zanco Veloz al Campamento Mojache de Feralas."}},
[7735] = {"Pellejo de yeti prístino", {"Lleva el pellejo de yeti prístino a Pratt McGrubben al Bastión Plumaluna de Feralas."}},
