[7737] = {"Conseguir aceptación", nil},
[7738] = {"Pellejo de yeti perfecto", {"Lleva los pellejos de yeti perfecto a Jangdor Zanco Veloz al Campamento Mojache de Feralas."}},
[7761] = {"Orden de Puño Negro", {"¡Será estúpido ese orco! Al parecer, tienes que encontrar la enseña y ganar la marca de Drakkisath para poder acceder al orbe de orden.","","Según la carta, el general Drakkisath guarda la enseña. Quizás deberías investigarlo."}},
[7781] = {"Señor de Roca Negra", {"Llévale la cabeza de Nefarian al alto señor Bolvar Fordragón en Ventormenta."}},
[7782] = {"Señor de Roca Negra", {"Vete al Valle de los Héroes, en la Ciudad de Ventormenta, y habla con el mariscal de campo Petraponte."}},
[7783] = {"Señor de Roca Negra", {"Llévale la cabeza de Nefarian a Thrall en Orgrimmar."}},
[7784] = {"Señor de Roca Negra", {"Habla con el señor supremo Colmillosauro en Orgrimmar. Te espera en el Valle de la Fuerza."}},
[7785] = {"Examina la vasija", {"Examina la vasija de renacer y devuélvesela al alto señor Demitrian."}},
[7786] = {"Thunderaan el Hijo del Viento", {"Para liberar a Thunderaan el Hijo del Viento de su cárcel, debes entregarle al alto señor Demitrian en Silithus la mitad izquierda y la derecha de las ataduras del Hijo del Viento, 10 barras de elementium y la esencia del Señor del Fuego."}},
[7787] = {"¡Arriba, Trueno Furioso!", {"Lleva la hoja besada por el viento durmiente al alto señor Demitrian."}},
[7788] = {"¡Expulsa al invasor!", {"Entra en la Garganta Grito de Guerra y derrota a la Horda, obtén una marca de Honor Grito de Guerra y vuelve con la centinela Cantolejano en el Claro Ala de Plata."}},
[7789] = {"Acaba con los usurpadores Ala de Plata", {"Entra en la Garganta Grito de Guerra y derrota a la Alianza, obtén una marca de Honor de la Garganta Grito de Guerra y llévasela al capitán Trizacráneo en el Campamento de Mor'shan."}},
