[8733] = {"Eranikus, el Tirano del Sueño", {"Viaja al continente de Teldrassil y busca al agente de Malfurion. Lo encontrarás a lo largo de los muros de Darnassus."}},
[8734] = {"Tyrande y Remulos", {"Ve a Claro de la Luna y habla con el guardián Remulos."}},
[8735] = {"La corrupción de Pesadilla", {"Ve a los 4 Portales del Sueño Esmeralda de Azeroth y recoge un fragmento de Corrupción de Pesadilla en cada uno. Habla otra vez con el guardián Remulos en Claro de la Luna cuando hayas completado la tarea."}},
[8736] = {"La Pesadilla se manifiesta", {"Defiende Amparo de la Noche de Eranikus. No dejes morir al guardián Remulos. No mates a Eranikus. Defiéndete. Espera a Tyrande."}},
[8737] = {"Templario azur", "Invoca y mata a un Templario azur y preséntate ante Bor Ferocrín en el Fuerte Cenarion. También debes llevar el Informe de tareas tácticas I para poder completar la misión."},
[8738] = {"Informe de exploración de Colmen'Regal", {"Contacta con el explorador Cenarion Landion en Colmen'Regal y entrega el informe de exploración de Colmen'Regal a Clamavientos Cuerno Digno en el Fuerte Cenarion. También debes llevar el Informe de tareas tácticas VII para poder completar la misión."}},
[8739] = {"Informe de exploración de Colmen'Ashi", {"Contacta con el explorador Cenarion Jalia en Colmen'Ashi y entrega el informe de exploración de Colmen'Ashi a Clamavientos Cuerno Digno en el Fuerte Cenarion. También debes llevar el Informe de tareas tácticas VIII para poder completar la misión."}},
[8740] = {"Los merodeadores Crepusculares", {"Mata a la Maleante Crepuscular Morna y a 5 Maleantes Crepusculares. Cuando termines tu tarea, entrégale el informe al Clamavientos Cuerno Digno. También debes llevar las Instrucciones de la misión táctica IX para poder completar la misión."}},
[8741] = {"El regreso del Campeón", {"Lleva el fragmento de cetro verde a Anacronos a las Cavernas del Tiempo, en Tanaris."}},
