[4961] = {"Purifie l'Orbe d'Orahil", {"Tuer le Démon de l’Orbe, puis parler à Tabetha."}},
[4962] = {"Un éclat de gangrechien", {"Prendre le Rubis de Felhas et l’utiliser sur l’un des gangrechiens de Désolace. Quand vous aurez réussi, rapporter le Rubis de Felhas et l’Esprit de gangrechien emprisonné à Menara Voidrender, dans les Tarides."}},
[4963] = {"Eclat d'Infernal", {"Prendre l’Emeraude d'Infus et l’utiliser sur les Infernaux qui se trouvent en Désolace. Quand vous aurez réussi, rapporter l’Emeraude d'Infus et l’Esprit d'Infernal emprisonné à Menara Voidrender, dans les Tarides."}},
[4964] = {"L'Orbe complet de Dar'Orahil", {"Attendre que Menara Voidrender termine l’Orbe de Dar'Orahil, puis lui reparler."}},
[4965] = {"Connaissance de l'Orbe d'Orahil", {"Parler à Menara Voidrender dans les Tarides."}},
[4966] = {"Protéger Kanati Greycloud", {"Protéger Kanati Greycloud contre l’attaque centaure."}},
[4967] = {"Connaissance de l'Orbe d'Orahil", {"Parler à Menara Voidrender dans les Tarides."}},
[4968] = {"Connaissance de l'Orbe d'Orahil", {"Parler à Menara Voidrender dans les Tarides."}},
[4969] = {"Connaissance de l'Orbe d'Orahil", {"Parler à Menara Voidrender dans les Tarides."}},
[4970] = {"Provisions de sabres-de-givre", {"Récolter 5 Viandes de Croc acéré et 5 Viandes de Noroît pour Rivern Frostwind du Berceau-de-l'Hiver."}},
[4971] = {"Une question de temps", {"Utiliser le Déplaceur temporel à côté de l’un des silos d’Andorhal pour révéler des Parasites temporels.","Tuer 15 Parasites temporels et rapporter le Déplaceur temporel à Chromie, à l’auberge d’Andorhal, dans les Maleterres de l’ouest."}},
[4972] = {"Arrêt du temps", {"Trouver 5 Montres d'Andorhal, dans des coffrets qui se trouvent parmi les décombres de la cité, et les rapporter à Chromie à l’Auberge d’Andorhal, dans les Maleterres de l’ouest."}},
[4973] = {"Arrêt du temps", {"Trouver 5 Montres d'Andorhal parmi les décombres de la cité, et les rapporter à Chromie à l’Auberge d’Andorhal, dans les Maleterres de l’ouest."}},
[4974] = {"Pour la Horde !", {"Aller au Pic Blackrock et tuer le Chef de guerre, Rend Blackhand. Prendre sa tête et retourner à Orgrimmar."}},
[4975] = {"L'Orbe complet de Noh'Orahil", {"Attendre que Menara Voidrender ait terminé l’Orbe de Noh'Orahil, puis lui reparler."}},
[4976] = {"Rapporter l'Orbe purifié", {"Rapporter l’Orbe infernal purifié à Menara Voidrender, dans les Tarides."}},
[4981] = {"Agent Bijou", {"Aller au Pic Blackrock et découvrir ce qui est arrivé à Bijou."}},
[4982] = {"Les effets de Bijou", {"Trouver les Affaires de Bijou et les lui rapporter. Elle vous a dit les avoir cachées dans le niveau inférieur de la ville."}},
[4983] = {"Rapport de reconnaissance de Bijou", {"Rapporter le Rapport de reconnaissance de Bijou au Grand-maître Lexlort, à Kargath."}},
[4984] = {"La faune et la flore souffrent aussi", {"Détruire 8 Loups malades, puis retourner voir Mulgris Deepriver, au Repaire putride dans les Maleterres de l’ouest."}},
[4985] = {"La faune et la flore en souffrent aussi", {"Détruire 8 Grizzlis malades, puis retourner voir Mulgris Deepriver au Repaire putride, dans les Maleterres de l’ouest."}},
[4986] = {"La branche de chêne à glyphes", {"D'après la magie contenue dans la Branche de chêne à glyphes, le druide tauren voulait qu'elle soit apportée au Cercle cénarien, à Darnassus. Aller solliciter l’assistance de l’un des druides, là-bas."}},
[4987] = {"La branche de chêne à glyphes", {"D'après la magie contenue dans la Branche de chêne à glyphes, le druide tauren voulait qu'elle soit apportée au Cercle cénarien, à Thunder Bluff. Aller solliciter l’assistance de l’un des druides, là-bas."}},
[5001] = {"Les effets de Bijou", {"Retrouver les Affaires de Bijou et les lui rapporter. Bonne chance !"}},
[5002] = {"Message à Maxwell", {"Se rendre dans les Steppes ardentes et confier les Renseignements de Bijou au maréchal Maxwell."}},
[5021] = {"Mieux vaut tard que jamais", {"Le discours incohérent de la femme fantomatique indique qu’elle a besoin qu’on livre un colis pour elle. Elle a dit que le colis se trouvait là où se trouve son cheval. Le fantôme est incompréhensible lorsqu’il s’agit de préciser où se trouve son cheval ou l’endroit où le paquet doit être livré."}},
[5022] = {"Mieux vaut tard que jamais", {"Rendre visite aux Facteurs royaux de Stormwind pour apprendre l’adresse d’une certaine Emma Felstone. En général, il y a un officier du recensement à l’Hôtel de ville."}},
[5023] = {"Mieux vaut tard que jamais", {"Se renseigner auprès des Surveillants royaux d’Undercity pour apprendre l’adresse d’un certain Jeremiah Felstone. En général, il y a un officier du recensement près du bureau d’inscription des guildes et tabards."}},
[5041] = {"Ravitaillement pour la Croisée", {"Trouver et rapporter les Caisses de ravitaillement de la Croisée à Thork dans les Tarides."}},
[5042] = {"La force d’Agammagan", nil},
[5043] = {"L'agilité d'Agammagan", nil},
[5044] = {"La sagesse d'Agammagan", nil},
[5045] = {"L'essor de l'esprit", nil},
[5046] = {"Peau tranchante", nil},
[5047] = {"Pépin Preste-Jugeote, à votre service !", {"Parler à Malyfous Darkhammer à Long-guet."}},
[5048] = {"Emma la bonne pâte", {"Trouver la vieille Emma à Stormwind, et découvrir si elle est en fait Emma Felstone. Si c’est le cas, elle appréciera peut-être le colis que Janice Felstone lui a préparé."}},
[5049] = {"Le blues de Jeremiah", {"Trouver Jeremiah Payson dans Undercity et découvrir s’il est en fait Jeremiah Felstone. Si c’est le cas, il appréciera peut-être le colis que Janice Felstone lui a préparé."}},
[5050] = {"Charme porte-bonheur", {"Rapporter son Demi-charme porte-bonheur à Janice Felstone dans les Maleterres de l’ouest."}},
[5051] = {"Deux moitiés deviennent Une", {"Trouver l’autre moitié du Charme porte-bonheur quelque part dans le périmètre de la Ferme Felstone dans les Maleterres de l’ouest et le recoller.","Donner le Charme porte-bonheur à Janice Felstone à la Ferme Felstone, dans les Maleterres de l’ouest."}},
[5052] = {"Joyaux de sang d'Agammagan", {"Apporter un Joyau de sang à Mangletooth, à Camp Taurajo, dans les Tarides.","Collectez plus de Joyaux de sang, si vous souhaitez qu’il vous gratifie de la puissance d’Agamaggan."}},
[5054] = {"Ursius des Crocs acérés", {"Storm Shadowhoof, à Long-guet au Berceau-de-l'Hiver, veut que vous tuiez Ursius."}},
[5055] = {"Brumeran des Noroît", {"Storm Shadowhoof, à Long-guet au Berceau-de-l'Hiver, veut que vous tuiez Brumeran."}},
[5056] = {"Shy-Rotam", {"Storm Shadowhoof, à Long-guet au Berceau-de-l'Hiver, veut que vous triomphiez de Shy-Rotam."}},
[5057] = {"Essais passés", {"Apporter le Marqueur de Storm Shadowhoof à Melor Stonehoof à Thunder Bluff."}},
[5058] = {"Journal de Mme Dalson", nil},
[5059] = {"Enfermé", nil},
[5060] = {"Enfermé", nil},
[5061] = {"Forme aquatique", {"Retourner à Darnassus et montrer le Pendentif du lion de mer à Mathrengyl Bearwalker."}},
[5062] = {"Feu sacré", {"Ramasser 10 boisseaux d'agave d'Incendia, puis consulter Magatha Grimtotem sur la Cime des Anciens dans Thunderbluff."}},
[5063] = {"La coiffe du savant écarlate", {"Pour créer la Coiffe du savant écarlate, il faut rapporter les éléments suivants à Malyfous Darkhammer : 1 Peau de la Bête en parfait état ; 5 Coutures d'Abomination effilochées ; 8 Cristaux des arcanes ; 5 Fils écarlates enchantés.","Il faudra aussi payer 50 pièces d’or pour le travail de fabrication."}},
[5064] = {"Espionner le Totem sinistre", {"Trouver et récupérer les trois Notes Secrètes à la Cime de Noir-nuage."}},
[5065] = {"Les tablettes perdues de Mosh'aru", {"Apporter la troisième et la quatrième tablette Mosh'aru au Prospecteur Ironboot à Tanaris."}},
[5066] = {"Un appel aux armes : les Maleterres", {"Trouver le commandant Ashlam Valorfist. Son camp de base est situé au camp du Noroît, au nord des montagnes d'Alterac."}},
[5067] = {"Jambières d'Arcana", {"Pour fabriquer les Jambières d'Arcana, il faut rapporter les éléments suivants à Malyfous Darkhammer : 1 Peau de la Bête en parfait état ; 5 Coutures d'Abomination effilochées ; 5 Barres d'arcanite ; 5 Fluides d'embaumement de Murmegivre.","Il faudra également payer 50 pièces d’or pour le travail de fabrication."}},
[5068] = {"La cuirasse carnassière", {"Pour fabriquer la Cuirasse carnassière, il faut rapporter les éléments suivants à Malyfous Darkhammer : 1 Peau de la Bête en parfait état ; 10 Coutures d'Abomination effilochées ; 5 Barres d'arcanite ; 5 Peaux des ombres.","Il faudra également payer 50 pièces d’or pour le travail de fabrication."}},
[5081] = {"Mission de Maxwell", {"Se rendre au Pic Blackrock et tuer le maître de guerre Voone, le généralissime Omokk, et le seigneur Wyrmthalak. Retourner auprès du maréchal Maxwell quand la tâche a été accomplie."}},
[5082] = {"La menace des Tombe-hiver", {"Donova Snowden du Berceau-de-l'Hiver veut que vous tuiez 8 Guides, 8 Protecteurs et 8 Totémiques de la tribu Tombe-hiver."}},
[5083] = {"L'eau de feu de Tombe-hiver", {"Apporter le Flacon d'eau de feu vide à Donova Snowden du Berceau-de-l'Hiver."}},
[5084] = {"Corrompu", {"Chercher un indice dans le camp des furbolgs Mort-bois, dans le nord de Gangrebois."}},
[5085] = {"Gelée mystère", {"Aller faire un rapport de vos trouvailles à Donova Snowden."}},
[5086] = {"Horreurs toxiques", {"Récolter 3 Gouttelettes d'Horreur toxique pour Donova Snowden au Berceau-de-l'Hiver."}},
[5087] = {"Les messagers des Tombe-hiver", {"Trouver les Messagers Tombe-hiver et les empêcher de livrer leur cargaison au village Tombe-hiver."}},
[5088] = {"Arikara", {"Tuer Arikara. Rapporter sa dépouille et de la poudre d’incendia à Motega Firemane au Poste de Blanc-relais comme preuve de votre acte."}},
[5089] = {"Ordres du général Drakkisath", {"Remettre les Ordres du général Drakkisath au maréchal Maxwell dans les Steppes ardentes."}},
[5090] = {"Un appel aux armes : les Maleterres", {"Trouver le commandant Ashlam Valorfist. Son camp de base est le Camp du Noroît, au nord des montagnes d'Alterac."}},
[5091] = {"Un appel aux armes : les Maleterres", {"Trouver le commandant Ashlam Valorfist. Il s'est établi au Camp du Noroît, au nord des montagnes d'Alterac."}},
[5092] = {"Nettoyer le passage", {"Tuer 10 Ecorcheurs Squelettes et 10 Goules écumantes sur la Colline des chagrins.","Retourner auprès du commandant Ashlam Valorfist au camp du Noroît, dans les Maleterres de l’ouest."}},
[5093] = {"Un appel aux armes : les Maleterres", {"Partir à la recherche du Grand exécuteur Derrington. Son camp de base se trouve à la Barricade, à l’est des Clairières de Tirisfal et d’Undercity."}},
[5094] = {"Un appel aux armes : les Maleterres", {"Partir à la recherche du Grand exécuteur Derrington. Son camp de base se trouve à la Barricade, à l’est des Clairières de Tirisfal et d’Undercity."}},
[5095] = {"Un appel aux armes : les Maleterres", {"Partir à la recherche du Grand exécuteur Derrington. Son camp de base se trouve à la Barricade, à l’est des Clairières de Tirisfal et d’Undercity."}},
[5096] = {"Diversions écarlates", {"Se rendre au camp de base de la Croisade écarlate situé entre le Champ de Felstone et les Larmes de Dalson et détruire leur tente d’état-major.","Placer la bannière du Fléau sur le camp, puis revenir auprès du Grand exécuteur Derrington à la Barricade, dans les Maleterres de l’ouest."}},
[5097] = {"Tout au long des tours de guet", {"À l’aide de la Torche-balise, marquer chaque tour à Andorhal ; il faut se tenir dans l’embrasure de la porte de la tour pour pouvoir la marquer.","Quand les quatre tours seront marquées, rapporter la Torche-balise au commandant Ashlam Valorfist au camp du Noroît, dans les Maleterres de l’ouest."}},
[5098] = {"Tout au long des tours de guet", {"À l’aide de la Torche-balise, marquer chaque tour à Andorhal ; il faut se tenir dans l’embrasure de la porte de la tour pour pouvoir la marquer.","Quand les quatre tours seront marquées, rapporter la Torche-balise au Grand exécuteur Derrington à la Barricade, dans les Maleterres de l’ouest."}},
[5102] = {"La reddition du général Drakkisath", {"Rendez-vous au Pic Blackrock et tuez le général Drakkisath, puis retournez voir le maréchal Maxwell."}},
[5103] = {"Mort brûlante", {"Quelqu’un dans ce monde doit bien savoir à quoi servent ces gantelets. Bonne chance !"}},
[5121] = {"Le grand chef Tombe-hiver", {"Donova Snowden du Berceau-de-l'Hiver veut que vous triomphiez du Grand chef Tombe-hiver."}},
[5122] = {"Le médaillon de foi", nil},
[5123] = {"Le dernier morceau", {"Apporter le Journal mal écrit à Donova Snowden au Berceau-de-l'Hiver."}},
[5124] = {"Gantelets en plaque de feu", {"Apporter à Malyfous Darkhammer 6 Barres de thorium enchanté, 2 Essences de feu, et 4 Rubis stellaires. Lui remettre également vos Gantelets en plaques inachevés."}},
[5125] = {"L'estimation d'Aurius", nil},
[5126] = {"L'histoire de Lorax", {"Parler à Lorax. Écouter ce qu’il a à dire."}},
[5127] = {"La forge démoniaque", {"Se rendre au pic Blackrock et trouver Goraluk Anvilcrack. Le tuer puis utiliser la Pique tachée de sang sur son cadavre. Après avoir siphonné son âme, la pique sera devenue Tachée d'âme.","Trouver aussi la Cuirasse couverte de runes inachevée.","Rapporter la Pique tachée d'âme et la Cuirasse couverte de runes inachevée à Lorax au Berceau-de-l'Hiver."}},
[5128] = {"Paroles du Grand Chef", {"Rapporter le Journal mal écrit à Kelek Skykeeper dans le sud de Gangrebois."}},
[5141] = {"Travail du cuir de dragon", {"Apporter 2 Cuirasses armées du scorpide, 2 Gants armés du scorpide et 10 Ecailles de dragon usées à Peter Galen, à Azshara.","Terminer cette quête vous donnera accès aux arts du travail du cuir en écailles de dragon.","Terminer cette quête avec succès vous interdira l’apprentissage du travail du cuir élémentaire et du travail du cuir tribal. Prenez le temps de réfléchir à la voie que vous souhaitez emprunter, avant de prendre une décision."}},
[5142] = {"La petite Pamela", {"Retrouver Pamela Redpath."}},
[5143] = {"Travail du cuir tribal", {"Apporter un Gilet en cuir sauvage et un Casque en cuir sauvage à Caryssia Moonhunter en Feralas.","L’accomplissement de cette quête donne accès aux arts du travail du cuir tribal.","L’accomplissement de cette quête vous empêchera d’apprendre le travail du cuir de dragon et le travail du cuir élémentaire ; il faut être sûr du chemin que l’on veut suivre avant de faire ce choix."}},
[5144] = {"Travail du cuir élémentaire", {"Apporter 2 Coeurs de feu, 2 Globes d’eau, 2 Noyaux de terre, et 2 Souffles de vent à Sarah Tanner dans la Gorge des Vents brûlants.","L’accomplissement de cette quête donne accès aux arts du Travail du cuir élémentaire.","L’accomplissement de cette quête vous empêchera d’apprendre le Travail du cuir de dragon et le Travail du cuir tribal ; il faut être sûr du chemin que l’on veut suivre avant de faire ce choix."}},
[5145] = {"Travail du cuir de dragon", {"Apporter 2 Cuirasses armées du scorpide, 2 Gants armés du scorpide et 10 Ecailles de dragon usées à Thorkaf Dragoneye, dans les Terres ingrates.","Terminer cette quête vous donnera accès aux arts du travail du cuir en écailles de dragon.","Terminer cette quête avec succès vous interdira l’apprentissage du travail du cuir élémentaire et du travail du cuir tribal. Prenez le temps de réfléchir à la voie que vous souhaitez emprunter, avant de prendre une décision."}},
[5146] = {"Travail du cuir élémentaire", {"Apporter 2 Coeurs de feu, 2 Globes d'eau, 2 Noyaux de terre, et 2 Souffles de vent à Brumn Winterhoof dans les montagnes d’Arathi.","L’accomplissement de cette quête donne accès aux arts du travail du cuir élémentaire.","L’accomplissement de cette quête vous empêchera d’apprendre le travail du cuir de dragon et le travail du cuir tribal ; il faut être sûr du chemin que l’on veut suivre avant de faire ce choix."}},
[5147] = {"On recherche : Arnak Grimtotem", {"Tuer Arnak Grimtotem et apporter une preuve de votre acte au garde-falaise Longhorn au Poste de Librevent."}},
[5148] = {"Travail du cuir tribal", {"Apporter un Gilet en cuir sauvage et un Casque en cuir sauvage à Se'Jib dans la vallée de Strangleronce.","L’accomplissement de cette quête donne accès aux arts du travail du cuir tribal.","L’accomplissement de cette quête vous empêchera d’apprendre le travail du cuir de dragon et le travail du cuir élémentaire ; il faut être sûr du chemin que l’on veut suivre avant de faire ce choix."}},
[5149] = {"Poupée de Pamela", {"Trouver la poupée de Pamela."}},
[5150] = {"Dadanga a faim !", nil},
[5151] = {"Hypercapaciteur bidulotron", {"Tuer la bête et rapporter l'Hypercapaciteur et la Clé de la cage de la panthèse à Wizlo Bearingshiner."}},
[5152] = {"Tante Marlene", {"Parler à Marlene, au sud des ruines d'Andorhal."}},
[5153] = {"Un étrange historien", {"Apporter l’Alliance de Joseph à Chromie."}},
[5154] = {"Les annales de Darrowshire", {"Apporter les Annales de Darrowshire à Chromie, à Andorhal."}},
[5155] = {"Les forces de Jaedenar", {"Entrer dans Jaedenar et tuer 4 Molosses de Jaedenar, 4 Gardiens de Jaedenar, 6 Adeptes de Jaedenar, et 6 Sectateurs de Jaedenar avant de retourner auprès de Greta Mosshoof dans Gangrebois."}},
[5156] = {"Enquête sur la corruption", {"Trouver et détruire 2 Bêtes d'entropie et 2 Horreurs d'entropie tout en explorant le Val Grêlé. Après avoir vérifié que les cratères à cet endroit sont toujours remplis d’eau corrompue, retourner auprès de Taronn Redfeather dans Gangrebois."}},
[5157] = {"Recueillir de l'eau souillée", {"Amener la Gourde vide au Puits de lune au centre de Jaedenar et la remplir avant de retourner voir Greta Mosshoof dans Gangrebois."}},
[5158] = {"À la recherche de soutien spirituel", {"Apporter l’Eau de Puits de lune corrompue à Islen Waterseer dans les Tarides."}},
[5159] = {"L'eau purifiée retourne à Gangrebois", {"Apporter l’Eau de Puits de lune purifiée à Greta Mosshoof dans Gangrebois."}},
[5160] = {"Le Protectorat de la matrone", {"Aller jusqu’au Berceau-de-l'Hiver et trouver Haleh. Lui donner l’Ecaille d'Awbee."}},
[5161] = {"La colère du vol bleu", {"Si vous souhaitez continuer, parlez à Haleh."}},
[5162] = {"La colère du vol bleu", {"Parler à Jeziba dans les Maleterres. Il vit à Andorhal."}},
[5163] = {"Yéti ? Y es-tu ?", {"Prendre le yéti mécanique d'Umi et effrayer ses amis avec :","Legacki, à Long-guet (Berceau-de-l'Hiver)","Sprinkle, à Gadgetzan (Tanaris)","Quixxil, au refuge des Marshal (Cratère d'Un'Goro)","Quand vous aurez terminé, rapporter le yéti mécanique à Umi."}},
[5164] = {"Guide de l'Indocile", {"Lire le Guide de l'Indocile."}},
[5165] = {"En calmant les flammes de protection", {"Avec de l’Eau de Puits de lune purifiée, éteindre les flammes des quatre braseros de protection à l’intérieur du Fort des ombres dans Gangrebois."}},
[5166] = {"La cuirasse du vol chromatique", {"Pour forger la Cuirasse du vol chromatique, il faut apporter les éléments suivants à Jeziba le 'Sculpteur' :","1 Carapace chromatique.","10 Ecailles chromatiques brillantes.","10 Sangs de héros.","10 Coutures d'Abomination effilochées."}},
[5167] = {"Cuissardes des duellistes chromatiques", {"Pour forger les Cuissardes des duellistes chromatiques, il faut apporter les éléments suivants à Jeziba le 'Sculpteur' :","1 Carapace chromatique.","10 Ecailles chromatiques brillantes.","10 Sangs de héros.","5 Peaux des ombres."}},
[5168] = {"Héros du Darrowshire", {"Apporter le Libram de Davil et le Bouclier Redpath à Carlin Redpath."}},
[5181] = {"Vilains de Darrowshire", {"Rapporter le Crâne d'Horgus et l’Epée brisée de Marduk à Carlin Redpath à la chapelle de l'Espoir de Lumière."}},
[5201] = {"L'incursion des Tombe-hiver", {"Rivern Frostwind veut que vous tuiez 5 Chamans Tombe-hiver et 5 Ursas Tombe-hiver."}},
[5202] = {"Une étrange clé rouge", {"Chercher dans Jaedenar à quoi sert la Clé rouge sang."}},
[5203] = {"Sauvetage à Jaedenar", {"Escorter Arko'narin à l’endroit où Lightforge et son équipement sont détenus, puis la protéger lors de l’évasion du Fort des ombres. Ensuite, aller trouver Jessir Moonbow dans Gangrebois pour lui apprendre ce qui est arrivé à ses amis."}},
[5204] = {"Le châtiment de la Lumière", {"Revenir à Jaedenar et tuer la succube Rakaiah avant de trouver le Cadavre de Trey Lightforge."}},
[5206] = {"Les maraudeurs du Darrowshire", {"Apporter 5 Crânes résonants et le Cristal mystique à Carlin Redpath, dans les Maleterres de l’est."}},
[5210] = {"Frère Carlin", {"Apporter les Annales complètes de Darrowshire à Carlin Redpath dans les Maleterres de l’est."}},
[5211] = {"Défenseurs de Darrowshire", {"Libérer 15 Esprits de Darrowshire et retourner auprès de Carlin Redpath."}},
[5212] = {"La chair ne ment pas", {"Récupérer 20 Echantillons de chair pestiférée à Stratholme puis les rapporter à Betina Bigglezink. Vous pensez que toutes les créatures de Stratholme possèdent de tels échantillons de chair."}},
[5213] = {"L'agent actif", {"Se rendre jusqu’à Stratholme et fouiller les ziggourats. Trouver et ramener de nouvelles informations sur le Fléau à Betina Bigglezink."}},
[5214] = {"Le grand Ezra Grimm", {"Trouver le magasin de tabac d’Ezra Grimm à Stratholme et récupérer une boîte de Tabac d’Ezra. Retourner auprès de Smokey LaRue quand ce sera fait."}},
[5215] = {"Les chaudrons du Fléau", {"Parler à la grande prêtresse MacDonnell à la pointe du Noroît, dans les Maleterres de l’ouest."}},
[5216] = {"Cible : le Champ de Felstone", {"Se rendre au Champ de Felstone dans les Maleterres de l’ouest pour trouver et vaincre le Seigneur du Chaudron qui s’y trouve. Il aura peut-être une clé qui permet d’accéder au chaudron. Il faut se munir de la Bouteille d'échantillon du Champ de Felstone vide pour pouvoir prélever un échantillon des poisons qui sont dans le chaudron."}},
[5217] = {"Retour au camp du Noroît", {"Apporter une Bouteille du Champ de Felstone remplie à la grande prêtresse MacDonnell au camp du Noroît, dans les Maleterres de l’ouest."}},
[5218] = {"Le Chaudron du champ de Felstone", nil},
[5219] = {"Cible : les Larmes de Dalson", {"Se rendre aux Larmes de Dalson dans les Maleterres de l’ouest pour trouver et vaincre le Seigneur du Chaudron qui s’y trouve, puis utiliser sa clé pour accéder au chaudron. Il faut se munir de la Bouteille des larmes de Dalson vide pour pouvoir prendre un échantillon des poisons qui sont dans le chaudron."}},
[5220] = {"Retour au camp du Noroît", {"Apporter la Bouteille des Larmes de Dalson remplie à la grande prêtresse MacDonnell au camp du Noroît, dans les Maleterres de l’ouest."}},
[5221] = {"Chaudron des Larmes de Dalson", nil},
[5222] = {"Cible : le Repaire putride", {"Se rendre au Repaire putride dans les Maleterres de l’ouest pour trouver et vaincre le Seigneur du Chaudron qui s’y trouve, et utiliser sa clé pour accéder au chaudron. Il faut se munir de la Bouteille vide du Repaire putride pour pouvoir récupérer un échantillon des poisons à l’intérieur du chaudron."}},
[5223] = {"Retour au camp du Noroît", {"Apporter la Bouteille du Repaire putride remplie à la grande prêtresse MacDonnell au camp du Noroît, dans les Maleterres de l’ouest."}},
[5224] = {"Le Chaudron du Repaire putride", nil},
[5225] = {"Cible : la Flétrissure de Gahrron", {"Se rendre à la Flétrissure de Gahrron dans les Maleterres de l’ouest pour trouver et vaincre le Seigneur du Chaudron qui s’y trouve, et utiliser sa clé pour accéder au chaudron. Il faut se munir de la Bouteille vide de la Flétrissure de Gahrron pour pouvoir récupérer un échantillon des poisons à l’intérieur du chaudron."}},
