[5241] = {"Oncle Carlin", {"Trouver Carlin Redpath."}},
[5242] = {"Un coup final", {"Tuer Moora et Salia, et rapporter la Tête du Seigneur des ténèbres Fel'dan à Greta Mosshoof dans Gangrebois."}},
[5243] = {"Les demeures du Sacré", {"Se rendre à Stratholme, au nord. Fouiller les caisses de fournitures qui encombrent la cité et récupérer 5 Eaux sacrées de Stratholme. Retourner auprès de Leonid Barthalomew le Révéré quand vous avez récolté assez de liquide béni."}},
[5244] = {"Les ruines de Kel'Theril", {"Parler à Jaron Stoneshaper au village de Pluie-d'Etoiles"}},
[5245] = {"Les Esprits troublés de Kel'Theril", {"Utiliser la Pique de Jaron pour trouver les quatre Fragments de relique des Bien-nés. Les apporter à Aurora Skycaller dans les Maleterres de l’est."}},
[5246] = {"Fragments du passé", {"Trouver les Ecrits sacrés des Bien-nés dans le temple de Zin-Malor à Azshara et les apporter à Aurora Skycaller à la tour du Col du nord, dans les Maleterres de l'est."}},
[5247] = {"Fragments du passé", {"Apporter une Barre de thorium enchanté, 5 Cristaux de restauration et une Fiole d'eau redoutable à Aurora Skycaller à la tour du Col du nord, dans les Maleterres de l’est."}},
[5248] = {"Tourmentée par le passé", {"Amener le Cristal de Zin-Malor au lac Kel'Theril dans le Berceau-de-l'Hiver et trouver un esprit Bien-né qui veuille bien vous parler."}},
[5249] = {"Au Berceau-de-l'Hiver", {"Trouver le village de Pluie-d'Etoiles dans le Berceau-de-l'Hiver, et parler à Wynd Nightchaser."}},
[5250] = {"Pluie-d'Etoiles", {"Trouver le village de Pluie-d'Etoiles dans le Berceau-de-l'Hiver et parler à Wynd Nightchaser."}},
[5251] = {"L'archiviste", {"Voyager jusqu’à Stratholme et trouver l’Archiviste Galford de la Croisade écarlate. L’abattre et brûler les Archives écarlates."}},
[5252] = {"Une Bien-née pleine de remords", {"Parler à Wynd Nightchaser au village de Pluie-d'Etoiles et lui demander quel est le bon endroit pour le Cristal de Zin-Malor."}},
[5253] = {"Le Cristal de Zin-Malor", {"Apporter le Cristal de Zin-Malor à l’archidruide Fandral Staghelm à Darnassus."}},
[5261] = {"Eagan le tanneur", {"Parler à Eagan Peltskinner."}},
[5262] = {"La vérité vient du ciel", {"Apporter la Tête de Balnazzar au duc Nicholas Zverenhoff dans les Maleterres de l’est."}},
[5263] = {"Au-dessus et au-delà", {"S’aventurer dans Stratholme et tuer le baron Rivendare. Prendre sa tête et retourner auprès du duc Nicholas Zverenhoff."}},
[5264] = {"Seigneur Maxwell Tyrosus", {"Parler au seigneur Maxwell Tyrosus. Il est à l’intérieur de l’église."}},
[5265] = {"Siège de l’Aube d'argent", {"Ouvrir le Coffre prendre votre récompense."}},
[5281] = {"Âmes tourmentées", {"Trouver Egan. Vous savez seulement qu’on l’a vu pour la dernière fois aux alentours de Stratholme."}},
[5282] = {"Âmes tourmentées", {"Se servir du Libérateur d'Egan sur les citoyens fantomatiques et spectraux de Stratholme. Quand les esprits sans repos se libèrent de leur enveloppes fantomatiques, utiliser de nouveau le Libérateur – ils seront enfin libres !","Libérer 15 Esprits sans repos puis retourner auprès d’Egan."}},
[5283] = {"L'art de l'armurier", {"Pour devenir un fabricant d'armures, il faut fabriquer les éléments suivants et les rapporter à Grumnus : 4 Heaumes ornés en mithril, 2 Bottes ornées en mithril, 1 Cuirasse ornée en mithril."}},
[5284] = {"La voie du Forgeron d'armes", {"Pour devenir un Fabricant d'armes, il faut fabriquer les éléments suivants et les rapporter à Ironus : 4 Epées d'acier lunaire, 4 Gigantesques haches en fer, 2 Haches en mithril lourd, et 2 Grandes masses noires."}},
[5301] = {"L'art de l'armurier", {"Pour devenir un fabricant d'armures, il faut fabriquer les éléments suivants puis les apporter à Okothos : 4 Heaumes ornés en mithril, 2 Bottes ornées en mithril, 1 Cuirasse ornée en mithril."}},
[5302] = {"La voie du Forgeron d'armes", {"Pour devenir un Fabricant d'armes, vous devez fabriquer les éléments suivants et les rapporter à Borgosh : 4 Epées d'acier lunaire, 4 Gigantesques haches en fer, 2 Haches en mithril lourd, et 2 Grandes masses noires."}},
[5305] = {"Douce sérénité", {"Aller jusqu’à Stratholme et tuer le Forgeur de marteaux cramoisi. Récupérer son Tablier et le ramener à Lilith."}},
[5306] = {"Pierre du serpent de la chasseresse des ombres", {"Aller jusqu’au Pic Blackrock et abattre la chasseresse des ombres Vosh'gajin. Récupérer la Pierre du serpent de Vosh'gajin et la ramener à Kilram."}},
[5307] = {"Corruption", {"Trouver le Fabricant d'épées de la Garde noire à Stratholme et le tuer. Récupérer l’Insigne de la Garde noire et retourner auprès de Seril Scourgebane."}},
[5321] = {"Le dormeur s'est éveillé", {"Escorter Kerlonian Evershade jusqu’à Liladris Moonriver au Poste de Maestra à Ashenvale."}},
[5341] = {"Fortune de la famille Barov", {"S’aventurer jusqu’à Scholomance et récupérer la fortune de la famille Barov. Cette fortune se compose de quatre titres de propriété : le titre de propriété de Caer Darrow ; le titre de propriété de Brill ; le titre de propriété de Moulin-de-Tarren ; et le titre de propriété de Southshore. Revenir auprès d’Alexi Barov quand la tâche est accomplie."}},
[5342] = {"Le dernier Barov", {"Voyagez jusqu’au Camp du Noroît, dans le territoire de l’Alliance, et y assassiner Weldon Barov. Prenez sa tête et revenez auprès d’Alexi Barov.","Attention : Weldon sera probablement bien protégé par la racaille de l’Alliance."}},
[5343] = {"Fortune de la famille Barov", {"S’aventurer jusqu’à la Scholomance et récupérer la fortune de la famille Barov. Cette fortune se compose de quatre titres de propriété : le titre de propriété de Caer Darrow ; le titre de propriété de Brill ; le titre de propriété de Moulin-de-Tarren ; et le titre de propriété de Southshore. Revenir auprès de Weldon Barov quand la tâche est accomplie."}},
[5344] = {"Le dernier Barov", {"Voyager jusqu’à la Barricade - territoire de la Horde - et assassiner Alexi Barov. Prendre sa tête et revenir auprès de Weldon Barov.","Attention : Alexi sera probablement bien protégé par les vandales de la Horde."}},
[5361] = {"Arbre familial", {"Apporter le Rapport du garde-falaise Longhorn à Nataka Longhorn au poste de Rôdeur-fantôme en Désolace."}},
[5381] = {"La Main d'Iruxos", {"Récupérer la Boîte démoniaque dans la forteresse de Hache-Tonnerre, puis la rapporter à Taiga Wisemane, à Proie-de-l'Ombre."}},
[5382] = {"Docteur Theolen Krastinov, le boucher", {"Trouver le docteur Theolen Krastinov à l’intérieur de la Scholomance. Le tuer, puis brûler le Cadavre d’Eva Sarkhoff et le Cadavre de Lucien Sarkhoff. Retourner auprès d’Eva Sarkhoff quand la tâche est accomplie."}},
[5384] = {"Kirtonos le héraut", {"Revenir à Scholomance avec le Sang des innocents. Trouver le porche et placer le Sang des innocents dans le brasero. Kirtonos viendra dévorer votre âme.","Combattre vaillamment, sans perdre un pouce de terrain ! Détruire Kirtonos et revenir auprès d’Eva Sarkhoff."}},
[5385] = {"Les restes de Trey Lightforge", {"Rapporter le Cadavre de Trey Lightforge à Jessir Moonbow dans Gangrebois."}},
[5386] = {"La prise du jour", {"Parler à Jinar'Zillen à Proie-de-l'Ombre, puis amener 2 poissons Ventre-rouge à Nataka Longhorn."}},
