[5461] = {"L'humain, Ras Murmegivre", {"Voyager jusqu’aux hautes-terres d'Arathi, et aller aux ruines de Stromgarde. Fouiller Stromgarde pour trouver un Souvenir de la mémoire. Si une telle trouvaille est faite, rapporter l’objet au magistrat Marduke."}},
[5462] = {"Le moribond, Ras Murmegivre", {"Voyager jusqu’à l’Espoir de lumière dans les Maleterres de l'est et trouver Leonid Barthalomew le Révéré. Lui montrer le Souvenir de la mémoire et lui dire tout ce que vous avez découvert."}},
[5463] = {"Le cadeau de Menethil", {"Voyager jusqu’à Stratholme et trouver le cadeau de Menethil. Placer le Souvenir de la mémoire sur le sol impie."}},
[5464] = {"Le cadeau de Menethil", {"Apporter le Souvenir lié à Leonid Barthalomew dans les Maleterres de l’est."}},
[5465] = {"Souvenir lié", {"Retourner auprès du Magistrat Marduke à Caer Darrow. Lui montrer le Souvenir lié."}},
[5466] = {"La Liche, Ras Murmegivre", {"Trouver Ras Murmegivre dans Scholomance. Après l’avoir trouvé, utiliser le Souvenir lié sur son visage mort-vivant. Si sa transformation en mortel réussit, l’abattre et prendre la Tête humaine de Ras Murmegivre. Apporter la tête au Magistrat Marduke."}},
[5481] = {"La tâche de Gordo", {"Récolter 3 herbes des ténèbres et les apporter à l’Apprenti Apothicaire Holland au cimetière de Brill."}},
[5482] = {"Herbe maléfique", {"Récolter 10 Herbes maléfiques et les apporter à l’apprenti apothicaire Holland."}},
[5501] = {"Collectionneur d'os", {"Apporter à Bibbly F'utzbuckle à la Hutte de Kormek 10 Os de kodo provenant du Cimetière des kodos."}},
[5502] = {"Un surveillant de la Horde", {"Achetez un Râtelier d’épées en mousse à Blax Fusenbouteille dans la Herse, à Orgrimmar. Puis ramenez votre protégé à l’orphelinat d’Orgrimmar, en parlant à la directrice de l’orphelinat, Plainteguerre, dans la Herse."}},
