[6081] = {"Entraîner la bête", {"Adressez-vous à Orkmar Grimshot à Orgrimmar."}},
[6082] = {"Dompter la bête", {"Domptez un Scorpide blindé à l'aide du Bâtonnet de domptage. Entraînez-vous puis retournez voir Thotar à Tranchecolline."}},
[6083] = {"Dompter la bête", {"Utiliser le Bâtonnet de domptage pour dompter un Clampant de l'écume. S’entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Thotar, à Tranchecolline."}},
