[6087] = {"Dompter la bête", {"Utiliser le Bâtonnet de domptage pour dompter un Traqueur des prairies. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Yaw Sharpmane, au village Bloodhoof."}},
[6088] = {"Dompter la bête", {"Utiliser le Bâtonnet de domptage pour dompter un Rapace. S’entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Yaw Sharpmane, au village Bloodhoof."}},
[6089] = {"Entraîner la bête", {"Parler à Holt Thunderhorn, sur la Cime des Chasseurs, à Thunder Bluff."}},
[6101] = {"Dompter la bête", {"Utiliser le Bâtonnet de domptage pour dompter un Rôdeur sabre-de-nuit. S’entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Dazalar, à Dolanaar."}},
[6102] = {"Dompter la bête", {"Utiliser le Bâtonnet de domptage pour dompter un Hurleur Strigid. S’entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Dazalar, à Dolanaar."}},
[6103] = {"Entraîner la bête", {"Parler à Jocaste, à l'enclave cénarienne de Darnassus."}},
[6121] = {"Nouvelles leçons", {"Aller à Reflet-de-Lune, et parler à Dendrite Starblaze, au village de Havrenuit."}},
[6122] = {"Le principe originel", {"Utiliser une Fiole d'échantillon des Chutes de la Bondissante vide pour collecter un échantillon d’eau, à l’entrée de la grotte, à proximité des Chutes.","Apporter la Fiole d'échantillon des Chutes de la Bondissante remplie à Alanndarian Nightsong, à Auberdine, dans Sombrivage."}},
[6123] = {"Rassembler les ingrédients du remède", {"Apporter 5 Terrestrines et 12 Champignons lunaires à Alanndarian Nightsong, à Auberdine ; elle pourra ainsi commencer à préparer son remède."}},
[6124] = {"Soigner la maladie", {"Utiliser le Remède pour animaux sur 10 Daims malades qui se trouvent dans Sombrivage ; ceci les soignera. Les Daims malades ont été aperçus d’abord entre le sud de la rivière Bondissante et le nord d’Auberdine ; on en trouve également tout au sud de Sombrivage, à la frontière d’Ashenvale."}},
[6125] = {"Pouvoir contre le poison", {"Retourner voir Mathrengyl Bearwalker, dans l’Enclave cénarienne, à Darnassus."}},
[6126] = {"Nouvelles leçons", {"Aller à Reflet-de-Lune, et parler à Dendrite Starblaze, au village de Havrenuit."}},
[6127] = {"Le principe originel", {"Utiliser la Fiole d'échantillon vide du Pic de Brume-funeste pour collecter un échantillon de l’eau d’un bassin, au sommet du Pic de Brume-funeste.","Apporter ensuite la Fiole d'échantillon remplie à Tonga Runetotem, à la Croisée, dans les Tarides."}},
[6128] = {"Rassembler les ingrédients du remède", {"Apporter 5 Terrestrines et 5 Cornes de kodo à Tonga Runetotem, à la Croisée, pour qu'il puisse préparer un baume curatif."}},
[6129] = {"Soigner la maladie", {"Utiliser le Remède pour animaux sur 10 Gazelles malades qui se trouvent dans la partie nord des Tarides ; ceci les soignera. Les Gazelles malades ont été vues au nord de la route orientée est-ouest qui traverse la Croisée."}},
[6130] = {"Pouvoir contre le poison", {"Retourner voir Turak Runetotem, sur la Cime des Anciens, à Thunder Bluff."}},
[6132] = {"Faites-moi sortir de là !", {"Escorter Melizza Brimbuzzle, puis parler à Hornizz Brimbuzzle au cimetière des Kodos en Désolace."}},
[6133] = {"L'ordre du seigneur forestier", {"Aller vers la frontière nord des Maleterres de l’est et récupérer le Registre de Quel'Thalas. L’objet se trouve quelque part dans le gîte de Quel'Lithien.","Tuer également 8 Forestiers, 8 Eclaireurs et 8 Bûcherons."}},
[6134] = {"Chasse à l'ectoplasme", {"Apporter 8 Fantôm-o-plasmes et la Caisse d'aimants fantômes à Hornizz Brimbuzzle, en Désolace."}},
[6135] = {"Crépuscaile, oh comme je te hais !", {"Trouver Crépuscaile et la tuer. Récupérer une Touffe de fourrure de Crépuscaile sur son cadavre, puis l’apporter à Nathanos le Flétrisseur.","Il paraît que Crépuscaile hante les terres qui se trouvent au nord de la ferme des Marris."}},
[6136] = {"Le corpulent", {"Trouver Creusechair, dans les terres au nord des Maleterres de l’est, et le tuer. Retourner voir Nathanos le Flétrisseur quand la mission sera terminée."}},
[6141] = {"Frère Anton", {"Adressez-vous au frère Anton, en Désolace."}},
[6142] = {"Des praires comme appât", {"Trouver 10 morceaux de Chair de praire et les apporter à Mai'Lahii, à Proie-de-l'Ombre."}},
[6143] = {"D'autres poissons à frire", {"Tuer 7 Myrmidons Ondulame, 7 Nagas Ondulame et 5 Sorcières Ondulame, puis parler à Drulzegar, à Proie-de-l'Ombre, en Désolace."}},
[6144] = {"L'appel du commandement", {"Aller à Undercity et parler à Varimathras."}},
[6145] = {"Le messager cramoisi", {"Retourner dans les Maleterres de l’est et traquer le Messager cramoisi, le tuer et récupérer les Ordres du Grand Croisé.","Apporter les Ordres à Nathanos le Flétrisseur pour obtenir des instructions supplémentaires."}},
[6146] = {"La ruse de Nathanos", {"Aller à la Main de Tyr, au sud-est de la ferme des Marris. Une fois sur place, mordre dans la Pomme pourrie. Profiter d’avoir revêtu l’apparence d’un membre de la Croisade écarlate pour apporter les Ordres du Grand Croisé au Grand maître Valdelmar."}},
[6147] = {"Retourner voir Nathanos", {"Retourner voir Nathanos le Flétrisseur avec la nouvelle information."}},
[6148] = {"L'Oracle écarlate, Demetria", {"La Croisade écarlate fait mouvement. Quelque part le long de la route, entre la Main de Tyr et Stratholm, vous trouverez l’Oracle Demetria et sa fratrie. La tuer et retourner voir Nathanos le Flétrisseur."}},
[6161] = {"Revendiquer le trésor de Rackmore !", {"Trouver la Clé d'argent de Rackmore. Trouver la Clé d'or de Rackmore. Trouver et ouvrir le Coffre de Rackmore."}},
[6162] = {"La dernière bataille d'un mari", {"Tuer le Seigneur Ror et apporter sa Griffe à Dreka'Sur, au Poste de la Vénéneuse, en Gangrebois."}},
[6163] = {"Ramstein", {"Aller à Stratholme et tuer Ramstein Grandgosier. Prendre sa tête, et l’apporter à Nathanos."}},
[6164] = {"Le livre de compte d'Augustus", {"Apporter le Livre de compte d'Augustus à Augustus le Touché à Val-Terreur."}},
[6181] = {"Un message rapide", {"Apporter la Note de Lewis à Thor, le Maître des gryphons."}},
[6182] = {"Le premier et le dernier", {"Parler à Mathias Shaw, dans la vieille ville de Stormwind. Il se trouve dans la caserne."}},
[6183] = {"Honorer la mort", {"Reparlez à Mathias Shaw, quand vous aurez décidé d’accepter son travail."}},
[6184] = {"Flint Shadowmore", {"Allez au camp du Noroît, dans les Maleterres de l’ouest, et rencontrez votre contact, Flint Shadowmore."}},
[6185] = {"Les Maleterres de l'Est", {"Fouillez les Maleterres de l’est pour trouver des indices relatifs au \"Flétrisseur\" et aux agents du SI:7 portés disparus. Si vous trouvez un quelconque Insigne du SI:7, rapportez-le à Flint Shadowmore, au camp du Noroît."}},
[6186] = {"La venue du Flétrisseur", {"Retourner à Stormwind et informer le généralissime Bolvar Fordragon du destin de Nathanos Marris."}},
[6187] = {"L'ordre doit être rétabli", {"Montez une armée et portez-vous vers les Maleterres de l’est. Lancez un assaut total sur Nathanos le Flétrisseur et détruisez tout misérable élément de la Horde qui tenterait de le défendre.","Gardez votre présence d’esprit, <nom> : la Horde le défendra jusqu’à la mort."}},
[6261] = {"Dungar Longdrink", {"Apportez la Caisse d'Osric à Dungar Longdrink, le Maître des gryphons."}},
[6281] = {"Continuer vers Stormwind", {"Achetez un voyage en gryphon à Thor, Maître des gryphons, puis apportez la Note de Lewis à Osric Strang au magasin “Immunité limitée”, dans la vieille ville de Stormwind."}},
[6282] = {"La menace des harpies", {"Maggran Earthbinder de la retraite de Roche-Soleil veut que vous anéantissiez sept Harpies, sept Embusquées, sept Tueuses et sept Volplumes Rougefuries."}},
[6283] = {"La lignée Rougefurie", {"Maggran, à la Retraite de Roche-Soleil, vous demande de détruire la Déchiqueteuse Rougefurie et de rapporter son corps comme preuve de votre exploit."}},
[6284] = {"Arachnophobie", {"Tuer Besseleth, et apporter le Croc de Besseleth à Maggran, dans la Retraite de Roche-Soleil."}},
[6285] = {"Aller voir Lewis", {"Achetez un voyage en gryphon pour la Colline des sentinelles à Dungar Longdrink, Maître des gryphons, puis apportez la Caisse d'Olric à Lewis à la Colline des sentinelles."}},
[6301] = {"Cycle de la renaissance", {"Tammra Windfield, de la retraite de Roche-Soleil, vous demande de rassembler 10 Graines de Gaea."}},
[6321] = {"Approvisionner le Sépulcre", {"Apporter l’Ordre de Podrig à Karos Razok."}},
[6322] = {"Michael Garret", {"Apporter la Caisse de Gordon à Michael Garret."}},
[6323] = {"Voyage vers Undercity", {"Acheter un voyage en chauve-souris pour Undercity, auprès du Maître des chauves-souris, Karos Razok, puis apporter l’Ordre de Podrig à Gordon Wendham, à Undercity."}},
[6324] = {"Retour vers Podrig", {"Acheter un voyage en chauve-souris pour le Sépulcre, auprès du Maître des chauves-souris Michael Garret, puis apporter la Caisse de Gordon au Garde noir Podrig, au Sépulcre."}},
[6341] = {"Le don de Teldrassil", {"Apporter la Collection de Nessa à Vesprystus."}},
[6342] = {"Vol vers Auberdine", {"Prenez un hippogryphe vers Auberdine chez le maître des hippogryphes Vesprystus, puis apportez la Collection de Nessa à Laird."}},
[6343] = {"Retourner à Nessa", {"Réponse de Laird à Nessa Shadowsong."}},
[6344] = {"Nessa Shadowsong", {"Parler à Nessa Shadowsong."}},
[6361] = {"Un Tas de peaux", {"Apporter le Tas de peaux au Maître des Cavaliers célestes, Devrak, à la Croisée."}},
[6362] = {"Voyage vers Thunder Bluff", {"Acheter un voyage pour Thunder Bluff, auprès du Maître des Cavaliers célestes, Devrak, puis apporter le Tas de peaux à Ahanu, à Thunder Bluff."}},
[6363] = {"Tal le Maître des Cavaliers célestes", {"Apporter les Marchandises en cuir d'Ahanu à Tal, à Thunder Bluff."}},
[6364] = {"Retourner vers Jahan", {"Acheter un voyage pour la Croisée auprès du Maître des Cavaliers célestes Tal, puis apporter les Marchandises en cuir d’Ahanu à Jahan Hawkwing."}},
[6365] = {"Viandes vers Orgrimmar", {"Apporter la Chair de Zargh à Devrak, à la Croisée."}},
[6381] = {"Nouvelle vie", {"Planter 10 Graines de Gaea sur les Monticules de Gaea, puis parler à Tammra, à la Retraite de Roche-Soleil."}},
[6382] = {"La Chasse d'Ashenvale", {"Parler à Senani Thunderheart, au Poste de Bois-brisé, à Ashenvale."}},
[6383] = {"La Chasse d'Ashenvale", nil},
[6384] = {"Trajet vers Orgrimmar", {"Acheter un voyage pour Orgrimmar, auprès du Maître des Cavaliers célestes, Devrak, puis apporter la Chair de Zargh à Gryshka, à Orgrimmar."}},
[6385] = {"Doras le Maître des Cavaliers célestes", {"Apporter la Lettre de Gryshka à Doras, à Orgrimmar."}},
[6386] = {"Retour à la Croisée", {"Acheter un voyage pour la Croisée auprès du Maître des Cavalier célestes, Doras, puis apporter la Lettre de Gryshka à Zargh, à la Croisée."}},
[6387] = {"L'honneur des étudiants", {"Apportez la Liste de Brock à Thorgrum Borrelson à Thelsamar."}},
[6388] = {"Gryth Thurden", {"Apportez les Pioches d'honneur à Gryth Thurden à Ironforge."}},
[6389] = {"Une Plaie sur la Croisade", {"Libérez les Termites des Maleterres dans la scierie de la Crête du nord, puis retournez voir Nathaniel Dumah, au camp du Noroît, dans les Maleterres de l'ouest, avec le Tonneau de Termites des Maleterres."}},
[6390] = {"Une Plaie sur la Croisade", {"Libérez les Termites des Maleterres dans la scierie de la Crête du nord, puis retournez voir Mickey Levine, à la Barricade, dans les Clairières de Tirisfal, avec le Tonneau de Termites des Maleterres."}},
[6391] = {"Voyage vers Ironforge", {"Acheter un billet de gryphon pour Ironforge, auprès du Maître des gryphons, Thorgrum Borrelson, puis apporter la Liste de Brock à Golnir Bouldertoe, à Ironforge."}},
[6392] = {"Retourner vers Brock", {"Acheter un billet de gryphon pour Thelsamar, auprès du Maître des gyphons Gryth Thurden, puis apporter les Pioches d'honneur à Brock Stoneseeker, à Thelsamar."}},
[6393] = {"Guerre élémentaire", {"Apporter 10 Incendrites à Tsunaman, dans la Retraite de Roche-Soleil."}},
[6394] = {"La pioche de Thazz'ril", {"Apportez la Pioche de Thazz'rill au contremaître Thazz'rill."}},
[6395] = {"La dernière volonté de Marla", {"Apporter le Cadavre de Samuel Fipps sur la Tombe de Marla, puis retourner voir la novice Elreth."}},
[6401] = {"Kaya est en vie", {"Apprendre la bonne nouvelle à Tammra Windfield, dans la Retraite de Roche-Soleil."}},
[6402] = {"Le rendez-vous à Stormwind", {"Aller à la Cité de Stormwind et se rendre aux portes de la ville. Parlez à l'écuyer Rowe, afin qu'il informe le maréchal Windsor de votre arrivée."}},
[6403] = {"La grande mascarade", {"Suivre Reginald Windsor dans Stormwind. Le protéger contre toute menace !"}},
[6421] = {"Le ravin des Eboulis", {"Explorer la grotte du ravin des Eboulis, et apporter 10 Cristaux de résonite à Mor'rogal, dans la Retraite de Roche-Soleil."}},
[6441] = {"Cornes de satyre", {"Collecter 16 Cornes de satyre pour Pixel, au Poste de Bois-brisé."}},
[6442] = {"Des nagas sur la grève de Zoram", {"Apporter 20 Têtes d'Irequeue à Marukai, le long de la grève de Zoram."}},
[6461] = {"Suceurs de sang", {"Xen'zilla, à Malaka'Jin, voudrait que vous tuiez 10 Rampants des mousses profondes et 7 Venimeux des mousses profondes."}},
[6462] = {"Charme troll", {"Apporter 8 Charmes trolls à Mitsuwa, à l’Avant-poste de Zoram'gar."}},
[6481] = {"Un Terrestre se lève", {"Ouvrir la Gangue de résonite avec le Cristal de résonite enchanté, puis tuer Goggeroc. Retourner voir Mor'rogal pour l'informer et lui rapporter le Cristal de résonite enchanté."}},
[6482] = {"Liberté pour Ruul", {"Escorter Ruul, puis parler à Yama Snowhoof, au Poste de Bois-brisé."}},
[6501] = {"L'Œil de dragon", {"Vous devez écumer le monde pour pouvoir rétablir le pouvoir du Fragment de l'Oeil de dragon. La seule information dont vous disposez à propos de cette chose, c’est qu’elle existe."}},
[6502] = {"Amulette drakefeu", {"Récupérer le Sang du Champion des dragons noirs sur le général Drakkisath. Il se trouve dans sa salle du trône, derrière les Halls d'Ascension, sur le Pic Blackrock."}},
[6503] = {"Les estafettes d'Ashenvale", {"Tuer 9 Estafettes d'Ashenvale, puis retourner voir Kuray'bin, à l’avant-poste de Bois-brisé."}},
[6504] = {"Les pages perdues", {"Trouver les 12 pages manquantes du Manuel d'utilisation du Déchiqueteur, puis les rassembler pour constituer les Chapitres 1, 2, et 3. Ramener les pages à Gurda Ragescar près du poste de Bois-brisé."}},
[6521] = {"Une alliance impie", {"Apporter la Tête de l'Ambassadeur Malcin à Varimathras, à Undercity."}},
[6522] = {"Une alliance impie", {"Apporter le Petit parchemin à Varimathras, à Undercity."}},
[6523] = {"Protéger Kaya", {"Escorter Kaya Flathoof au Camp Aparaje, puis retourner voir Makaba Flathoof, à côté de la frontière sud-est des Serres-Rocheuses."}},
[6541] = {"Rapport à Kadrak", {"Faire un rapport à Kadrak, dans la tour de guet, dans le nord des Tarides."}},
[6542] = {"Rapport à Kadrak", {"Faire un rapport à Kadrak, dans la tour de guet, dans le nord des Tarides."}},
[6543] = {"Les rapports Warsong", {"Apporter les Rapports du clan Warsong à l’Eclaireur Warsong, au Messager Warsong et au Voltigeur Warsong. Rapporter leurs comptes rendus mis à jour à Kadrak, dans la tour de guet nord, dans les Tarides."}},
[6544] = {"L'assaut de Torek", {"Escorter Torek lors de son attaque contre l’Avant-poste d'Aile-argent et Duriel Moonfire, puis faire un rapport à Ertog Ragetusk, au Poste de Bois-brisé."}},
[6545] = {"Mise à jour du Messager Warsong", nil},
[6546] = {"Mise à jour du voltigeur Warsong", nil},
[6547] = {"Mise à jour de l'Eclaireur Warsong", nil},
[6548] = {"Vengez mon village !", {"Tuez 8 Forbans du Totem sinistre et 6 Mercenaires du Totem sinistre, puis retournez voir Makaba Flathoof, près de la lisière sud-est des Serres-Rocheuses."}},
[6561] = {"L'infamie de Brassenoire", {"Apporter la tête du Seigneur du crépuscule Kelris à Bashana Runetotem, à Thunder Bluff."}},
[6562] = {"Problèmes dans les profondeurs", {"Parler à Je'neu Sancrea, à Ashenvale."}},
[6563] = {"L'Essence d'Aku'Mai", {"Apporter 20 Saphirs d'Aku'Mai à Je'neu Sancrea, à Ashenvale."}},
[6564] = {"Allégeance aux Dieux très anciens", {"Apporter la Note humide à Je'neu Sancrea, à Ashenvale."}},
[6565] = {"Allégeance aux Dieux très anciens", {"Tuer Lorgus Jett, dans les Profondeurs de Brassenoire et retourner voir Je'neu Sancrea, à Ashenvale."}},
[6566] = {"Ce que le vent apporte", {"Écouter Thrall."}},
[6567] = {"Le Champion de la Horde", {"Chercher Rexxar. Le Chef de guerre vous a dit où il était. Chercher sur les chemins de Désolace, entre les Serres-Rocheuses et Feralas."}},
[6568] = {"Le testament de Rexxar", {"Apporter le Testament de Rexxar à Myranda la Mégère, dans les Maleterres de l’ouest."}},
[6569] = {"Illusions d'Occulus", {"Aller au Pic Blackrock et collecter 20 Yeux de draconide noir. Retourner voir Myranda la Mégère, quand la tâche sera terminée."}},
[6570] = {"Brandeguerre", {"Aller à la tourbière du Ver, dans le marécage d'Âprefange, et chercher la tanière de Brandeguerre. Une fois à l’intérieur, porter l’Amulette de subversion draconique, et parler à Brandeguerre."}},
[6571] = {"Fournitures Warsong", {"Collecter les Fournitures Warsong : Lames du clan Warsong, Huile Warsong, et la Cargaison de Haches Warsong ayant pour destinataire Locke Okarr, au Poste de Bois-brisé."}},
[6581] = {"Lames du clan Warsong", nil},
[6582] = {"L'épreuve des crânes, Clairvoyant", {"Vous devez trouver Clairvoyant, le drake champion du Vol bleu, et le tuer. Arracher son crâne à son cadavre, et le rapporter à Brandeguerre.","Clairvoyant se trouve au Berceau-de-l'Hiver."}},
[6583] = {"L'épreuve des crânes, Somnus", {"Détruire le Champion drake du Vol vert, Somnus. Arracher son crâne à son cadavre, puis le rapporter à Brandeguerre."}},
[6584] = {"L'épreuve des crânes, Chronalis", {"Chronalis, enfant de Nozdormu, garde les Grottes du temps, dans le Désert de Tanaris. Tuez-le et rapportez son crâne à Brandeguerre."}},
[6585] = {"L'épreuve des crânes, Axtroz", {"Aller à Grim Batol et traquer Axtroz, le Champion drake du Vol rouge. Le tuer et arracher son crâne, puis l’apporter à Brandeguerre."}},
[6601] = {"Ascension...", {"Il semble que la comédie soit finie. Vous savez que l’Amulette de subversion draconique, créée par Myranda la Mégère, ne fonctionnera pas à l’intérieur du pic Blackrock. Peut-être devriez-vous trouver Rexxar et lui exposer votre fâcheuse situation. Montrez-lui l'Amulette drakefeu terne. Avec un peu de chance, il saura quoi faire."}},
[6602] = {"Le sang du champion des dragons noirs", {"Aller au Pic Blackrock, et tuer le général Drakkisath. Récupérer son sang et retourner voir Rexxar."}},
[6603] = {"Troubles au Berceau-de-l'Hiver", {"Trouver Donova Snowden, à côté des sources chaudes du Berceau-de-l'Hiver."}},
[6604] = {"Les Indomptables enragés", {"Parler à Jaron Stoneshaper, au village de Pluie-d'Etoiles."}},
[6605] = {"Une étrange personne", {"Parler à Winna Hazzard, à Gangrebois."}},
[6606] = {"Un peu de chance", {"Parler à la Mau'ari, à Long-guet."}},
[6607] = {"Nat Pagle, pêcheur de l'extrême", {"Nat Pagle voudrait que vous attrapiez les poissons suivants :","Le Mahi Mahi de Brumejonc, de la grève de Brumejonc, dans le marais des Chagrins.","Un Frappeur Sar'theris, de la grève de Sar'theris en Désolace.","L'Ahi de Feralas, de la rivière Verdantis de Feralas.","Le Sailfin bleu de la Côte sauvage, de la Côte sauvage de la vallée de Strangleronce.","Retournez voir Nat Pagle quand la collecte sera terminée."}},
[6608] = {"Vous êtes trop bon", {"Vous pensez que Lumak essaie de vous dire qu’il ne peut plus vous aider à améliorer votre compétence de pêche. Vous devriez essayer de vous adresser maintenant à son ami, Nat Pagle, un pêcheur humain des îles au sud de Theramore (c’est du moins ce que vous croyez comprendre du discours de Lumak)."}},
[6609] = {"Il ne me reste rien !", {"Aller à Kalimdor, dans la ville côtière de Theramore. À partir de là, chercher une île au sud de la ville principale. Nat Pagle vous attend !"}},
[6610] = {"Surprise de palourdes", {"Dirge Quikcleave voudrait que vous lui apportiez les objets suivants :","12 Oeufs géants. Vous les trouverez sur les Rocs qui hantent Tanaris ou sur toute autre espèce de grand oiseau.","10 morceaux de Chair de palourde piquante. Vous pouvez trouver la chair de palourde sur... les palourdes, naturellement !","20 Emmentals d'Alterac. Vous les trouverez dans une fromagerie !","Collectez tous ces objets, puis retournez voir Dirge."}},
[6611] = {"Vers Gadgetzan vous allez !", {"Aller à Gadgetzan, dans le Désert de Tanaris, et parler de votre carrière de cuisinier à Dirge Quikcleave."}},
[6612] = {"Je connais un gars...", {"Aller à Gadgetzan, dans le Désert de Tanaris, et parler de votre carrière de cuisinier à Dirge Quikcleave."}},
[6621] = {"Le roi des Vile-sylve", {"Placer la Bannière de Karang sur le Monticule du totem Vile-sylve. Ne pas laisser les furbolgs détruire la Bannière. Tuer le chef Murgut, et apporter le Totem de Murgut à Karang Amakkar, à Zoram'gar."}},
[6622] = {"Triage", {"Sauver des patients en utilisant des Bandages de Triage, afin de panser leurs blessures. Vous devez sauver 15 patients avant que 6 d’entre eux ne meurent de leurs blessures.","Rappelez-vous l’ordre du triage ! Les Soldats agonisants meurent les premiers, suivis des Soldats grièvement blessés. Les Soldats blessés sont ceux qui meurent les derniers, et ils doivent être soignés APRES ceux qui agonisent et ceux qui sont grièvement blessés ; selon le tri que vous avez fait."}},
[6623] = {"Service de traumatologie de la Horde", {"Aller au Trépas-d'Orgrim, dans les hautes-terres d'Arathi, et évoquer avec le docteur Gregory Victor la possibilité de rejoindre les rangs des médecins de la Horde."}},
[6624] = {"Triage", {"Sauver des patients en utilisant des Bandages de Triage, afin de panser leurs blessures. Vous devez sauver 15 patients avant que 6 d’entre eux ne meurent de leurs blessures.","Rappelez-vous l’ordre du triage ! Les Soldats agonisants meurent les premiers, suivis des Soldats grièvement blessés. Les Soldats blessés sont ceux qui meurent les derniers, et ils doivent être soignés APRES ceux qui agonisent et ceux qui sont grièvement blessés ; selon le tri que vous avez fait."}},
[6625] = {"Service de traumatologie de l'Alliance", {"Aller à Theramore, dans le marécage d'Âprefange, et parler au docteur Gustaf VanHowzen de la possibilité de rejoindre les rangs des médecins de l’Alliance."}},
[6626] = {"L'hôte du mal", {"Tuer 8 Gardes de guerre Tranchebauge, 8 Tisseurs d'épines Tranchebauge et 8 Sectateur de la Tête de mort, puis retourner voir Myriam Moonsinger, à l’entrée des Souilles de Tranchebauge."}},
[6627] = {"L'épreuve de la Connaissance", {"Réussir à répondre à la question de Braug Dimspirit, puis lui reparler. Il se trouve dans les Serres-Rocheuses."}},
[6628] = {"L'épreuve de la Connaissance", {"Réussir à répondre à la question de Parqual Fintallas, puis lui reparler. Il se trouve à Undercity."}},
[6629] = {"Tuer Grundig Darkcloud", {"Tuer Grundig Darkcloud et 6 Brutes du Totem sinistre, puis retourner voir Makaba Flathoof, à côté de la frontière sud-est des Serres Rocheuses."}},
[6641] = {"Vorsha la Flagellante", {"Garantir la sécurité de Muglash tandis qu’il voyage vers le brasero. L’aider à éteindre le brasero, puis le protéger, en combattant les nagas.","En cas de réussite, retourner à l’avant-poste de Zoram'gar et informer le messager Warsong de la mort de Vorsha la Flagellante."}},
[6642] = {"Faveur auprès de la Confrérie, Minerai de sombre fer", nil},
[6643] = {"Faveur auprès de la Confrérie, Noyau de feu", nil},
[6644] = {"Faveur auprès de la Confrérie, Noyau de lave", nil},
[6645] = {"Faveur auprès de la Confrérie, Cuir du Magma", nil},
[6646] = {"Faveur auprès de la Confrérie, Sang de la montagne", nil},
[6661] = {"Chasse au rat dans les profondeurs", {"Capturer 5 Rats des profondeurs en utilisant le Pipeau du dératiseur. Conduire les Rats vers Monty. N’oubliez pas de rendre le Pipeau quand vous aurez terminé."}},
[6662] = {"Mon frère, Nipsy", {"Apporter le Carton de viande mystère à Nipsy, au Dépôt du Tram des profondeurs de Stormwind."}},
[6681] = {"Ravenholdt, le manoir", {"Apporter le Sceau de Ravenholdt à Fahrad, au Manoir de Ravenholdt, dans les collines d'Hillsbrad.","Votre sagacité sera mise à l’épreuve tout au long de la route. Préparez-vous."}},
[6701] = {"Emblèmes du Syndicat", nil},
[6721] = {"La voie du chasseur", {"Parler à Dazalar, à Dolanaar."}},
[6722] = {"La voie du chasseur", {"Parler à Dazalar, à Dolanaar."}},
[6741] = {"Plus de butin !", nil},
[6761] = {"La nouvelle frontière", {"Parler à Mathrengyl Bearwalker, dans l’Enclave cénarienne de Darnassus."}},
[6762] = {"Rabine Saturna", {"Parler à Rabine Saturna, au village de Havrenuit, à Reflet-de-Lune. Reflet-de-Lune se trouve entre Gangrebois et le Berceau-de-l'Hiver. Le chemin qui y mène traverse le Repaire des Grumegueules."}},
[6781] = {"Plus de morceaux d'armure !", nil},
[6801] = {"Lokholar le Seigneur des Glaces", nil},
[6804] = {"Eau empoisonnée", {"Utiliser l’Aspect de Neptulon sur les élémentaires empoisonnés des Maleterres de l’est. Apporter 12 Bracelets discordants et l’Aspect de Neptulon au duc Hydraxis, en Azshara."}},
[6805] = {"Tempêtes", {"Tuer 15 Tempétueux de poussière et 15 Grondeurs du désert, puis retourner voir le duc Hydraxis, en Azshara."}},
[6821] = {"Oeil du Prophète ardent", {"Apporter l’Oeil du Prophète ardent au duc Hydraxis, en Azshara."}},
[6822] = {"Le Cœur du Magma", {"Tuer 1 Seigneur du feu, 1 Géant de lave, 1 Chien antique du Magma et 1 Surgisseur de lave, puis retourner voir le duc Hydraxis, à Azshara."}},
[6823] = {"Agent de Hydraxis", {"Parvenir au niveau de réputation Honoré auprès des Hydraxiens, puis parler au duc Hydraxis, en Azshara."}},
[6824] = {"Les mains de l'ennemi", {"Apporter les Mains de Lucifron, de Sulfuron, de Gehennas et de Shazzrah au duc Hydraxis, en Azshara."}},
[6825] = {"L'appel des airs - l'escadrille de Guse", nil},
[6826] = {"L'appel des airs - l'escadrille de Jeztor", nil},
[6827] = {"L'appel des airs - l'escadrille de Mulverick", nil},
[6844] = {"Umber, Archiviste", {"Apporter l’Objet silithide incrusté à Umber, l’archiviste du Cercle cénarien, au village de Havrenuit, à Reflet-de-Lune."}},
[6845] = {"Les secrets du passé", {"Parler à Rabine Saturna, au village de Havrenuit, à Reflet-de-Lune."}},
[6846] = {"Que l'attaque commence !", nil},
[6847] = {"L'Œil qui-voit-tout de Maître Ryson", {"Récupérer l’Oeil qui-voit-tout de Maître Ryson dans les Grottes Haches-d’hiver, puis le rapporter à Maître Ryson.","Sachez que l’on dit de l’Oeil qui-voit-tout de Maître Ryson qu’il contient une terrible malédiction !"}},
[6848] = {"L'Œil qui voit tout de maître Ryson", {"Récupérer l’Oeil-qui-voit-tout de Maître Ryson dans les Grottes Winterax, puis le rapporter à Maître Ryson.","Sachez que l’on dit de l’Oeil-qui-voit-tout de Maître Ryson qu’il contient une terrible malédiction !"}},
[6861] = {"Le Déchiqueteur portable de Zinfizzlex", {"Le Maître Ingénieur Zinfizzlex veut que vous lui apportiez les matériaux suivants :","*30 Barres de thorium.","*50 Barres de Mithril.","*75 Barres de fer.","*1 Scie mécanique."}},
