[7001] = {"Ecuries vides", {"Trouvez un Loup des glaces dans la vallée d’Alterac. Servez-vous de la Muselière de loup des glaces pour « dompter » la bête. Lorsque ce sera fait, le loup des glaces vous suivra jusqu’au Maître des écuries Frostwolf. Parlez au Maître des écuries Frostwolf pour être crédité de cette capture."}},
[7002] = {"Harnais en cuir de bélier", nil},
[7003] = {"Géants rétrécis", {"Utilisez l'Ultra-réducteur de Zorbin sur des géants de n'importe quel type en Feralas afin les rendre plus faciles à gérer. Apportez 15 Résidus de miniaturisation que vous trouverez sur les versions rétrécies des géants à Zorbin Fandazzle aux docks de la Côte oubliée en Feralas."}},
[7021] = {"Grand-père Hiver est ici !", {"Parler à Grand-père Hiver ; il se trouve près des Gourmandises Fumebois, à Orgrimmar."}},
[7022] = {"Grandpère Hiver est ici !", {"Parler à Grandpère Hiver ; il se trouve près des Gourmandises Fumebois, à Ironforge."}},
