[7026] = {"Harnais pour béliers", nil},
[7027] = {"Écuries vides", {"Trouvez un Bélier d'Alterac dans la vallée d'Alterac. Lorsque vous serez à proximité, servez-vous du Collier d'entraînement Stormpike pour le \"dompter\". Une fois dompté, le Bélier d'Alterac vous suivra jusqu'au Maître des écuries. Parlez au Maître des écuries pour être crédité de cette capture."}},
[7028] = {"Forces maléfiques retorses", {"Collecter 25 Ciselures de Cristaux Théradriques pour Saule, en Désolace."}},
[7029] = {"La corruption de Vylelangue", {"Remplir la Fiole céruléenne renforcée, au Bassin de cristal orange, dans Maraudon.","En faire usage sur les Vignes de Vylevrille pour forcer les Engeances de Noxxious corrompues à en sortir.","Soigner 8 plantes en tuant les Engeances de Noxxious, puis retourner voir Vark Battlescar, à Proie-de-l'Ombre."}},
[7041] = {"La corruption de Vylelangue", {"Remplir la Fiole céruléenne renforcée, au Bassin de cristal orange dans Maraudon.","En faire usage sur les vignes de Vylevrille pour forcer les Engeances de Noxxious corrompues à en sortir.","Soigner 8 plantes en tuant les Engeances, puis retourner voir Taliendra, à la Combe de Nijel."}},
[7042] = {"Friandises volées de la fête du Voile d'hiver", {"Localiser l'enquêteur des Gourmandises Fumebois qui a été porté disparu dans les montagnes d'Alterac."}},
[7043] = {"La méchanceté vous habite…", {"Trouver les Friandises volées, et les ramener à Wumort Jinglepocket, à Ironforge. C'est l'Abominable Grinche qui aurait le chargement en sa possession, il résiderait dans les régions neigeuses des montagnes d'Alterac."}},
[7044] = {"Légendes de Maraudon", {"Retrouver les deux parties du Sceptre de Celebras : le Bâtonnet de Celebras et le Diamant de Celebras.","Trouvez un moyen de parler à Celebras."}},
[7045] = {"Un grand merci des Gourmandises Fumebois !", {"Parler à Grandpère Hiver à Ironforge."}},
[7046] = {"Le sceptre de Celebras", {"Aidez Celebras le Racheté pendant qu'il recrée le Sceptre de Celebras.","Parlez-lui après la réussite du rituel."}},
[7061] = {"La fête du Voile d'hiver", {"Prenez le temps de lire ce livre, \"La fête du Voile d'hiver\", pour en apprendre plus sur cette période de fête. Quand vous en aurez fini avec le livre, apportez-le à Cairne Bloodhoof, dans la ville de Thunder Bluff."}},
[7062] = {"L'esprit de cette fête", {"Parlez à l'historien Karnik de la fête du Voile d'hiver, en vous rendant à la Ligue des Explorateurs d'Ironforge."}},
[7063] = {"La fête du Voile d'hiver", {"Prenez le temps de lire le livre « La fête du Voile d'hiver » pour en apprendre plus sur cette période sainte. Quand vous en aurez fini avec le livre, apportez-le à Muradin Barbe-de-Bronze, à Forgefer."}},
[7064] = {"Corruption de la terre et de la graine", {"Anéantir la princesse Theradras et retourner voir Selendra près de Proie-de-l'Ombre, en Désolace."}},
[7065] = {"Corruption de la terre et de la Graine", {"Tuer la princesse Theradras, et retourner voir le Gardien Marandis, à la Combe de Nijel en Désolace."}},
[7066] = {"Graine de vie", {"Chercher Remulos à Reflet-de-Lune, et lui donner la Graine de vie."}},
[7067] = {"Les instructions du paria", {"Lire les instructions du paria. Après cela, obtenir l'Amulette d'union de Maraudon et la ramener au centaure renégat, dans le sud de Désolace."}},
[7068] = {"Fragments d'Ombréclat", {"Collecter 10 Fragments d'Ombréclats dans Maraudon, et les ramener à Uthel'nay, à Orgrimmar."}},
[7070] = {"Fragments d'Ombréclat", {"Collecter 10 Fragments d'Ombréclats, et les porter à l'archimage Tervosh, dans le marécage d'Âprefange."}},
[7081] = {"Les cimetières de la vallée d'Alterac", {"Attaquez un cimetière, puis présentez-vous au caporal Noreg Stormpike à Dun Baldar."}},
[7082] = {"Les cimetières d'Alterac", {"Attaquez un cimetière, puis présentez-vous au caporal Teeka Bloodsnarl dans les montagnes d'Alterac."}},
[7101] = {"Tours et fortins", {"Prenez une tour ennemie, puis présentez-vous au caporal Teeka Bloodsnarl dans les montagnes d'Alterac."}},
[7102] = {"Tours et fortins", {"Détruisez la bannière d'une tour ou d'un fortin ennemi, puis présentez-vous au sergent Durgen Stormpike à Dun Baldar."}},
[7121] = {"L'intendant", {"Parlez à l’Intendant Stormpike."}},
[7122] = {"Prendre une mine", {"Prenez une mine que les Stormpike ne contrôlent pas, puis présentez-vous à l’intendant Stormpike à Dun Baldar."}},
[7123] = {"Parlez à notre Intendant", {"Parlez à l’Intendant Frostwolf."}},
[7124] = {"Prendre une mine", {"Prenez une mine, puis présentez-vous au caporal Teeka Bloodsnarl dans les montagnes d'Alterac."}},
[7141] = {"La bataille d'Alterac", {"Entrez dans la vallée d'Alterac, tuez Drek'thar, le général de la Horde, puis retournez voir la Prospectrice Stonehewer dans les montagnes d'Alterac."}},
[7142] = {"La bataille pour Alterac", {"Entrez dans la vallée d'Alterac, tuez Vanndar Stormpike, le général nain, puis retournez voir Voggah Deathgrip dans les montagnes d'Alterac"}},
[7161] = {"Montrer sa valeur", {"Rendez-vous dans les cavernes des Follepattes, au sud-est de la base principale, et récupérez la Bannière Frostwolf qui s’y trouve. Ramenez-la au Maître de guerre Laggrond."}},
[7162] = {"Montrer sa valeur", {"Rendez-vous dans les cavernes de l'Aile de glace, au sud-ouest de la base principale, et récupérez la Bannière Stormpike qui s’y trouve. Ramenez-la au lieutenant Haggerdin."}},
