[7181] = {"La légende de Korrak", {"Korrak le Ragesang se cache dans les profondeurs des cavernes Winterax. D’après la légende, le chef des puissants trolls Winterax ne se montre qu’après la mort de 500 de ses guerriers.","Rendez-vous aux cavernes Winterax et massacrez tout ce qui vous fait obstacle jusqu’à ce que Korrak se montre. Tuez-le et présentez-vous au Maître de guerre Laggrond."}},
[7201] = {"Le dernier élément", {"Partir pour les Profondeurs de Blackrock et trouver 10 Essences des éléments. Commencer vos recherches par les golems et par les créateurs de golems. Vivian Lagrave a aussi marmonné quelque chose à propos d'élémentaires."}},
[7202] = {"Korrak le Ragesang", {"Korrak le Ragesang se cache dans les profondeurs des cavernes Winterax. D’après la légende, le chef des puissants trolls Winterax ne se montre qu’après la mort de 500 de ses guerriers.","Rendez-vous aux cavernes Winterax et massacrez tout ce qui vous fait obstacle jusqu’à ce que Korrak se montre. Tuez-le et présentez-vous au Lieutenant Haggerdin."}},
[7221] = {"Discuter avec la prospectrice Stonehewer", {"Discutez avec la Prospectrice Stonehewer à la Garnison de Dun Baldar."}},
[7222] = {"Discuter avec Voggah Deathgrip", {"Parlez à Voggah au donjon Frostwolf."}},
[7223] = {"Morceaux d'armures", {"Apportez 20 Morceaux d’armure à Murgot Deepforge à Dun Baldar."}},
[7224] = {"Pris à l'ennemi", {"Apportez 20 Morceaux d’armure au forgeron Regzar au village Frostwolf."}},
[7241] = {"La défense des Frostwolf", {"Rendez-vous dans la vallée d'Alterac, dans les contreforts d'Hillsbrad. Trouvez le maître de guerre Laggrond, parlez-lui et commencez votre carrière comme soldat Frostwolf."}},
[7261] = {"L'impératif souverain", {"Rendez-vous dans la vallée d'Alterac, dans les contreforts d'Hillsbrad. Présentez-vous au lieutenant Haggerdin.","Pour la gloire de Bronzebeard !"}},
[7281] = {"Amour fraternel", {"Pénétrez en territoire Stormpike et tuez le commandant Karl Philips. Lorsque ce sera fait, présentez-vous au commandant Louis Philips."}},
[7282] = {"Amour fraternel", {"Pénétrez en territoire Frostwolf et tuez le commandant Louis Philips. Lorsque ce sera fait, présentez-vous au commandant Karl Philips."}},
[7301] = {"Les ailes brisées", {"Enfoncez-vous en territoire Frostwolf à la recherche des chefs d’escadrille Slidor, Vipore et Ichman. Si vous les retrouvez, présentez-vous au commandant Duffy."}},
[7302] = {"Les ailes brisées", {"Découvrez ce qui est arrivé aux chefs d’escadrille Guse, Jetzor et Mulverick. Lorsque vous les aurez retrouvés, présentez-vous au commandant Mulfort."}},
[7321] = {"Bisque de tortue apaisante", {"Apporter 10 morceaux de Viande de tortue et des Epices apaisantes à Christoph Jeffcoat au Moulin-de-Tarren."}},
[7341] = {"Un marché honnête", nil},
[7342] = {"Les flèches, c'est pour les mauviettes", nil},
[7361] = {"En faveur auprès des Sombrelances", {"Vous avez été <chargé/chargée> de tuer les personnages Elfes de la nuit présents dans la vallée d’Alterac.","Tuez un Elfe de la nuit et présentez-vous à Najak Maléficas avec une Tête coupée d’Elfe de la nuit."}},
[7362] = {"Allié des Taurens", {"Vous avez été <chargé/chargée> de tuer les personnages gnomes présents dans la vallée d’Alterac.","Tuez un Gnome et présentez-vous à Ravak Totem-Sinistre au donjon Loup-de-givre avec une Touffe de cheveux de Gnome."}},
[7363] = {"La condition humaine", {"Vous avez été <chargé/chargée> de tuer les personnages humains présents dans la vallée d’Alterac.","Tuez un humain et présentez-vous au commandant Louis Philips (qui fait la navette entre le front et le donjon Loup-de-givre) avec un Bout d’os humain.","Un remède à la condition humaine est à portée de main !"}},
[7364] = {"Au service de Gnomeregan", {"Vous avez été <chargé/chargée> de tuer les personnages taurens présents dans la vallée d’Alterac.","Tuez un Tauren et présentez-vous à Dirk Lembrouille à Dun Baldar avec un Sabot de Tauren."}},
[7365] = {"Le requiem de Forteramure", {"Vous avez été <chargé/chargée> de tuer les personnages trolls présents dans la vallée d’Alterac.","Tuez un Troll et présentez-vous à Athramanis à Dun Baldar avec un Mojo de Troll sombrelance."}},
[7366] = {"La pitié de l'archevêque", {"Vous avez été <chargé/chargée> de tuer les personnages Réprouvés présents dans la vallée d’Alterac.","Tuez un Réprouvé et présentez-vous au commandant Karl Philips (qui fait la navette entre le scierie et la mine) avec un Cœur de Réprouvé."}},
[7367] = {"Désarmer les mines", {"Trouvez l’Expert en explosifs Frostwolf et tuez-le. Lorsque ce sera fait, présentez-vous à l’Expert en explosifs Stormpike."}},
[7368] = {"Désarmer les mines", {"Trouvez l’Expert en explosifs Stormpike et tuez-le. Lorsque ce sera fait, présentez-vous à l’Expert en explosifs Frostwolf."}},
[7381] = {"Le retour de Korrak", {"Si Korrak le Ragesang devait réapparaître dans le Champ sanglant, traquez-le et tuez-le.","Apportez le Crâne de Korrak au Maître de guerre Laggrond dans les contreforts de Hautebrande."}},
[7382] = {"Korrak l'Indestructible", {"Si Korrak le Ragesang réapparaît dans le Champ sanglant, traquez-le et tuez-le.","Apportez le Crâne de Korrak au lieutenant Haggerdin dans les Contreforts de Hautebrande."}},
[7383] = {"La couronne de la Terre", {"Remplir la Fiole d'améthyste et la rapporter à Corithras Moonrage à Dolanaar."}},
[7385] = {"Quelques litres de sang", nil},
[7386] = {"Grappes de cristaux", nil},
[7401] = {"ON RECHERCHE : des Nains !", {"Vous avez été <chargé/chargée> de tuer les personnages nains présents dans la vallée d’Alterac.","Tuez des Nains et arrachez-leur la colonne vertébrale. Apportez les Colonnes vertébrales de Nains au sergent Yazra Gronde-Sang au donjon Loup-de-givre."}},
[7402] = {"On recherche : ORCS !", {"Vous avez été chargé de tuer les personnages orcs présents dans la Vallée d’Alterac.","Tuez un orc et remettez les Dents d’orc au caporal Noreg Foudrepique à Dun Baldar."}},
