[7441] = {"Pusillin et l'Ancien Azj'Tordin", {"Rendez-vous à Hache-tripes et trouvez le diablotin Pusillin. Persuadez-le par tous les moyens de vous rendre le Livre d’Incantations d’Azj’Tordin.","Si vous parvenez à le récupérer, retournez voir Azj’Tordin au Pavillon de Lariss, en Feralas."}},
[7461] = {"Folie intérieure", {"Vous devez détruire les gardiens qui protègent les 5 Pylônes qui alimentent la Prison d’Immol’thar. Une fois que les Pylônes seront coupés, le champ de force qui entoure Immol’thar se dissipera.","Entrez dans la Prison d’Immol’thar et éradiquez l’infâme démon qui se trouve en son cœur. Enfin, affrontez le prince Tortheldrin à l’Athenaeum.","Lorsque vous aurez terminé, retournez voir l’Ancienne des Shen’dralar dans la cour."}},
[7462] = {"Le trésor des Shen'dralar", {"Retournez à l’Athenaeum et trouvez le Trésor des Shen’dralar. Prenez votre récompense !"}},
[7463] = {"Rafraîchissement arcanique", {"Rendez-vous dans le quartier de Crochebois de Hache-tripes et tuez Hydrogénos, l’élémentaire de l’eau. Retournez voir le Gardien du savoir Lydros à l’Athenaeum avec l’Essence d’Hydrogénos."}},
[7481] = {"Légendes elfiques", {"Fouillez Hache-tripes à la recherche de Telmius Cherche-Rêve. Lorsque vous aurez des informations, donnez-les au Sage Korolusk du Camp Mojache."}},
[7482] = {"Légendes elfiques", {"Fouillez Hache-tripes à la recherche de Telmius Cherche-Rêve. Lorsque vous aurez des informations, donnez-les à l’Erudite Runethorn, à Feathermoon."}},
[7483] = {"Libram de Rapidité", {"Apportez un Libram de rapidité, 1 Diamant noir en parfait état, 2 Grands éclats brillants et 2 doses de Sang des héros au Gardien du savoir Lydros à Hache-tripes pour recevoir un Arcanum de rapidité."}},
[7484] = {"Libram de focalisation", {"Apportez un Libram de focalisation, 1 Diamant noir en parfait état, 4 Grands éclats brillants et 2 Peaux des ombres au Gardien du savoir Lydros à Hache-tripes pour recevoir un arcanum de focalisation."}},
[7485] = {"Libram de Protection", {"Apportez un Libram de protection, 1 Diamant noir en parfait état, 2 Grands éclats brillants et 1 Couture d'abomination effilochée au Gardien du savoir Lydros à Hache-tripes pour recevoir un Arcanum de protection."}},
[7486] = {"La récompense du héros", {"Allez chercher votre récompense dans le Coffre d’Hydraxis."}},
[7488] = {"Le filet de Lethtendris", {"Ramenez le Filet de Lethtendris à Latronicus Moonspear au bastion de Feathermoon en Feralas."}},
[7489] = {"Le filet de Lethtendris", {"Ramenez le Filet de Lethtendris à Talo Thornhoof au Camp Mojache, en Feralas."}},
[7490] = {"Victoire pour la Horde !", {"Apportez la Tête d’Onyxia à Thrall à Orgrimmar."}},
[7491] = {"Pour que tous voient", {"Allez voir le seigneur Runthak dans la vallée de la Force."}},
