[7507] = {"Le guide de Dagharn", {"Rapporter « Le guide de Nostro du tueur de dragons » à l’Athenaeum."}},
[7508] = {"Forger Quel'Serrar", {"Donnez la Lame elfique inachevée au Gardien du savoir Lydros."}},
[7509] = {"Forger Quel'Serrar", {"Vous devez amener Onyxia à cracher le feu sur la Lame ancienne brute. Lorsque ce sera fait, ramassez la Lame ancienne chauffée. Attention, elle ne restera pas chaude éternellement. Le temps est très important.","Avant de revenir me voir, il vous faudra tuer la bête et plonger la Lame ancienne chauffée dans son cadavre.","Si vous y parvenez, Quel’Serrar sera à vous."}},
[7541] = {"Un service pour la Horde", nil},
[7562] = {"Mor'zul Bloodbringer", {"Parler à Mor'zul Bloodbringer dans les Steppes ardentes."}},
[7563] = {"Rage de sang", {"Apporter 30 bouteilles de Sang de bêtes enragées à Mor'zul Bloodbringer dans les Steppes ardentes."}},
[7564] = {"Gorzeeki Wildeyes", {"Apporter la Caisse de sang à Gorzeeki Wildeyes dans les Steppes ardentes."}},
[7581] = {"Les ancrages de la prison", {"Rendez-vous à Hache-tripes en Feralas et récupérez 15 doses de Sang de satyre sur les satyres Follengeance qui habitent le quartier de Crochebois. Lorsque ce sera fait, retournez voir Levandre à la Balafre impure."}},
[7582] = {"Les parois de la prison", {"Rendez-vous à la gorge du Sombre murmure, dans le Berceau-de-l'Hiver, et récupérez 5 Larmes de l’Hederine sur les démons Hederine qui occupent la gorge. Lorsque ce sera fait, retournez voir Levandre à la Balafre impure."}},
[7583] = {"Suppression", {"Pénétrez dans la Balafre impure et localisez un Commandant de la garde funeste.","Servez-vous de la Prison de cristal luminescente sur lui. Attendez-vous à être sauvagement <attaqué/attaquée>, car le démon fera l’impossible pour éviter d’être capturé.","Si vous réussissez, amenez le garde funeste emprisonné à Levadre à la Balafre impure."}},
[7601] = {"Les Commandements de Niby", {"Parlez à Démonet à Gangrebois."}},
[7602] = {"La gangressence parfaite", {"Démonet vous a demandé de lui apporter trois Gangressences parfaites, venant de trois régions distinctes.","Les satyres Legashi d'Azshara, les légionnaires de Jaedenar et les gangregardes sentinelles des Terres foudroyées détiennent la Gangressence parfaite de leur région.","Récupérez les Gangressences parfaites et rapportez-les à Démonet, à Gangrebois."}},
[7603] = {"Le noyau infernal de Kroshius", {"Fouillez le Val Grêlé pour trouver les restes de Kroshius. Quand vous les aurez localisés, utilisez le Gangrefeu à proximité et attendez le retour de Kroshius. Quand l'Infernal se sera réveillé, tuez-le et prenez le Noyau infernal sur son cadavre.","Rapportez le Noyau infernal de Kroshius à Niby le Tout-Puissant, à Gangrebois."}},
[7604] = {"Un contrat contraignant", {"Apportez le Contrat de la Confrérie du Thorium à Lokhtos Darkbargainer, si vous voulez recevoir les plans du marteau de sulfuron."}},
[7621] = {"Un avertissement", nil},
[7622] = {"L’équilibre de la Lumière et de l’Ombre", {"Sauvez 50 Paysans avant que 15 d'entre eux aient été tués. Si vous y parvenez, allez parler à Eris Havenfire.","Consultez le Panneau des morts pour voir combien de paysans ont été tués."}},
[7623] = {"Le seigneur Banehollow", {"Achetez des Potions des ténèbres à Gorzeeki dans les Steppes ardentes.","Servez-vous en pour vous rendre à Jaedenar, et parlez au seigneur Banehollow."}},
[7624] = {"Ulathek le traître", {"Affrontez Ulathek, puis apportez le Coeur du traître au seigneur Banehollow à Jaedenar."}},
[7625] = {"Poussière d'étoile xorothienne", {"Acheter de la Poussière d’étoiles xorothienne à Ur’dan. L’apporter à Gorzeeki dans les Steppes ardentes."}},
[7626] = {"La Cloche de Dethmoora", {"Apportez 10 Elixirs de Puissance de l'ombre à Gorzeeki Wildeyes dans les Steppes ardentes."}},
[7627] = {"La Roue de la Marche noire", {"Apportez 6 Grands éclats brilliants et 25 charges de Minerai de sombre fer à Gorzeeki dans les Steppes ardentes."}},
[7628] = {"La Chandelle de l'apocalypse", {"Apportez 35 Ecailles de dragon noir à Gozeeki WIldeyes dans les Steppes ardentes."}},
[7629] = {"Livraison de diablotin", {"Apporter le Diablotin en bouteille au laboratoire d'alchimie de la Scholomance. Une fois le parchemin créé, retournez voir Gorzeeki Wildeyes."}},
[7630] = {"L'arcanite", {"Apporter 3 Barres d’arcanite à Gorzeeki dans les Steppes ardentes."}},
[7631] = {"Le destrier de l'effroi de Xoroth", {"Lisez les Instructions de Mor’zul. Invoquez un Destrier de l’effroi xorothien, vainquez-le, et liez son esprit."}},
[7632] = {"La feuille ancienne", {"Trouvez le propriétaire de la Feuille ancienne pétrifiée. Bonne chance, <nom>, le monde est vaste."}},
[7633] = {"Une rencontre", nil},
[7634] = {"Feuille d'Ancien cousue de tendons", {"L'Ancien Hasat vous demande de lui ramener un Tendon de dragon bleu adulte. Si vous en trouvez un, ramenez-le-lui à Gangrebois"}},
[7635] = {"La bonne corde", {"L’Ancien Stoma vous demande de lui ramener un Tendon de dragon noir adulte. Si vous en trouvez un, ramenez-le à Stoma à Gangrebois."}},
[7636] = {"Le bâton des Anciens", {"Vous devez trouver et tuer quatre corrupteurs démoniaques :","Simona la Séductrice.","Klinfran l’Enragé.","Solenor le Tueur.","Artorius le Néfaste.","Détruisez ces créatures et retournez voir l’Ancien Vartus à Gangrebois.","Vous trouverez des indices sur leurs faits et gestes sur l’Ecorce pétrifiée qui se trouve dans votre inventaire.","Vous DEVEZ agir seul."}},
[7637] = {"L'accent sur le sacrifice", {"Rendez-vous à Ironforge et procurez-vous l’Encensoir d’exorcisme du grand prêtre Rohan. Pour vous le procurer, il vous faudra verser une donation de 150 pièces d’or."}},
[7638] = {"Le seigneur Grayson Shadowbreaker", {"Parler au seigneur Grayson Shadowbreaker dans le Quartier de la cathédrale de Stormwind."}},
[7639] = {"Des preuves de jugement", {"Ramener l’Encensoir d’exorcisme au seigneur Grayson Shadowbreaker, dans le Quartier de cathédrale de Stormwind."}},
[7640] = {"Exorciser Val-Terreur", {"Servez-vous de l’Encensoir d’exorcisme pour chasser les esprits qui tourmentent Val-Terreur. Lorsque vous aurez tué 25 Esprits de Val-Terreur, retournez voir le seigneur Grayson Shadowbreaker dans le Quartier de la cathédrale de Stormwind."}},
[7641] = {"L'œuvre de Grimand Elmore", {"Parlez à Grimand Elmore dans le Quartier des nains de Stormwind."}},
[7642] = {"Une ribambelle d'objets", {"Apporter 40 Etoffes runiques, 6 Barres d’arcanite, 10 Larmes d’Arthas, 5 Fioles d’eau sacrée de Stratholme, et 150 pièces d’or à Grimand Elmore, au Quartier des nains de Stormwind."}},
[7643] = {"L'ancien esprit équin", {"Procurez-vous un aliment pour chevaux capable de nourrir un esprit équin. Il semble que Merideth Carlson, à Southshore, sache en fabriquer.","Ensuite, rendez-vous au donjon de Hache-tripes en Feralas, et tuez Tendris Crochebois. Sa mort libérera l’Ancien esprit équin. Nourrissez-le avec l’aliment spécial afin de l’apaiser. Enfin, donnez-lui le Caparaçon d’arcanite pour qu’il le bénisse."}},
[7644] = {"Le caparaçon en arcanite béni", {"Retourner dans le quartier de la cathédrale de Stormwind avec le Caparaçon d’arcanite béni pour voir le seigneur Grayson Shadowbreaker."}},
[7645] = {"Fourrage enrichi en manne", {"Récupérer 20 Biscuits enrichis en manne, l’ingrédient vital qui permet de préparer du Fourrage enrichi en manne, pour le compte de Merideth Carlson de Southore dans les contreforts d’Hillsbrad. L’Aube d’argent est la seule à fabriquer ces biscuits.","Vous devrez également lui donner 50 pièces d’or pour apaiser sa susceptibilité."}},
[7646] = {"Le Clairvoyant", {"Apportez un Diamant d’Azeroth et un Diamant noir en parfait état au seigneur Grayson Shadowbreaker dans le Quartier de la cathédrale de Stormwind."}},
[7647] = {"Jugement et rédemption", {"Utilisez le Clairvoyant au coeur du sous-sol du Grand ossuaire, dans la Scholomance. Cela attirera des esprits que vous devrez juger. Lorsqu’ils seront vaincus, le Chevalier de la mort Ravassombre apparaîtra. Vainquez-le et purifiez l’âme du destrier déchu.","Ensuite, donnez l’Ame rachetée du destrier et le Caparaçon enchanté et béni au Destrier déchu de Ravassombre."}},
[7648] = {"Le chef d'œuvre de Grimand", {"Présentez le Caparaçon d’arcanite au seigneur Grayson Shadowbreaker dans le Quartier de la cathédrale de Stormwind, pour inspection."}},
[7649] = {"Les plates en thorium enchanté - Volume I", {"Rendez ce livre à ses légitimes propriétaires."}},
[7650] = {"Les plates en thorium enchanté - Volume II", {"Rendez ce livre à ses légitimes propriétaires."}},
[7651] = {"Les plates en thorium enchanté - Volume III", {"Rendez ce livre à ses légitimes propriétaires."}},
