[7701] = {"ON RECHERCHE : Surveillant Maltorius", {"Loin dans les profondeurs de la Fosse aux scories dans le Chaudron, dans la Gorge des Vents brûlants, vous trouverez le nain traître du clan Sombrefer répondant au nom de surveillant Maltorius.","Tuez le surveillant Maltorius et prenez sa tête. Apportez-la au capitaine de guet Lolo Longstriker dans la Gorge des Vents brûlants pour la récompense."}},
[7703] = {"Affaires inachevées chez les Gordok", {"Trouvez le Gantelet de Puissance Gordok et rendez-le au capitaine Kromcrush à Hache-tripes.","Selon Kromcrush, les histoires des « vieux » racontent que Tortheldrin – un « vilain » elfe qui se fait appeler prince – l’a volé à l’un des rois gordok."}},
[7704] = {"Regardez un peu la taille !", {"Apportez l'Oreiller de la Femme de chambre Pillaclencher à Evonice Sootsmoker dans la Gorge des Vents brûlants."}},
[7721] = {"Du carburant pour la zappette", {"Récupérez 10 Noyaux élémentaires d'eau sur les Elémentaires marins et les Embruns le long de la Côte oubliée de Feralas, et rapportez-les à Zorbin Fandazzle, près du quai là-bas."}},
[7722] = {"Le Flux embrasé", {"Quelque part dans la Fosse aux scories du Chaudron, vous trouverez les Plans secrets : Flux embrasé dérobés par le surveillant Maltorius. Trouvez ces plans et rapportez-les au Maître forgeron Burninate dans la gorge des Vents brûlants."}},
[7723] = {"Maudits gros doigts", {"Hansel Heavyhands veut que vous tuiez 20 Golems de guerre lourds.","Les golems de guerre lourds errent dans les régions qui entourent le Chaudron dans la Gorge des Vents brûlants. Quand vous en aurez tué assez, revenez voir Hansel dans la Gorge des Vents brûlants."}},
[7724] = {"Une menace incandescente", {"Hansel Heavyhands veut que vous tuiez 20 Grandes araignées de lave dans la gorge des Vents brûlants. Vous pouvez trouver les Grandes araignées de lave dans les plateaux et les mares de lave de la gorge des Vents brûlants.","Revenez voir Hansel dans la gorge des Vents brûlants quand vous aurez tué assez de Grandes araignées de lave."}},
[7725] = {"Le retour des géants rétrécis", {"Utilisez l'Ultra-réducteur de Zorbin sur des géants de n'importe quel type en Feralas afin les rendre plus faciles à gérer. Apportez 10 Résidus de miniaturisation que vous trouverez sur les versions rétrécies des géants à Zorbin Fandazzle aux docks de la Côte oubliée en Feralas."}},
[7726] = {"Encore du carburant pour la zappette", {"Récupérez 6 Noyaux élémentaires d'eau sur les Elémentaires marins et les Embruns le long de la Côte oubliée de Feralas, et rapportez-les à Zorbin Fandazzle, près du quai."}},
[7727] = {"Incendosaures ? N'importosaures, plutôt", {"Hansel Heavyhands veut que vous tuiez 20 Incendosaures.","On peut les trouver dans les bassins de lave de la Fosse aux scories, qui occupe une partie du Chaudron de la gorge des Vents brûlants.","Retournez voir Hansel Heavyhands dans la gorge des Vents brûlants lorsque vous aurez tué suffisamment d’Incendosaures."}},
[7728] = {"OBJETS VOLES : Tuyère et longue-vue", {"Trouvez la Tuyère de forge et la Longue-vue du guetteur et rapportez-les au sous-chef Scrange dans la gorge des Vents brûlants.","Les seules informations dont vous disposez sur ces objets sont les suivantes : ils ont été dérobés par les nains Sombrefer, la Tuyère de forge est un outil utilisé par les forgerons et la Longue-vue du guetteur est un outil de surveillance inestimable pour les guetteurs."}},
[7729] = {"OFFRE D'EMPLOI : Réduire la concurrence", {"Tuez 15 Sous-chefs Sombrefer et 15 Esclavagistes Sombrefer. Revenez voir le sous-chef Scrange dans la gorge des Vents brûlants quand vous aurez accompli cette tâche."}},
[7730] = {"Infestation de Zukk'ash", {"Rapportez 20 Carapaces de Zukk'ash à Hadoken Swiftstrider au Camp Mojache en Feralas."}},
[7731] = {"Fouet-cuisant", {"Battez Fouet-cuisant dans le Gouffre grouillant et rapportez ses glandes à Hadoken Swiftstrider au Camp Mojache en Feralas."}},
[7732] = {"Le rapport sur les Zukk'ash", {"Apportez le rapport sur les Zukk'ash du Camp Mojache à Zilzibin Drumlore. Il réside à la Herse d'Orgrimmar"}},
[7733] = {"Qualité améliorée", {"Fournir 10 Peaux de yéti Griffe Féroce à Pratt McGrubben au bastion de Feathermoon, en Feralas."}},
[7734] = {"Qualité améliorée", {"Fournir 10 Peaux de yéti Griffe Féroce à Jangdor Swiftstrider au Camp Mojache, en Feralas."}},
[7735] = {"Une peau de yéti en parfait état", {"Apporter la Peau de yéti en parfait état à Pratt McGrubben au bastion de Feathermoon, en Feralas."}},
[7736] = {"Réapprovisionnement en flux embrasé (Sang-royal)", nil},
[7737] = {"Se faire accepter", nil},
[7738] = {"Une peau de yéti parfaite", {"Apporter la Peau de yéti parfaite à Jangdor Swiftstrider au Camp Mojache, en Feralas."}},
[7761] = {"Les ordres de Blackhand", {"Cet orc est stupide. Il semble que vous deviez trouver un moyen d’obtenir la Marque de Drakkisath pour accéder à l’Orbe de commandement.","D’après la lettre, la Marque est gardée par le général Drakkisath. Vous devriez peut-être enquêter."}},
[7781] = {"Le seigneur de Blackrock", {"Apporter la Tête de Nefarian au généralissime Bolvar Fordragon à Stormwind."}},
[7782] = {"Le seigneur de Rochenoire", {"Allez à la vallée des Héros de Hurlevent et parlez au grand maréchal Afrasiabi."}},
[7783] = {"Le seigneur de Blackrock", {"Apportez la Tête de Nefarian à Thrall à Orgrimmar."}},
[7784] = {"Le seigneur de Blackrock", {"Parlez au Haut seigneur Saurfang à Orgrimmar. Il vous attend dans la vallée de la Force."}},
[7785] = {"Examiner le Calice", {"Examiner le Calice de la renaissance et le rapporter au généralissime Demitrian."}},
[7786] = {"Thunderaan le Cherchevent", {"Pour libérer Thunderaan le Cherchevent de sa prison, vous devez présenter au généralissime Demetrian les moitiés droite et gauche des Liens du Cherchevent, 10 barres d'élémentium, et l'Essence du Seigneur du feu."}},
[7787] = {"Éveille-toi, Lame-tonnerre !", {"Présenter la Lame du baiser du vent dormante au généralissime Demitrian."}},
[7788] = {"Triomphez des envahisseurs !", {"Entrez dans le goulet des Warsong, triomphez de la Horde et gagnez une Marque d'honneur du goulet des Warsong. Puis revenez voir la sentinelle Farsong au bosquet d'Aile-argent."}},
[7789] = {"Écrasez les usurpateurs d'Aile-argent", {"Entrez dans le goulet des Warsong, triomphez de l'Alliance et obtenez une Marque d'honneur du goulet des Warsong. Puis revenez voir le capitaine Shatterskull au campement de Mor'shan."}},
