[8283] = {"On recherche : Poigne-de-mort, Terreur des sables", {"Apporter la Pince de Poigne-de-mort à Vish Kozus, capitaine de la garde."}},
[8284] = {"Le mystère du Crépuscule", {"La géologue Larksbane du Fort cénarien en Silithus veut que vous rassembliez 8 Fragments de Tablette du crépuscule."}},
[8285] = {"Le déserteur", {"Apportez la Tablette réparée à l'ermite Ortell en Silithus."}},
[8286] = {"Ce que demain apportera", {"Rendez vous dans les grottes du Temps en Tanaris et trouvez Anachronos, la progéniture de Nozdormu."}},
[8287] = {"Un terrible but", {"Remettez la Transcirption décodée de la tablette au commandant Mar'alith."}},
[8288] = {"Il ne peut y en avoir qu'un", {"Rapportez la Tête du seigneur des couvées Lanistaire à Baristolth des Sables changeants, au Fort cénarien en Silithus."}},
