[8553] = {"Le sabre du capitaine Smott", {"Parler à Sprogger."}},
[8554] = {"Face à Negolash", {"Apporter 10 Ailes de busard grillées et 5 bouteilles de Vin de Junglevine dans le Canot de sauvetage du capitaine Smott.","Tuer Negolash, et apporter le Coutelas de Smotts au capitaine Smotts, à Strangleronce."}},
[8555] = {"Le fardeau des Vols draconiques", nil},
[8556] = {"Chevalière de force inflexible", {"Apporter 1 Anneau de magistrat qiraji, 2 Idoles brillantes, 5 Scarabées en bronze et 5 Scarabées d'ivoire à l'imploratrice céleste Yessendra, en Silithus. Vous devrez aussi être <Honoré/Honorée> par le Cercle cénarien pour terminer cette quête."}},
[8557] = {"Drapé de force inflexible", {"Apporter 1 Drapé martial qiraji, 2 Idoles d'onyx, 5 Scarabées d'argent et 5 Scarabées d'os à Keyl Swiftclaw, en Silithus. Vous devrez aussi être <Révéré/Révérée> par le Cercle cénarien pour terminer cette quête."}},
[8558] = {"La faucille de force inflexible", {"Apporter 1 Manche à pointes qiraji, 2 Idoles d'albâtre, 5 Scarabées de cristal et 5 Scarabées de pierre au Gardien Haro, en Silithus. Vous devrez aussi être <Exalté/Exaltée> par le Cercle cénarien pour terminer cette quête."}},
[8559] = {"Les grèves de conquérant", {"Apportez des Manchettes de commandement qiraji, 2 Idoles de la guerre, 5 Scarabées d’ivoire et 5 Scarabées d’or à Kandrostrasz, à Ahn'Qiraj. Il faut aussi que la Progéniture de Nozdormu soit au moins Neutre à votre égard."}},
[8560] = {"Les garde-jambes de conquérant", {"Ramener la Peau intacte d’Ouro, 2 Idoles de la mort, 5 Scarabées en bronze et 5 Scarabées d’ivoire à Kandrostrasz, à l’intérieur d’Ahn'Qiraj. Pour terminer cette quête, il faut également que la Progéniture de Nozdormu soit au moins Amicale à votre égard."}},
[8561] = {"La couronne de conquérant", {"Apportez le Diadème de Vek’nilash, 2 Idoles du soleil, 5 Scarabées de pierre et 5 Scarabées de cristal à Andorgos, à Ahn’Qiraj. Pour terminer cette quête, il faut également que la Progéniture de Nozdormu soit au moins Amicale à votre égard."}},
[8562] = {"La cuirasse de conquérant", {"Apportez la Carapace du Dieu très ancien, 2 Idoles de la guerre, 5 Scarabées d’argent et 5 Scarabées d’os à Vethsera, à l’intérieur d’Ahn’Qiraj. Pour terminer cette quête, vous devez être <Honoré/Honorée> par la Progéniture de Nozdormu."}},
