[8778] = {"La brigade d'Ironforge a besoin d'explosifs !", {"Apporter 6 Huildes d’immolation, 5 doses de Carburant de fusée des gobelins et 10 doses de Poudre d’explosion dense à l’arcaniste Nozzlespring, près de la Ruche’Zora en Silithus. Vous devez aussi apporter le Briefing logistique IV pour terminer cette quête."}},
[8779] = {"Le matériel de divination", {"Apporter un 1 Grand éclat brillant, 1 Gros éclat irradiant et 1 Enorme émeraude à la géologue Larksbane, au Fort cénarien en Silithus. Vous devez aussi apporter le Briefing logistique V pour terminer cette quête."}},
[8780] = {"Des renforts d'armure pour le champ de bataille", {"Apportez 8 Renforts d’armure robustes et 8 Renforts d’armure louds à Janela Stouthammer, à l’avant-poste de la brigade d’Ironfoge, près de la Ruche’Zora. Vous devez aussi apporter le Briefing logistique VII pour terminer cette quête."}},
[8781] = {"Des armes pour le champ de bataille", {"Apportez 2 Epées d’acier lunaire à Janela Stouthammer, à l’avant-poste de la brigade d’Ironfoge, près de la Ruche’Zora. Vous devez aussi apporter le Briefing logistique VI pour terminer cette quête."}},
[8782] = {"La question des uniformes", {"Remettre 1 Etoffe lunaire, 2 Rouleaux d’étoffe runique et 1 Soie d’araignée toilacier à l’Imploratrice céleste Proudhorn au Fort cénarien en Silithus. Vous devez aussi apporter le Briefing logistique VIII pour terminer cette quête."}},
[8783] = {"Des matériaux extraordinaires", {"Remettre 2 Barres de thorium enchanté et 2 Cuirs enchantés à Vargus au Fort cénarien en Silithus. Vous devez aussi apporter le Briefing logistique IX pour terminer cette quête."}},
[8784] = {"Les secrets des Qiraji", {"Remettre l’Ancien artefact qiraji aux dragons qui se cachent près de l’entrée du temple."}},
[8785] = {"La légion d'Orgrimmar a besoin de mojo !", {"Apporter 6 Mojos puissants, 6 Flacons de grand mojo et 8 Huiles d’immolation à la Prêtresse des ombres Shai, près de la Ruche’Regal en Silithus. Pour terminer cette quête, vous devez aussi apporter le Briefing logistique IV."}},
[8786] = {"Des armes pour le champ de bataille", {"Remettre 3 Gigantesques haches en fer à Merok Longstride, au camp de la légion d’Orgrimmar, à l’extérieur de la Ruche’Regal. Pour terminer cette quête, vous devez aussi apporter le Briefing logistique VI."}},
[8787] = {"Des renforts d'armure pour le champ de bataille", {"Apportez 8 Renforts d’armure robustes et 8 Renforts d’armure lourds à Merok Longstride près de la Ruche'Regal. Pour terminer cette quête, vous devez aussi apporter le Briefing logistique VII."}},
