[9033] = {"Les échos de la guerre", {"Le commandant Eligor Portelaube, de la chapelle de l’Espoir de Lumière dans les Maleterres de l'Est, veut que vous tuiez 5 Monstruosités vivantes, 5 Gargouilles peau de pierre, 8 Capitaines des chevaliers de la mort et 3 Traque-venin."}},
[9034] = {"Cuirasse de cuirassier", {"Korfax, à la chapelle de l’Espoir de Lumière dans les Maleterres de l’Est, vous fabriquera une cuirasse de cuirassier si vous lui apportez les choses suivantes : 1 Cuirasse désacralisée, 25 Débris d’armure en plaques, 4 Barres d’arcanite et 2 Cristaux de nexus."}},
[9036] = {"Cuissards de cuirassier", {"Korfax, à la chapelle de l’Espoir de Lumière dans les Maleterres de l’Est, vous fabriquera des cuissards de cuirassier si vous lui apportez les choses suivantes : 1 paire de Cuissars désacralisés, 20 Débris d’armure en plaques, 4 Barres d’arcanite et 3 Peaux robustes traitées."}},
[9037] = {"Casque de cuirassier", {"Korfax, à la chapelle de l’Espoir de Lumière dans les Maleterres de l’Est, vous fabriquera un casque de cuirassier si vous lui apportez les choses suivantes : 1 Heaume désacralisé, 15 Débris d’armure en plaques, 5 Barres d’arcanite et 1 Cristal de nexus."}},
[9038] = {"Espauliers de cuirassier", {"Korfax, à la chapelle de l’Espoir de Lumière dans les Maleterres de l’Est, vous fabriquera des espauliers de cuirassier si vous lui apportez les choses suivantes : 1 paire d'Espauliers désacralisés, 12 Débris d’armure en plaques, 2 Barres d’arcanite et 3 Peaux robustes traitées."}},
[9039] = {"Solerets de cuirassier", {"Korfax, à la chapelle de l’Espoir de Lumière dans les Maleterres de l’Est, vous fabriquera des solerets de cuirassier si vous lui apportez les choses suivantes : 1 paire de Solerets désacralisés, 12 Débris d’armure en plaques, 2 Barres d’arcanite et 3 Peaux robustes traitées."}},
[9040] = {"Gantelets de cuirassier", {"Korfax, à la chapelle de l’Espoir de Lumière dans les Maleterres de l’Est, vous fabriquera des gantelets de cuirassier si vous lui apportez les choses suivantes : 1 paire de Gantelets désacralisés, 8 Débris d’armure en plaques, 1 Barre d’arcanite et 5 Peaux robustes traitées."}},
