[5152] = {"Tia Marlene", {"Fale com Marlene, ao sul das Ruínas de Andorhal."}},
[5153] = {"Uma historiadora estranha", {"Leve o Anel de Casamento do Josefo para Crona."}},
[5154] = {"Os Anais da Vila das Flechas", {"Leve os Anais da Vila das Flechas para Crona em Andorhal."}},
[5155] = {"Forças de Jaedenar", {"Entre em Jaedenar e mate 4 Cães de Jaedenar, 4 Guardas de Jaedenar, 6 Adeptos de Jaedenar e 6 Sectários de Jaedenar. Depois, fale novamente com Greta Musgo no Casco, na Selva Maleva."}},
[5156] = {"Verificar a corrupção", {"Procure e destrua 2 Feras Entrópicas e 2 Horrores Entrópicos enquanto explora o Vale das Escaras Pungentes. Depois de ver se as crateras ainda estão cheias de água contaminada com vileza, fale novamente com Taronn Pena Vermelha, na Selva Maleva."}},
[5157] = {"Coletar a água corrompida", {"Leve o Cantil Vazio para o Poço Lunar no meio de Jaedenar e encha-o. Depois, fale novamente com Greta Musgo no Casco na Selva Maleva."}},
[5158] = {"À procura de auxílio espiritual", {"Leve a Água Corrompida do Poço Lunar para Islen Vidente da Água, nos Sertões."}},
[5159] = {"O retorno da água purificada à Selva Maleva", {"Leve a Água Purificada do Poço Lunar para Greta Musgo no Casco, na Selva Maleva."}},
[5160] = {"A protetorada-máter", {"Viaje para Hibérnia e encontre Haleh. Entregue para ela a Escama do Albi."}},
[5161] = {"Ira da Revoada Azul", {"Fale com Haleh se quiser continuar."}},
[5162] = {"Ira da Revoada Azul", {"Fale com Jezibá nas Terras Pestilentas. Ele mora em Andorhal."}},
[5163] = {"Eu yeti falar uma coisa...", {"Leve o Yeti Mecânico da Umi e assuste os amigos dela:","Legacki, em Visteterna (Hibérnia),","Sguisha, em Geringontzan (Tanaris) e","Quixxil, no Refúgio do Marshal (Cratera Un'Goro).","Quando terminar, leve o Yeti Mecânico de volta para Umi."}},
[5164] = {"Catálogo do Imprevisível", {"Leia o Catálogo do Imprevisível."}},
[5165] = {"Extinguindo as chamas da proteção", {"Use a Água Purificada do Poço Lunar para extinguir os quatro braseiros de proteção no interior da Fortaleza do Concílio das Sombras, em Selva Maleva, depois fale novamente com Greta Musgo no Casco, no Santuário Esmeralda."}},
[5166] = {"Peitoral da Revoada Cromática", {"Para forjar o Peitoral da Revoada Cromática, você deverá levar os seguintes itens para Jezibá, o Escultor:","1 Carapaça Cromática.","10 Escamas Cromáticas Brilhantes.","10 Sangues dos Heróis.","10 Suturas de Abominação Puídas."}},
[5167] = {"Coxotes do Desafiante Cromático", {"Para forjar os Coxotes do Desafiante Cromático, você deverá levar os seguintes itens para Jezibá, o Escultor:","1 Carapaça Cromática.","10 Escamas Cromáticas Brilhantes.","10 Sangues de Heróis.","5 Peles de Sombra."}},
[5168] = {"Heróis da Vila das Flechas", {"Leve o Incunábulo de Davil e o Escudo de Trilharrubra para Carlin Trilharrubra."}},
[5181] = {"Vilões da Vila das Flechas", {"Leve o Crânio de Horgus e a Espada Estilhaçada de Marduk para Carlin Trilharrubra na Capela Esperança da Luz."}},
[5201] = {"Invasão dos Invernosos", {"Ripárion Glaciares quer que você mate 5 Xamãs Invernosos e 5 Ursinos Invernosos."}},
[5202] = {"Uma estranha chave vermelha", {"Procure em Jaedenar o que pode ser aberto pela Chave Vermelho Sangue."}},
[5203] = {"Resgate de Jaedenar", {"Escolte Arko'narin até onde Forjaleve e o equipamento dela estão, e então a proteja enquanto escapam da Fortaleza do Concílio das Sombras. Depois, procure Jessir Lunarco em Selva Maleva para informá-la sobre o que houve com os amigos dela."}},
[5204] = {"Retaliação da Luz", {"Volte a Jaedenar e mate o súcubo Rakaiah antes de encontrar os Restos Mortais de Trey Forjaleve."}},
[5206] = {"Espoliadores da Vila das Flechas", {"Leve 5 Crânios Ressonantes e o Cristal Místico para Carlin Trilharrubra nas Terras Pestilentas Orientais."}},
[5210] = {"Irmão Carlin", {"Leve os Anais Estendidos da Vila das Flechas para Carlin Trilharrubra nas Terras Pestilentas Orientais."}},
[5211] = {"Defensores da Vila das Flechas", {"Liberte 15 Espíritos da Vila das Flechas e fale novamente com Carlin Trilharrubra."}},
[5212] = {"A carne não mente", {"Recupere 20 Amostras de Carne Infectada de Stratholme e leve-as para Betina Bigozinco. Você desconfia que qualquer criatura em Stratholme poderia ter essa amostra de carne."}},
[5213] = {"O agente ativo", {"Vá para Stratholme e vasculhe os zigurates. Encontre novos Dados do Flagelo e leve-os para Betina Bigozinco."}},
[5214] = {"O grande Ezra Carranca", {"Encontre a tabacaria de Ezra Carranca em Stratholme e recolha uma caixa do Tabaco da Lata do Carranca. Fale novamente com Leandro Leonardo depois que tiver terminado o serviço."}},
[5215] = {"Os caldeirões do Flagelo", {"Fale com a Alta-sacerdotisa Donélia no Morro do Ventogelante, que fica nas Terras Pestilentas Ocidentais."}},
[5216] = {"Alvo: Campo Pedravil", {"Vá ao Campo Pedravil nas Terras Pestilentas Ocidentais para localizar e derrotar o Mestre do Caldeirão de lá. Ele pode ter uma chave que dê acesso ao caldeirão. Você precisa estar com a Garrafa do Campo Pedravil Vazia para guardar uma amostra dos venenos usados no caldeirão."}},
[5217] = {"Retorno ao Acampamento Ventogelante", {"Leve a Garrafa do Campo Pedravil Cheia à Alta-sacerdotisa Donélia no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais."}},
[5218] = {"Caldeirão do Campo Pedravil", nil},
[5219] = {"Alvo: Lágrimas de Dalson", {"Vá a Lágrimas de Dalson nas Terras Pestilentas Ocidentais para localizar e derrotar o Mestre do Caldeirão de lá, e use a chave dele para ter acesso ao caldeirão. Você precisa estar com a Garrafa das Lágrimas de Dalson Vazia para guardar uma amostra dos venenos usados no caldeirão."}},
