[5525] = {"Liberdade ainda que tardia", nil},
[5526] = {"Pedaços de Vinhavil", {"Encontre a Vinhavil no Gládio Cruel e obtenha uma lasca dela. É bem provável que você só consiga encontrar uma depois da derrota de Azzin, o Selvamorfo. Use o Relicário da Pureza para selar a lasca com segurança e leve-a de volta para Ravine Saturna, no Refúgio Noturno, que fica na Clareira da Lua."}},
[5527] = {"Relicário da Pureza", {"Viaje até Silithus e procure um Relicário da Pureza dentro das ruínas da Vila do Vento Sul. Se conseguir encontrá-lo, regresse com ele até Ravine Saturna, no Refúgio Noturno, que fica na Clareira da Lua."}},
[5528] = {"O teste de sabor dos Gordok", nil},
[5529] = {"Filhotes Pestilentos", {"Mate 20 Filhotes Pestilentos, depois fale novamente com Betina Bigozinco, na Capela Esperança da Luz."}},
