[6103] = {"Treinando a fera", {"Fale com Jocasta no Enclave Cenariano, que fica em Darnassus."}},
[6121] = {"Novas lições", {"Vá à Clareira da Lua e fale com Dendrite Stellardor no vilarejo de Refúgio Noturno."}},
[6122] = {"A fonte principal", {"Use o Coletor Vazio das Quedas de Fontescarpa para coletar uma amostra de água da entrada da caverna da cachoeira.","Entregue o Coletor Cheio das Quedas de Fontescarpa para Alanndária Noturcanto em Auberdine, na Costa Negra."}},
[6123] = {"Colhendo a cura", {"Leve 5 Raízes-telúricas e 12 Fungos Lunares para Alanndária Noturcanto em Auberdine para que ela possa preparar o bálsamo curativo."}},
[6124] = {"Curando os doentes", {"Use o Bálsamo Curativo para Animais em 10 Cervos Adoentados espalhados pela Costa Negra. Isso deve curá-los. Cervos Adoentados foram avistados da região ao sul do Rio Fontescarpa, seguindo até a área ao norte de Auberdine, até a Costa Negra meridional, na fronteira do Vale Gris."}},
[6125] = {"O poder sobre os venenos", {"Fale novamente com Mathrengyl Ursivagus no Enclave Cenariano, em Darnassus."}},
[6126] = {"Novas lições", {"Vá à Clareira da Lua e fale com Dendrite Stellardor no vilarejo de Refúgio Noturno."}},
[6127] = {"A fonte principal", {"Use o Coletor Vazio do Morro de Brumedo para obter uma amostra de água de um lago no topo do morro.","Entregue o Coletor Cheio do Morro de Brumedo a Tonga Runa Totem na Encruzilhada, nos Sertões."}},
[6128] = {"Colhendo a cura", {"Leve 5 Raízes-telúricas e 5 Chifres de Kodo para Tonga Runa Totem na Encruzilhada para que ele possa preparar um bálsamo curativo."}},
[6129] = {"Curando os doentes", {"Use o Bálsamo Curativo para Animais em 10 Gazelas Doentes espalhadas pelo norte dos Sertões. Isso deve curá-las. Gazelas Doentes foram avistadas por toda parte a norte da estrada leste-oeste, que atravessa a Encruzilhada."}},
[6130] = {"O poder sobre os venenos", {"Fale novamente com Turak Runa Totem no Platô dos Anciãos, no Penhasco do Trovão."}},
[6132] = {"Me tira daqui!", {"Escolte Melizza Zumbidorla e depois fale com Guido Zumbidorla no Cemitério dos Kodos, na Desolação."}},
[6133] = {"A mando do lorde patrulheiro", {"Vá à fronteira norte das Terras Pestilentas Orientais e recupere os Arquivos de Quel'Thalas em algum lugar do Abrigo Quel'Lithien.","Você também precisa matar 8 Patrulheiros, 8 Trilheiros e 8 Mateiros."}},
[6134] = {"Vá catar fantasmoplasma!", {"Leve 8 Fantasmoplasmas e o Caixote de Ímãs Fantasmais para Guido Zumbidorla na Desolação."}},
[6135] = {"Ah, Quiroscuro, como vos odeio...", {"Encontre Quiroscuro e mate-o. Do cadáver, extraia um Tufo de Pele de Quiroscuro e leve para Nathanos Arauto da Praga.","Dizem que Quiroscuro vaga pelo campo ao norte do Sítio dos Marris."}},
[6136] = {"O corpulento", {"Encontre Brocassangre nos ermos do norte das Terras Pestilentas e destrua-o. Fale novamente com Nathanos Arauto da Praga quando tiver cumprido a missão."}},
[6141] = {"Irmão Anton", {"Fale com o Irmão Anton na Desolação."}},
[6142] = {"Boa isca", {"Encontre 10 nacos de Carne de Marisco de Casca Mole e entregue-os para Mai'Lahii na Aldeia Pescassombra."}},
[6143] = {"Camarão que dorme a onda leva", {"Mate 7 Mirmidões Sibilâmina, 7 Nagas Sibilâmina e 5 Feiticeiras Sibilâmina. Em seguida, fale com Drulzeger, na Aldeia Pescassombra, em Desolação."}},
[6144] = {"Chamado ao comando", {"Vá à Cidade Baixa e fale com Varimatras."}},
[6145] = {"O mensageiro carmesim", {"Volte às Terras Pestilentas Orientais e rastreie o Mensageiro Carmesim. Mate o mensageiro e obtenha as Ordens do Grão-cruzado.","Leve-as para Nathanos Arauto da Praga para obter suas próximas instruções."}},
[6146] = {"O ardil de Nathanos", {"Vá à Manopla de Tyr, a sudeste do Sítio dos Marris. Quando chegar lá, coma um pedaço da Maçã Podre. Com o disfarce da Cruzada Escarlate, entregue as Ordens do Grão-cruzado ao Lorde Cruzado Valdelmar."}},
[6147] = {"Retorno a Nathanos", {"Fale novamente com Nathanos Arauto da Praga para repassar a ele as novas informações."}},
[6148] = {"Demétria, o Oráculo Escarlate", {"A Cruzada Escarlate está em marcha. Em algum ponto da estrada da Manopla de Tyr para Stratholme estará o Oráculo, Demétria, e a comitiva dela. Encontre-a e mate-a. Fale novamente com Nathanos Arauto da Praga se tiver sucesso."}},
[6161] = {"O tesouro de Rodovalho!", {"Encontre a Chave Prateada de Rodovalho e a Chave Dourada de Rodovalho. Encontre e abra o Baú de Rodovalho."}},
[6162] = {"A última batalha de um marido", {"Mate o Suserano Ror e leve a garra dele para Dreka'Sur no Posto Peçonha, na Selva Maleva."}},
[6163] = {"Ramstein", {"Vá a Stratholme e mate Ramstein, o Devorador. Leve a cabeça dele como souvenir para Nathanos."}},
[6164] = {"O livro-caixa do Augustus", {"Leve o Livro-caixa do Augustus para Augustus, o Pirado, na Várzea do Medo."}},
[6181] = {"Mensagem rápida", {"Mostre o Bilhete do Ludovico a Thor, o mestre de grifos."}},
[6182] = {"O primeiro e último", {"Fale com Mathias Shaw na Cidade Velha de Ventobravo. Ele mora no Quartel."}},
[6183] = {"Em honra aos mortos", {"Fale com Mathias Shaw novamente se desejar aceitar esta tarefa."}},
[6184] = {"Flint Umbramais", {"Vá até o Acampamento Ventogelante, nas Terras Pestilentas Ocidentais, e encontre seu contato, Flint Umbramais."}},
[6185] = {"As Pestes do Leste", {"Vasculhe as Terras Pestilentas Orientais em busca de pistas sobre o \"Arauto da Praga\" e os agentes da AVIN desaparecidos. Ser encontrar alguma Insígnia da AVIN, leve de volta para Flint Umbramais, no Acampamento Ventogelante."}},
[6186] = {"A ascensão do Arauto da Praga", {"Retorne a Ventobravo e informe o Grão-lorde Bolvar Fordragon sobre o destino de Nathanos Marris."}},
[6187] = {"A restauração da ordem", {"Reúna um exército e siga até as Terras Pestilentas Orientais. Lance um ataque maciço contra Nathanos Arauto da Praga e qualquer lixo da Horda que tente protegê-lo.","Mantenha-se sempre alerta, <name>. Os membros da Horda vão defender o lorde patrulheiro com as próprias vidas."}},
[6261] = {"Dungar Tragolongo", {"Leve o Caixote de Larso para Dungar Tragolongo, o mestre de grifos."}},
[6281] = {"Siga para Ventobravo", {"Compre uma passagem de grifo do mestre de grifos Thor, e leve o Bilhete do Ludovico para Larso Norde na loja Imunidade Limitada, que fica na Cidade Velha, em Ventobravo."}},
[6282] = {"Harpias ameaçadoras", {"Maggran Ata Terra, no Retiro Rocha do Sol, quer que você mate 7 Hapias Furissague, 7 Emboscadoras Furissangue, 7 Matadoras Furissangue e 7 Plumerrantes Furissangue."}},
[6283] = {"O sangue das Furissangue", {"Maggram, no Retiro Rocha do Sol, quer que você mate a Estripadora Furissangue e traga os restos mortais dela como prova de seu feito."}},
[6284] = {"Aracnofobia", {"Mate Besseleth e leve a Presa de Besseleth a Maggran no Retiro Rocha do Sol."}},
[6285] = {"Entrega para Ludovico", {"Compre uma passagem para o Morro da Sentinela do mestre de grifos Dungar Tragolongo. Em seguida, leve o Caixote de Larso para Ludovico, no Morro da Sentinela."}},
[6301] = {"Ciclo de renascimento", {"Tammra Campo de Vento, no Retiro Rocha do Sol, quer que você colete 10 Sementes de Gaia."}},
[6321] = {"Suprimentos para o Sepulcro", {"Leve as Ordens do Rodrigo para Carlos Razok."}},
[6322] = {"Miguel Garreta", {"Leve o Caixote do Antônio até Miguel Garreta."}},
[6323] = {"Carona para a Cidade Baixa", {"Adquira uma viagem de morcego até a Cidade Baixa com o tratador de morcegos Carlos Razok. Em seguida, leve as Ordens do Rodrigo para Antônio Nunes, na Cidade Baixa."}},
[6324] = {"Fale novamente com Rodrigo", {"Adquira um voo de morcego para o Sepulcro do tratador de morcegos Miguel Garreta, depois entregue o Caixote do Antônio para o Necroguarda Rodrigo, no Sepulcro."}},
[6341] = {"O butim de Teldrassil", {"Leve a Coleção de Nessa para Vesprystus."}},
[6342] = {"Voo para Auberdine", {"Viaje de hipogrifo para Auberdine com o mestre de hipogrifos Vesprystus, depois leve a Coleção de Nessa para Laird."}},
[6343] = {"Resposta para Nessa", {"Leve a Resposta de Laird para Nessa Cantonegro."}},
[6344] = {"Nessa Cantonegro", {"Fale com Nessa Cantonegro."}},
[6361] = {"Um pacote de peles", {"Leve o Pacote de Peles para o mestre de mantícoras Devrak, na Encruzilhada."}},
[6362] = {"Voo para o Penhasco do Trovão", {"Pegue uma mantícora com o mestre de mantícoras Devrak e leve o Pacote de Peles para Ahanu, no Penhasco do Trovão."}},
[6363] = {"Tal, o Mestre de Mantícoras", {"Leve os Artigos em Couro do Ahanu para Tal no Penhasco do Trovão."}},
[6364] = {"Fale novamente com Jahan", {"Pegue uma mantícora para a Encruzilhada com o mestre de mantícoras Tal e leve os Artigos em Couro do Ahanu para Jahan Asa de Falcão."}},
[6365] = {"Encomenda para Gryshka", {"Leve as Carnes de Zargh para Devrak, na Encruzilhada."}},
[6381] = {"Vida nova", {"Plante 10 Sementes de Gaia em Montículos de Terra de Gaia e, então, devolva as Sementes Encantadas de Gaia restantes a Tammra, no Retiro Rocha do Sol."}},
[6382] = {"A caçada do Vale Gris", {"Fale com Senani Coração Trovejante no Posto Machadada, que fica no Vale Gris."}},
[6383] = {"A Caçada do Vale Gris", nil},
[6384] = {"Carona para Orgrimmar", {"Pegue uma mantícora para a Orgrimmar com o mestre de mantícoras Devrak, depois leve as Carnes de Zargh para Orgrimmar."}},
[6385] = {"Doraso, o mestre de mantícoras", {"Entregue a Carta de Gryshka para Doraso em Orgrimmar."}},
[6386] = {"Retorno à Encruzilhada.", {"Pegue uma mantícora para a Encruzilhada com o mestre de mantícoras Doraso e leve a Carta de Gryshka para Zargh, na Encruzilhada."}},
[6387] = {"Alunos brilhantes", {"Leve a Lista de Brock para Thorgrum Borrelson, em Thelsamar."}},
[6388] = {"Grif Trovino", {"Entregue as Picaretas Honorárias para Grif Trovino em Altaforja."}},
[6389] = {"Praga de cupins!", {"Solte os Cupins das Terras Pestilentas na Serraria de Beiranorte, depois retorne com o Barril de Cupins das Terras Pestilentas até Nataniel Dumah, no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais."}},
[6390] = {"Praga de cupins!", {"Solte os Cupins das Terras Pestilentas na Serraria de Beiranorte, depois retorne com o Barril de Cupins das Terras Pestilentas até Guel Levine, no Baluarte, que fica nas Clareiras de Tirisfal."}},
[6391] = {"Carona para Altaforja", {"Compre um voo de grifo para Altaforja do mestre de grifos Thorgrum Borrelson. Chegando lá, entregue a Lista de Brock para Golnir Topadão."}},
[6392] = {"Fale novamente com Brock", {"Compre um voo de grifo para Thelsamar do mestre de grifos Grif Trovino. Chegando lá, entregue as Picaretas Honorárias para Brock Buscapedra."}},
[6393] = {"Guerra elemental", {"Leve 10 Incenderitas a Tsunomem no Retiro Rocha do Sol."}},
[6394] = {"A picareta de Thazz'ril", {"Leve a Picareta de Thazz'ril para o Encarregado Thazz'ril."}},
[6395] = {"O último desejo de Marla", {"Leve os Restos de Saulo Mitos para o Túmulo de Marla, depois fale novamente com a Noviça Elvira."}},
[6401] = {"Kaya está viva", {"Conte a boa notícia a Tammra Campo de Vento no Retiro Rocha do Sol."}},
[6402] = {"Encontro marcado em Ventobravo", {"Viaje a Ventobravo e vá até os portões da cidade. Fale com o Escudeiro Rui para que ele avise ao Marechal Vilasboas que você chegou."}},
[6403] = {"O grande baile de máscaras", {"Siga Reginaldo Vilasboas através de Ventobravo. Proteja-o de qualquer ataque!"}},
[6421] = {"Ravina da Avalanche", {"Explore as profundezas da caverna na Ravina da Avalanche e colete 10 Cristais de Ressonita para que Mor'rogal, no Retiro Rocha do Sol, os analise."}},
[6441] = {"Chifres de Sátiro", {"Consiga 16 Chifres de Sátiro para Pixel no Posto Machadada."}},
[6442] = {"Nagas na Praia de Zoram", {"Entregue 20 Cabeças de Iracauda a Marukai, na Praia de Zoram."}},
[6461] = {"Fome sangrenta", {"Xen'Zilla, em Malaka'Jin, precisa que você mate 10 Rastejadoras de Fundolimo e 7 Cospeçonhas de Fundolimo."}},
[6462] = {"Patuá troll", {"Entregue 8 Patuás Troll para Mitsuwa, no Assentamento Zoram'gar."}},
[6481] = {"O terrano se ergue", {"Abra o esquife de ressonita com o Cristal de Ressonita Encantado e, então, mate Goggeroc. Fale novamente com Mor'rogal levando as notícias e o Cristal de Ressonita Encantado."}},
[6482] = {"Liberdade para Ruul!", {"Escolte Ruul e, em seguida, fale com Yama Casco de Neve, no Posto Machadada."}},
[6501] = {"O Olho do Dragão", {"Você deve vasculhar o mundo em busca de uma criatura capaz de restaurar o poder do Fragmento do Olho do dragão. A única informação que você tem sobre essa criatura é que ela existe."}},
[6502] = {"Amuleto da Chama Dracônica", {"Confronte o General Drakkisath e consiga o Sangue do Campeão dos Dragões Negros. Drakkisath está na sala do trono atrás dos Salões da Ascensão, no Pico da Rocha Negra."}},
[6503] = {"Vanguardeiros do Vale Gris", {"Mate 9 Vanguardeiros do Vale Gris, depois fale novamente com Koray'bin, no Posto Machadada."}},
[6504] = {"As páginas perdidas", {"Encontre as 12 páginas que voaram do Manual de Operações do Retalhador e organize-as nos capítulos 1, 2 e 3. Devolva o manual a Gurda Marcafúria, que está perto do Posto Machadada."}},
[6521] = {"Uma aliança profana", {"Leve a cabeça do Embaixador Malcin para Varimatras, na Cidade Baixa."}},
[6522] = {"Uma aliança profana", {"Leve o Pergaminho Pequeno para Varimatras, na Cidade Baixa."}},
[6523] = {"Proteja Kaya", {"Escolte Kaya Casco Chato até a Aldeia Apareje e, então, fale novamente com Makaba Casco Chato perto da extremidade sudeste das Torres de Pedra."}},
[6541] = {"Apresente-se a Kadrak", {"Apresente-se a Kadrak na torre de vigia dos Sertões Setentrionais."}},
[6542] = {"Apresente-se a Kadrak", {"Apresente-se a Kadrak na torre de vigia dos Sertões Setentrionais."}},
[6543] = {"Os relatórios dos Brado Guerreiro", {"Abra o Monte de Relatórios e entregue os Relatórios dos Brado Guerreiro ao Batedor do Brado Guerreiro, o Mensageiro do Brado Guerreiro e o Pioneiro do Brado Guerreiro. Em seguida, entregue o informe sobre a situação atual que você receberá de cada um deles para Kadrak, na torre de vigia do norte dos Sertões."}},
[6544] = {"Junte-se aos bons", {"Acompanhe Torek no ataque ao Posto Avançado Asa de Prata e sua comandante, Duriel Flameluna. Em seguida, apresente-se a Ertog Raivatusco no Posto Machadada."}},
[6545] = {"O informe do mensageiro Brado Guerreiro", nil},
[6546] = {"O informe do pioneiro do Brado Guerreiro", nil},
[6547] = {"O informe do batedor Brado Guerreiro", nil},
[6548] = {"Vingue minha vila", {"Mate 8 Rufiões Temível Totem e 6 Mercenários Temível Totem para, então, voltar a falar com Makaba Casco Chato perto da extremidade sudoeste das Torres de Pedra."}},
[6561] = {"Vilania das Profundezas", {"Leve a cabeça do Senhor do Crepúsculo Kelris para Bashana Runa Totem, no Penhasco do Trovão."}},
[6562] = {"Problemas nas Profundezas", {"Fale com Je'neu Sancrea no Vale Gris."}},
[6563] = {"A essência de Aku'mai", {"Entregue 20 Safiras de Aku'Mai a Je'neu Sancrea no Vale Gris."}},
[6564] = {"Lealdade aos Deuses Antigos", {"Leve o Bilhete Úmido para Je'neu Sancrea, no Vale Gris."}},
[6565] = {"Lealdade aos Deuses Antigos", {"Mate Lorgus Jett nas Profundezas Negras e depois fale novamente com Je'neu Sancrea no Vale Gris."}},
[6566] = {"O que o vento carrega", {"Ouça Thrall."}},
[6567] = {"O campeão da Horda", {"Procure Rexxar. O Chefe Guerreiro <o/a> instruiu quanto ao paradeiro dele. Procure nos caminhos de Desolação, entre a Cordilheira das Torres de Pedra e Feralas."}},
[6568] = {"O testamento de Rexxar", {"Leve o Testamento de Rexxar para Myranda, a Bruxa Velha, nas Terras Pestilentas Ocidentais."}},
[6569] = {"Ilusões ópticas", {"Vá para o Pico da Rocha Negra e colete 20 Olhos de Dragomorfo Preto. Fale novamente com Myranda, a Bruxa Velha, depois que tiver terminado a tarefa."}},
[6570] = {"Ardeluta", {"Vá até o Pântano da Serpe, no Pântano Vadeoso, e procure o Covil de Ardeluta. Quando estiver lá, use o Amuleto de Subversão Dracônica e fale com Ardeluta."}},
[6571] = {"Suprimentos do Brado Guerreiro", {"Colete os seguintes Suprimentos do Brado Guerreiro: Lâminas Serrilhadas dos Brado Guerreiro, Corda para Transportar Toras, Óleo dos Brado Guerreiro e Carregamento de Machados dos Brado Guerreiro para Locke Okarr no Posto Machadada."}},
[6581] = {"Lâminas Serrilhadas dos Brado Guerreiro", nil},
[6582] = {"O teste dos crânios: Áugure", {"Encontre e mate o Áugure, o draco da Revoada Dragônica Azul. Arranque a cabeça do draco morto e leve-a para Ardeluta.","Você sabe que o Áugure se encontra em Hibérnia."}},
[6583] = {"O teste dos crânios: Somnus", {"Destrua Somnus, o draco campeão da Revoada Verde. Pegue seu crânio e leve-o para Ardeluta."}},
[6584] = {"O teste dos crânios: Chronalis", {"Guardando as Cavernas do Tempo no Deserto de Tanaris, encontra-se Chronalis, filho de Nozdormu. Destrua-o e leve seu crânio para Ardeluta."}},
[6585] = {"O teste dos crânios: Axtroz", {"Vá para Grim Batol e encontre Axtroz, o draco campeão da Revoada Vermelha. Destrua-o e arranque sua cabeça. Leve o crânio para Ardeluta."}},
[6601] = {"Ascensão...", {"Pelo jeito, a farsa chegou ao fim. Você já sabe que o Amuleto de Subversão Dracônica criado por Myranda, a Bruxa Velha, não vai funcionar no Pico da Rocha Negra. Você devia ir atrás de Rexxar e explicar a situação. Mostre a ele o Amuleto da Chama Dracônica Fraco. Pode ser que ele saiba o que fazer."}},
[6602] = {"Sangue do Campeão dos Dragões Negros", {"Viaje para o Pico da Rocha Negra e mate o General Drakkisath. Recolha seu sangue e leve-o para Rexxar."}},
[6603] = {"Problema em Hibérnia!", {"Encontre Bruna Covaneve perto das fontes termais de Hibérnia."}},
[6604] = {"Coruscante enfurecido", {"Fale com Jaron Moldapedra na Aldeia Chuva Estelar."}},
[6605] = {"Sujeitinha esquisita", {"Fale com Winna Perícon na Selva Maleva."}},
[6606] = {"Um pouquinho de sorte", {"Fale com a Mandingueira Mau'ari em Visteterna."}},
[6607] = {"Nat Pagle, o pescador supremo", {"Nat Pagle quer que você pesque os seguintes peixes:","O Graçapé-brumalga, na Praia Brumalga, que fica no Pântano das Mágoas.","A Piranha-de-sar'theris, na Praia de Sar'theris, que fica na Desolação.","O Atum-amarelo-de-feralas, no Rio Verdantis, em Feralas.","A Molinésia-azul-da-costa-selvagem, na Costa Selvagem, na Selva do Espinhaço.","Fale novamente com Nat Pagle quando já tiver fisgado todos!"}},
[6608] = {"Você bom demais", {"Você acha que Lumak está tentando dizer que ele já não pode mais ajudar a melhorar suas habilidades de pesca. Você pode tentar encontrar o amigo dele, Nat Pagle, um humano pescador, que fica em uma das ilhas ao sul de Theramore (pelo menos é isso o que você acha que Lumak disse)."}},
[6609] = {"Eu não sei nada!", {"Vá até a cidade costeira de Theramore, em Kalimdor. A partir de lá, procure uma ilha a sul da capital. Nat Pagle <o/a> aguarda!"}},
[6610] = {"Escondidinho Marisquete", {"Funesto Marrão quer que você consiga os seguintes itens:","12 Ovos Gigantescos. Você os encontra nas rocas que vagueiam por Tanaris ou em qualquer pássaro grande.","10 fatias de Carne de Marisco Condimentada. Você as encontra dentro dos mariscos, óbvio!","20 pedaços de Queijo Furado de Alterac. Procure alguém que venda esse queijo!","Consiga todos os itens e fale novamente com Funesto."}},
[6611] = {"Para Geringontzan!", {"Vá para Geringontzan, no Deserto de Tanaris, e fale com Funesto Marrão para progredir na carreira de <cozinheiro/cozinheira>."}},
[6612] = {"Eu sei de um cara...", {"Vá para Geringontzan, no deserto de Tanaris, e fale com Funesto Marrão sobre como se desenvolver na carreira de <cozinheiro/cozinheira>."}},
