[8556] = {"Sinete da Força Obstinada", {"Leve 1 Anel Magistral Qiraji, 2 Ídolos Bruxuleantes, 5 Escaravelhos de Bronze e 5 Escaravelhos de Marfim para a Arauto do Vento Yessendra em Silithus. Também é necessário ser <Honrado/Honrada> pelo Círculo Cenariano para concluir a missão."}},
[8557] = {"Clâmide da Força Obstinada", {"Leve 1 Clâmide Marcial Qiraji, 2 Ídolos Ônix, 5 Escaravelhos de Prata e 5 Escaravelhos de Osso para Keyl Garralesta em Silithus. Também é necessário ser <Reverenciado/Reverenciada> pelo Círculo Cenariano para concluir a missão."}},
[8558] = {"Foice da Força Obstinada", {"Leve 1 Punho de Pontas Qiraji, 2 Ídolos de Alabastro, 5 Escaravelhos de Cristal e 5 Escaravelhos de Pedra para o Guardião Haro em Silithus. Também é necessário ser <Exaltado/Exaltada> pelo Círculo Cenariano para concluir a missão."}},
[8559] = {"Grevas do Conquistador", {"Leve os Braceletes do Comando Qiraji, 2 Ídolos de Guerra, 5 Escaravelhos de Marfim e 5 Escaravelhos de Ouro para Kandrostrasz em Ahn'Qiraj. Também é necessário ser <Tolerado/Tolerada> pela Prole de Nozdormu para concluir a missão."}},
