[8734] = {"Tyrande e Remulos", {"Vá à Clareira da Lua e fale com o Guardião Remulos."}},
[8735] = {"A Corrupção do Pesadelo", {"Vá até os quatro portais do Sonho Esmeralda em Azeroth e obtenha um Fragmento da Corrupção do Pesadelo de cada um. Quando tiver cumprido essa tarefa, fale novamente com o Guardião Remulos na Clareira da Lua."}},
[8736] = {"Os pesadelos manifestos", {"Defenda o Refúgio Noturno de Erânicos. Impeça que o Guardião Remulos pereça. Não mate Erânicos. Defenda-se. Espere Tyrande."}},
