[2201] = {"Время собирать камни", {"Найдите рубин, сапфир, и топаз, которые спрятаны в разных местах Ульдамана. Когда найдете, свяжитесь с Талвашем де Кисселем, используя фиал Видения, который он предоставил.","Из дневника вы узнали следующее:","*Рубин укрыт в забаррикадированной Кузне Теней.","*Топаз спрятан в урне в одной из областей, занятых троггами, недалеко от расположения дворфов Альянса.","*Сапфир присвоил себе Гримлок, лидер троггов."}},
[2202] = {"В Ульдаман за реактивом", {"Принести 12 грибов-малиновиков Джаркалу Замшелому Клыку в Каргат."}},
[2203] = {"Пробежка по Бесплодным землям-2", {"Наполните пустые волшебные сосуды кровью опаленных драконов-стражей в Бесплодных землях и принесите их Джаркалу Замшелому Клыку в Каргат."}},
[2204] = {"Восстановление ожерелье", {"Добудьте источник энергии из самого мощного волшебного создания, которое найдете в Ульдамане и доставьте его Талвашу де Кисселю в Стальгорн."}},
[2205] = {"Выход на ШРУ", {"Доставьте пакет от Керина Сильвиуса мастеру Матиасу Шоу в казармы Штормграда."}},
[2206] = {"Держи-хватай!", {"Найдите портового начальника из Братства Справедливости и добудьте расписание поставок для мастера Матиаса Шоу."}},
[2218] = {"Путь к спасению", {"Поговорите с Хальфданом Чернобородом в Стальгорне."}},
[2238] = {"Простая уловка", {"Найдите Онина Макмолота в Гномрегане."}},
[2239] = {"Донесение Онина", {"Доставьте донесение Онина Хальфдану в Стальгорн."}},
[2240] = {"Потайной чертог", {"Прочитайте журнал Бейлога, исследуйте, потайной чертог и потом доложите об увиденном геологу Грозовой Вершине."}},
[2241] = {"Катись, яблочко...", {"Отнесите розу Джаннока Сиарне в Дарнасс."}},
[2242] = {"Зов судьбы", {"Найдите Сетира Древнего и принесите все, что сумеете найти, Сиарне."}},
[2258] = {"Пробежка по Бесплодным землям", {"Принесите 5 вторых желудков канюка, 10 клыков скального койота и 5 осколков каменных элементалей Джаркалу Смешанному Лишайнику в Каргат."}},
[2259] = {"Эрион Шепот Тени", {"Встретьтесь в Дарнассе с Эрионом Шепотом Тени."}},
[2260] = {"Директива Эриона", {"Отправляйтесь в Штормград и посоветуйтесь с Рензиком Ножом."}},
[2278] = {"Платиновые Диски", {"Поговорите с каменным Стражем и запомните все, что он передаст вам. Когда он закончит свой пересказ событий древности, активируйте диски Норганнона."}},
[2279] = {"Платиновые Диски", {"Отнесите миниатюрную копию дисков Норганнона в Лигу Исследователей в Стальгорне."}},
[2280] = {"Платиновые Диски", {"Отнесите миниатюрную копию дисков Норганнона кому-то из мудрецов с Громового Утеса."}},
[2281] = {"Встречи в Красногорье", {"Пойдите к Приозерью в Красногорье и поговорите с Люцием."}},
[2282] = {"Лесопилка Альтера", {"Откройте сундук Люция, достаньте Руну Воровства и вернитесь к Люцию в Приозерье."}},
[2283] = {"Пропавшее ожерелье", {"Найдите на раскопках Ульдамана драгоценное ожерелье (возможно, поврежденное) и принесите его в Оргриммар Драну Дрофферсу."}},
[2284] = {"Пропавшее ожерелье, этап 2", {"Разыщите ключ к местонахождению самоцветов в глубинах Ульдамана."}},
[2298] = {"Королевская облава", {"Отправляйтесь в Штормград и найдите Рензика Ножа."}},
[2299] = {"К Хальфдану!", {"Отправляйтесь в Стальгорн и поговорите с Хальфданом Чернобородом."}},
[2300] = {"ШРУ", {"Отправляйтесь в Штормград и разыщите Рензика Ножа."}},
[2318] = {"Трудности перевода", {"Найдите кого-нибудь, кто сможет перевести для вас дневник паладина. Ближайший к вам такой умелец, скорее всего, найдется в Каргате, форпосте в Бесплодных Землях."}},
[2338] = {"Трудности перевода", {"Одолжите ожерелье Джаркалу. Взамен он переведет дневник."}},
[2339] = {"Найти самоцветы и источник энергии", {"Добудьте все три самоцвета и источник энергии для ожерелья из Ульдамана и принесите их Джаркалу Замшелому Клыку в Каргат. Джаркал считает, что источник энергии можно найти у самой сильной твари в Ульдамане.","Из дневника следует...","* Рубин спрятан на огражденной завалами территории Кузни Теней.","*Топаз спрятан в урне на территорях троггов под боком у дворфов Альянса.","*Сапфир забрал Гримлок, старейшина троггов."}},
[2340] = {"Принести самоцветы", {"Доставьте ожерелье и драгоценности Драну Дрофферсу в Оргриммар."}},
[2341] = {"Пропавшее ожерелье, этап 3", {"Навестите Джаркала Замшелого Клыка в Каргате."}},
[2342] = {"Возвращенные сокровища", {"Принесите Патрику Гарретту в Подгород фамильное сокровище из сундука в Южном зале Ульдамана."}},
[2358] = {"Рога Незры", {"Верните рога Незры Арантиру в Приозерье."}},
[2359] = {"Башня Клейвена", {"Добудьте ключ Башни Братства Справедливости, проберитесь в башню и выясните, каково содержимое сундука из Сумеречного леса. Отнесите найденные вещи Матиасу Шоу в Штормград. Почитайте дневник Кеарнен, чтобы больше узнать о башне."}},
[2360] = {"Матиас и Братство Справедливости", {"Найдите посланницу Кеарнен в Западном Крае и поговорите с ней."}},
[2361] = {"Восстановление ожерелья", nil},
[2378] = {"Поиски Изувеченной Длани", {"Отправляйтесь в Оргриммар и поговорите с Шентулом в Расселине Теней."}},
[2379] = {"Зандо'зан", {"Поговорите с Зандо'заном в расселине Теней."}},
[2380] = {"В Оргриммар!", {"Поговорите с Шентулом в расселине Теней в Оргриммаре."}},
[2381] = {"Грабь награбленное", {"Принесите Рениксу Презренному из Кабестана сокровище Южного моря. Не забудьте взять из гизмотронного аппарата Реникса ХОЧУ и отмычки. Они понадобятся для выполнения задания.","Если на вас нападут необычайно враждебные попугаи, воспользуйтесь ХОЧУ!"}},
[2382] = {"Реникс из Кабестана", {"Поговорите с Рениксом Презренным в Кабестане."}},
[2383] = {"Записка на пергаменте", {"Прочтите пергамент и поговорите с Франгом в долине Испытаний."}},
[2398] = {"Потерянные дворфы", {"Найдите Бейлога в Ульдамане."}},
[2399] = {"Росток папоротника", nil},
[2418] = {"Камни Силы", {"Принесите 8 дентриевых камней Силы и 8 энелиевых камней Силы Ригглфаззу в Бесплодные земли."}},
[2438] = {"Изумрудный ловец снов", {"Принесите изумрудного ловца снов Таллонкаю Гибкому Корню в Доланаар."}},
[2439] = {"Платиновые диски", {"Отнесите наградной сертификат Дините Каменной Мантии в хранилище Стальгорна."}},
[2440] = {"Платиновые диски", {"Отнесите наградной сертификат Бене Заиндевевшее Копыто в Громовой Утес."}},
[2458] = {"Надежное прикрытие", {"Отыщите башню Торговой компании к северу от Нефтяного болота и вступите в контакт с тайным агентом Изувеченной Длани, надсмотрщиком Физзулом.","Возле башни выстрелите из сигнального ружья ДВА раза, потом подойдите к надсмотрщику Физзулу и продемонстрируйте приветствие Изувеченной Длани (команда /честь).","Все нужно делать строго по порядку – Физзул убивает всех, кто кажется ему подозрительным."}},
[2459] = {"Фероситас Пожиратель Снов", {"Таллонкай Гибкий Корень из Доланаара хочет, чтобы вы убили 7 мистиков из племени Кривой Сосны и нашли пропавший самоцвет."}},
[2460] = {"Приветствие Изувеченной Длани", {"Повторите приветствие Раздробленной Руки вслед за Шентулом."}},
[2478] = {"Миссия: выполнима, но с трудом", {"Украдите ключ от башни у штейгера Силиксиза, убейте двух мертвяков-мутантов Торговой компании, двух патрульных Торговой компании и двух смотрителей Торговой компании.","Ликвидируйте старшего мастера Галливикса, взломайте замок на его ящике и украдите запас измененной смеси Занзила. Отнесите запас смеси и голову Галливикса Шентулу в Оргриммар и получите следующие указания."}},
[2479] = {"Помощь Хинотта", {"Пойдите на Мельницу Таррен в предгорьях Хилсбрада и доставьте образчик смеси Занзила Сержу Хинотту.","Сядьте на дирижабль, идущий до Подгорода, и летите вдоль дороги на юг, через Серебряный бор в сторону Хилсбрада. Следите за знаками."}},
[2480] = {"Помощь Хинотта", {"Дождитесь, пока Серж Хинотт доделает лекарство."}},
[2498] = {"Возвращение к Деналану", {"Релиан Зеленый Костер хочет, чтобы вы поговорили с Деналаном у озера Аль'Амет."}},
[2499] = {"Дубохмур", {"Деналан с озера Аль'Амет хочет, чтобы вы сняли гигантскую опухоль с Дубохмура."}},
[2500] = {"За реактивами в Бесплодные земли", {"Добудьте в Бесплодных землях реактивы для Гхака Целителя и отнесите их ему в Телcамар."}},
[2501] = {"За реактивами в Бесплодные земли II", {"Наполните пустые волшебные сосуды кровью опаленных драконов-стражей в Бесплодных землях и принесите их Гхаку Целителю в Телcамар."}},
[2518] = {"Слезы Луны", {"Жрица А'моора из дарнасского храма Луны хочет, чтобы вы принесли ей серебряные паутинные железы леди Сатры."}},
[2519] = {"Храм Луны", {"Сестра Аквинна хочет, чтобы вы поговорили со жрицей А'моорой из Храма Луны."}},
[2520] = {"Жертвоприношение Сатры", {"Жрица А'моора просит вас положить серебряные паутинные железы леди Сатры у фонтана в храме и затем вернуться к ней."}},
[2521] = {"Служба для Кум'иша", {"Если в своем путешествии по Выжженным землям вы найдете безупречную сферу из дренейского аметиста, отнесите ее Кум'ише Коллекционеру.","Будьте внимательны – этот камень – один из самых редких кристаллов во всем Азероте. Любое существо в этих землях может владеть безупречной сферой из дренейского аметиста.","Вы будете вознаграждены за каждую безупречную сферу из дренейского аметиста, которую доставите Кум-ише."}},
[2522] = {"Попытки Кум'иша", {"Найдите безупречную сферу из дренейского аметиста и верните ее Кум'ише Коллекционеру."}},
[2523] = {"Оскверненный песнецвет", nil},
[2541] = {"Спящий друид", {"Принесите шаманский оберег вуду Обену Яростному Когтю в обитель Бен'этиль."}},
[2561] = {"Друид-медведь", {"Обен Яростный Коготь просит, чтобы вы уничтожили его лишенное души тело, а затем воспользовались оберегом вуду."}},
[2581] = {"Челюсти гиены-хохотуна", {"Принесите три челюсти гиены, два обожженных легких вепря и одну клешню скорпока магу Крови Дразиалу."}},
[2582] = {"Вековая ярость", {"Принесите три челюсти гиены, два обожженных легких вепря и одну клешню скорпока магу Крови Дразиалу."}},
[2583] = {"Жизненная сила вепря", {"Принесите три обожженных легких вепря, две клешни скорпока и один мозг василиска волшебнице крови Дразиале."}},
[2584] = {"Дух вепря", {"Принесите три обожженных легких вепря, две клешни скорпока и один мозг василиска магу Крови Дразиалу."}},
[2585] = {"Решительный удар", {"Принесите три клешни скорпока, две единицы потрохов стервятника и одно обожженное легкое вепря волшебнице крови Дразиале."}},
[2586] = {"Скорпоковая соль", {"Принесите три клешни скорпока, две меры потрохов стервятника и одно обожженное легкое вепря волшебнице крови Дразиале."}},
[2601] = {"Укус василиска", {"Принесите десять мозгов василиска и потроха двух стервятников волшебнице крови Линоре."}},
[2602] = {"Непогрешимый разум", {"Принесите десять мозгов василиска и потроха двух стервятников волшебнице крови Линоре."}},
[2603] = {"Стойкость стервятника", {"Принесите потроха десяти стервятников и две челюсти гиены волшебнице крови Линоре."}},
[2604] = {"Духовное господство", {"Принесите потроха десяти стервятников и две челюсти гиены волшебнице крови Линоре."}},
[2605] = {"Жаждущий гоблин", {"Добудьте насыщенную влагой железу и принесите ее Синю Гогельмогелю в Прибамбасск."}},
[2606] = {"Хороший вкус", {"Поговорите с Поливалкой в Прибамбасске по поручению Синя Гогельмогеля."}},
[2607] = {"Прикосновение Занзила", {"Поговорите с Доком Миксиль-Пиксилем в подвале казарм."}},
[2608] = {"Прикосновение Занзила", {"Лягте на землю (действие /lay) для осмотра Доком Миксиль-Пиксилем – он не сможет подобрать лекарство, пока не найдет причину болезни."}},
[2609] = {"Прикосновение Занзила", {"Принесите Доку Миксиль-Пиксилю один букетик простых полевых цветов, одну освинцованную колбу, одну бронзовую трубку и одну катушку светлого зеленовато-желтого шелка. Чтотит, знаете ли, сам собой не проходит."}},
[2621] = {"Опозоренный", {"Поговорите с командиром разведотряда Руагом в Каменоре."}},
[2622] = {"Пропавшие приказы", {"Поговорите с Бенгором."}},
[2623] = {"Болотный говорун", {"Заберите приказы Вождя и верните их павшему герою Орды."}},
[2641] = {"Маленький секрет Поливалки", {"Найдите фиалковый траган и вернитесь к Поливалке в Прибамбасск."}},
[2661] = {"Посылка для Синя", {"Отнесите фиалковый порошок Синю Гогельмогелю в Прибамбасск."}},
[2662] = {"Эликсир Гогельмогеля", {"Синь Гогельмогель хочет поговорить с вами после завершения работы над эликсиром."}},
[2681] = {"Камни, что связывают нас", {"Освободите девять Слуг Разелиха, трех Слуг Севины, трех Слуг Аллистария и трех Слуг Грола. Когда ваша задача будет выполнена, возвращайтесь к павшему герою. Вы должны оставаться поблизости от камней, иначе процесс не будет завершен."}},
[2701] = {"Герои древних времен", {"Откройте шкатулку и возьмите награду."}},
[2702] = {"Герои древних времен", {"Поговорите с капралом Тундом Треснувшее Копыто."}},
[2721] = {"Кирит", {"Узнайте, что стало с лейтенантом Киритом."}},
[2741] = {"Супер Яйц-О-Матик", nil},
[2742] = {"Рин'джи в ловушке", {"Выведите Рин'джи из Сторожки Кель'Данил, а потом найдите то, что он спрятал в Отвесных скалах, на востоке."}},
[2743] = {"Покров Тьмы", {"Вернитесь к павшему герою Орды со своими находками."}},
[2744] = {"Охотник на демонов", {"Поговорите в Азшаре с Лорамусом Многоногом."}},
[2745] = {"Проникновение в крепость", {"Поговорите с Тирионом в Крепости Штормграда."}},
[2746] = {"Все по порядку", {"Принесите Тириону в Штормград 3 отреза шелка и 2 свежих яблока Клары."}},
[2747] = {"Необычное яйцо", nil},
[2748] = {"Хорошее яйцо", nil},
[2749] = {"Обычное яйцо", nil},
[2750] = {"Плохое яйцо", nil},
[2751] = {"Варварские доспехи", {"Принесите 2 узорчатых бронзовых боевых браслета, 2 бронзовых больших меча и 2 острых когтя Орокку Омошу в Оргриммар."}},
[2752] = {"За железными наплечниками", {"Принесите Орокку Омошу в Оргриммар 4 бронзовых боевых топора и 4 бронзовых боевых молота."}},
[2753] = {"Тяжелая поступь", {"Принесите 4 зеленых железных шлема, 4 пары зеленых железных наручей и 2 пары зеленых железных поножей Орокку Омошу в Оргриммар."}},
[2754] = {"Рога безумия", {"Принесите Орокку Омошу в Оргриммар 2 твердых железных кувалды, 2 пары посеребренных бронзовых сапог и 2 пары посеребренных бронзовых рукавиц."}},
[2755] = {"Радости Омоша", {"Научитесь танцу Радости Омошей."}},
[2756] = {"Старые обычаи", {"Принесите 4 стальные кирасы и 4 стальных латных шлема Атурку Наковальне в Оргриммар."}},
[2757] = {"Вперед, в Пиратскую бухту!", {"Поговорите с Макгеваном в Пиратской бухте."}},
[2758] = {"Происхождение кузнечного искусства", {"Принесите 6 пар золотых чешуйчатых наручей Ханку Молоту в Штормград."}},
