[3904] = {"Урожай Милли", {"Принесите 8 корзин с виноградом Милли Осворт в Аббатство Североземья."}},
[3905] = {"Уведомление о поставке винограда", {"Отнесите уведомление о поставке винограда брату Нилзу в Аббатство Североземья."}},
[3906] = {"Дисгармония пламени", {"Отправьтесь в карьер у Черной горы и уничтожьте Подчинителя Пирона. По выполнению вернитесь к Громосерду."}},
[3907] = {"Дисгармония пламени", {"Ступайте в глубины Черной горы и выследите лорда Опалителя. Убейте его и принесите Громосерду все, что может дать какую-то информацию."}},
[3908] = {"Тайна, покрытая мраком", {"Отнесите меч Линкена Донове Снегоноре."}},
[3909] = {"Эликсир Видере", {"Найдите Грегана Пивоплюя на севере Фераласа. Выясните у него, как получить эликсир Видере, и вернитесь к Донове Снегоноре."}},
[3912] = {"Встречаемся на кладбище", {"Отправляйтесь на кладбище на окраине Прибамбасска и выпейте там эликсир Видере. Далее, следуя инструкциям Доновы, найдите Гаэриана."}},
[3913] = {"Замогильная история", {"Воскресните, найдите могильный камень, о котором говорил Гаэриан."}},
[3914] = {"Меч Линкена", {"Доставьте превосходный меч Линкена Линкену в Кратер Ун'Горо."}},
[3921] = {"Венаки Бенике", {"Отнесите сломанный самофланж Венаки Бенике."}},
[3922] = {"Металлические заготовки", {"Принесите 15 металлических заготовок для деталей Венаки Бенике в Степи."}},
[3923] = {"Рилли Грязегоб", {"Отнесите разбитый и сломанный самофланж Рилли Грязегобу в Оргриммар."}},
[3924] = {"Руководство по самофланжу", {"Отнесите руководство по самофланжу Рилли Грязегобу в Оргриммар."}},
[3941] = {"Помощь гномов", {"Найдите Джей Ди Колли в Укрытии Маршалла."}},
[3942] = {"Память Линкена", {"Найдите Эридану Грусть Ветра на юге Оскверненного леса."}},
[3961] = {"Приключения Линкена", {"Отнесите серебряный тотем Аквамонтоса Линкену в Укрытие Маршалла."}},
[3962] = {"Один в поле не воин", {"Отправляйтесь к Вулкану Огненного Венца, к югу от Укрытия Маршалла.","Найдите Яропламеня и разрушьте его защитную ауру с помощью тотема. Когда дух будет сражен, заберите из пещеры Золотое пламя."}},
[3981] = {"Командир Гор'шак", {"Найдите командира Гор'шака в Глубинах Черной горы.","Судя по рисунку в записке, искать следует в темнице где-то вроде того."}},
[3982] = {"Что происходит?", {"Защитите Горшака."}},
[4001] = {"Что происходит?", {"Поговорите с дворфом по имени Каран Могучий Молот и соберите информацию о похищении принцессы Мойры Бронзобород. Передайте информацию Траллу в Оргриммар.","Карана держат в темнице напротив."}},
[4002] = {"Восточные королевства", {"Поговорите с Траллом."}},
[4003] = {"Спасение принцессы", {"Убейте императора Даграна Тауриссана и освободите принцессу Мойру Бронзобород от его черного заклятия."}},
[4004] = {"Спасенная принцесса", {"Вернитесь к Траллу."}},
[4005] = {"Аквамонтос", {"Отнесите книгу Аквора, серебристые когти и сердце Железнолесья в Танарис.","Воспользуйтесь книгой для призыва Аквамонтоса.","По получении тотема, отнести его Джей Ди Колли в Укрытие Маршалла."}},
[4021] = {"Контратака!", {"Принесите кусок знамени Кром'зара Регтару Вратам Смерти. Его можно найти на западе от Перекрестка."}},
[4022] = {"Вкус пламени", {"Покажите Цирусу Раскаивателю сброшенную кожу черного дракона, полученную от Каларана Ветрореза."}},
[4023] = {"Вкус пламени", {"Покажите Цирусу Раскаивателю доказательство своих деяний.","Возникает чувство, что Цирус заранее сомневается в ваших качествах."}},
[4024] = {"Вкус пламени", {"Отправьтесь в глубины Черной горы и убейте Бейл'Гора.","Известно лишь то, что великан живет в глубинах. Воспользуйтесь измененной сброшенной шкурой черного дракона и возьмите из останков Бейл'Гора сущность Огня.","Отнесите пойманную сущность Огня Цирусу Раскаивателю."}},
[4041] = {"Эликсир Видере", nil},
[4061] = {"Восстание машин", {"Отправляйтесь в Пылающие степи и соберите 10 осколков элементалей для верховной жрицы Теодоры Мальвадании.","Теодора упоминала, что осколки можно найти у элементалей и големов."}},
[4062] = {"Восстание машин", {"Отнесите образец осколка элементаля Лотвилю Вериатусу.","Теодора упоминала, что его лагерь находится на востоке."}},
[4063] = {"Восстание машин", {"Найдите и убейте повелителя големов Аргелмаха. Принесите его голову Лотвилу. Также соберите 10 невредимых ядер стихий с беспощадных големов и созданий-завоевателей, охраняющих Аргелмаха. Информация передана посредством телепатии."}},
[4081] = {"УНИЧТОЖИТЬ НА МЕСТЕ: Дворфы Черного Железа", {"Отправляйтесь в Глубины Черной горы и уничтожьте подлых агрессоров!","По приказу полководца Клинозуба уничтожьте 15 охранников, 10 надсмотрщиков и 5 пехотинцев из клана Ярости Горна. Вернитесь сразу по выполнении задания."}},
[4082] = {"УНИЧТОЖИТЬ НА МЕСТЕ: Высокопоставленные чины Черного Железа", {"Отправляйтесь в Глубины Черной горы и уничтожьте подлых агрессоров!","По приказу полководца Клинозуба уничтожьте 10 медиков, 10 солдат и 10 офицеров из клана Ярости Горна. Вернитесь сразу по выполнении задания."}},
[4083] = {"Призрачный кубок", nil},
