[4120] = {"Сила порчи", {"Убейте 12 гризли злобных когтей и 12 опустошителей-сквернолапов в Оскверненном лесу по просьбе Тало Тернового Копыта из лагеря Мохаче."}},
[4121] = {"Опасное положение", {"Отконвоируйте пленника, Грарка Лорктуба, через Пылающие степи и Черную гору к Тлеющему ущелью.","Лекслорт что-то говорил насчет того, что по другую сторону будут ждать его ребята, которые уведут Грарка в тюрьму.","Также отдайте Лекслорту ториевые оковы."}},
[4122] = {"Грарк Лоркруб", {"Отправьтесь в Пылающие степи и найдите Грарка Лоркруба. Лекслорт упоминал, что в последний раз предателя видели в крепости Черной горы.","По обнаружении Грарка Лоркруба закуйте его в ториевые оковы и затем доставьте его от Черной горы к Тлеющему ущелью, Лекслорт и его подчиненные займутся допросом преступника."}},
[4123] = {"Сердце горы", {"Принесите сердце горы Максворту Суперблеску в Пылающие степи."}},
[4124] = {"Пропавший курьер", {"Поговорите с Гинро Пылающим Очагом в Крепости Оперенной Луны."}},
[4125] = {"Пропавший курьер", {"Отыщите лодку курьера, которая должна находиться где-то на побережье Фераласа."}},
[4126] = {"Харли Пьянодых", {"Найдите украденный рецепт громоварского и верните его Рагнару Громовару в Каранос."}},
[4127] = {"Разбитая лодка", {"Вернитесь в Крепость Оперенной Луны и покажите Гинро Пылающему Очагу выброшенный нож, который вы нашли."}},
[4128] = {"Рагнар Громовар", {"Переговорите с Рагнаром Громоваром."}},
[4129] = {"Тайна ножа", {"Отнесите выброшенный нож к друиду по имени Квинтис Ночной Костер в Крепости Оперенной Луны."}},
[4130] = {"Сверхзнание", {"Поговорите с Гинро Пылающим Очагом в Крепости Оперенной Луны."}},
[4131] = {"Гноллы из стаи Древолапов", {"Обыщите лагеря Древолапов в южном Фераласе в поисках вещей курьера либо самого курьера."}},
[4132] = {"Операция:смерть Кузне Гнева", {"Отправляйтесь в Глубины Черной горы и убейте генерала Кузню Гнева. Вернитесь к полководцу Клинозубу по выполнении задания."}},
[4133] = {"Вивиана Лягроб", {"Переговорите с тенемагом Вивиан Лаграв в Каргате."}},
[4134] = {"Украденный рецепт громоварского", {"Принесите рецепт громоварского светлого Вивиан Лягроб в Каргат."}},
[4135] = {"Гудящая Бездна", {"Продолжайте поиски Раскала (или его останков) в Гудящей Бездне. Согласно записке, Гудящая Бездна находится к югу от лагерей стаи Древолапов."}},
[4136] = {"Риббли Крутипроб", {"Принесите голову Риббли Юке Крутипроб в Пылающие степи."}},
[4141] = {"Майджин и Ларион", {"Соберите 15 кровоцветов и принесите их Майджину."}},
[4142] = {"Визит к Грегану", {"Отнесите кровоцвет Грегану Пивоплюю в Фералас."}},
[4143] = {"Туман зла", {"Соберите 5 образцов тумана Аталаи и принесите их Майджину в Кратер Ун'Горо."}},
[4144] = {"Побеги кровоцвета", nil},
[4145] = {"Ларион и Майджин", {"Уничтожьте 5 кровоцветов-хлестунов, 5 кровоцветов-крепкозубов, 5 кровоцветов-живодеров и 5 кровоцветов-звероловов по просьбе Лариона из Укрытия Маршалла."}},
[4146] = {"Питание для шокера", {"Доставьте незаряженный шокер и 5 образцов тумана Аталаи Лариону в Укрытие Маршалла."}},
[4147] = {"Мастерская Марвона", {"Отправьтесь к Марвону Клепальщику в его мастерскую в Кабестане."}},
[4148] = {"Антикровоцветный шокер", nil},
[4161] = {"Рецепт кальдораев", {"Принесите 7 маленьких паучьих лапок Заррину в Доланаар."}},
[4181] = {"Гоблинское инженерное дело", {"Если вы хотите начать обучение гоблинскому инженерному делу, отнесите cправочник по инженерным дисциплинам Никсу Шепружину в Прибамбасск."}},
[4182] = {"Драконья угроза", {"Убейте 15 детенышей черного дракона, 10 черных драконидов, 4 черных драконид и 1 черного дракона. По выполнении задания вернитесь к Хелендису Речному Мысу."}},
[4183] = {"Подлинные хозяева", {"Отправьтесь в Приозерье и доставьте письмо Хелендиса Речного Мыса мировому судье Соломону."}},
[4184] = {"Подлинные хозяева", {"Передайте обращение Соломона верховному лорду Болвару Фордрагону.","Болвар Фордрагон пребывает в Крепости Штормграда."}},
[4185] = {"Подлинные хозяева", {"Поговорите с Верховным лордом Болваром Фордрагоном после беседы с леди Катраной Престор."}},
[4186] = {"Подлинные хозяева", {"Отнесите указ Болвара мировому судье Соломону в Приозерье."}},
[4201] = {"Приворотное зелье", {"Принесите 4 листа крови Грома, 10 огромных серебряных слитков и наполненный сосуд Нагмары госпоже Нагмаре в Глубины Черной горы."}},
[4221] = {"Оскверненный ветроцвет", nil},
[4222] = {"Оскверненный ветроцвет", nil},
[4223] = {"Подлинные хозяева", {"Поговорите с маршалом Максвеллом в Пылающих степях."}},
[4224] = {"Подлинные хозяева", {"Поговорите с Джоном-Оборванцем и узнайте о судьбе маршала Виндзора. По выполнении задания вернитесь к маршалу Максвеллу.","Кажется, маршал Максвелл говорил, что Джона следует искать в пещере на севере."}},
[4241] = {"Маршал Винздор", {"Отправляйтесь на Черную гору на северо-западе и войдите в ее глубины. Узнайте, что сталось с маршалом Виндзором.","Кажется, Джон-Оборванец говорил о том, что Виндзора утащили в темницу."}},
[4242] = {"Утраченная надежда", {"Сообщите маршалу Максвеллу плохие новости."}},
[4243] = {"В поисках Чи-Та 3", {"Найдите Чи-Та 3 в Кратере Ун'Горо."}},
[4244] = {"В поисках Чи-Та 3", {"Найдите мифриловую обшивку и вернитесь к Чи-Та 3 в Кратер Ун'Горо."}},
[4245] = {"В поисках Чи-Та 3", {"Доставьте Чи-Та 3 назад Карне Сомнамбулер в Кратер Ун'Горо."}},
[4261] = {"Древний дух", {"Сопроводите Арая в безопасное место, затем поговорите с Кайнетом Штилем в Лесной Песне на востоке Ясеневого леса."}},
[4262] = {"Подчинитель Пирон", {"Убейте подчинителя Пирона и вернитесь к Джалинде Тирлипуньке.","Кажется, Джалинда говорила, что Пирон охраняет каменоломню. Может, поискать его там?"}},
[4263] = {"Опалитель!", {"Отыщите и уничтожьте лорда Опалителя в глубинах Черной горы!"}},
[4264] = {"Измятая записка", {"Не исключено, что ваша находка заинтересует маршала Виндзора. Может быть, все не так плохо, и надежда еще жива."}},
[4265] = {"Освобожденный из улья", {"Освободите Раскала из улья Цуккаш.","Вернитесь к Гинро Пылающему Очагу в Крепости Оперенной Луны и сообщите ему, что Раскал жив и здоров."}},
[4266] = {"Благодарность за отвагу", {"Поговорите с Шандрисой Оперенной Луной в Крепости Оперенной Луны."}},
[4267] = {"Появление силитидов", {"Отнесите донесение Раскала Грацине Силе Духа в Дарнассе."}},
[4281] = {"Посылка в Таланаар", {"Отнесите недоставленную посылку Хранителю дорог Фалфиндеру в Таланаар."}},
[4282] = {"Проблеск надежды", {"Верните маршалу Виндзору утерянную информацию.","Маршал уверен, что информацией завладели повелитель големов Аргелмах и генерал Кузня Гнева."}},
[4283] = {"ПЯТЬДЕСЯТ! АГА!", {"Орелиусу нужны 50 медальонов Черной горы. Скорее всего их можно найти у орков Пылающих степях."}},
[4284] = {"Кристаллы Силы", {"Соберите по 7 кристаллов Силы каждого цвета: красных, синих, желтых и голубых. Принесите их Джей Ди Колли в Укрытие Маршалла."}},
[4285] = {"Северный пилон", {"Отыщите Северный хрустальный пилон в Кратере Ун'Горо."}},
[4286] = {"Хороший товар", {"Отправьтесь в глубины Черной горы и принесите 20 поясных сумок дворфов Черного Железа. По выполнении задания вернуться к Орелиусу. Предположительно поясные сумки можно отобрать у дворфов Черного Железа в глубинах Черной горы."}},
[4287] = {"Восточный пилон", {"Отыщите Восточный хрустальный пилон в Кратере Ун'Горо."}},
[4288] = {"Западный пилон", {"Отыщите Западный хрустальный пилон."}},
[4289] = {"Обезьяны Ун'Горо", {"Принесите 2 шкуры Ун'Горской гориллы, 2 шкуры Ун'Горского топтуна и 2 шкуры Ун'Горского рокотуна землепроходцу Торве к входу в Кратер Ун'Горо."}},
[4290] = {"Добыча Лар'корви", {"Найдите последнюю добычу Лар'корви, украдите кусок трупа жесткозуба и вернитесь к землепроходцу Торве в Кратер Ун'Горо."}},
[4291] = {"Запах Лар'корви", {"Принесите 2 феромонные железы равазавра землепроходцу Торве в преддверие Кратера Ун'Горо."}},
[4292] = {"Приманка для Лар'корви", {"С помощью предметов из мешочка Торвы выманите Лар'корви и сразите его. Принесите голову Лар'корви землепроходцу Торве ко входу в Кратер Ун'Горо."}},
[4293] = {"Проба слизи...", {"Возьмите на пробу образцы слизи из Оскверненного леса. Воспользуйтесь тестовым оборудованием химика Барабоса в Катакомбах, чтобы отобрать 5 наиболее чистых образцов и отдайте их ему."}},
