[4808] = {"Фелнок Сталлист", {"Отнесите письмо Тинки Фелноку Сталлисту."}},
[4809] = {"Рога стылодыхов", {"Принесите 8 безупречных рогов стылодыха Фелноку Сталлисту."}},
[4810] = {"Возвращение к Тинки", {"Отнесите ящик Фелнока Тинки Кипеллер в Пылающие степи."}},
[4811] = {"Красный кристалл", {"Направляйтесь на восток от Аубердина и поищите большой красный кристалл в горной гряде на востоке Темного Берега. Вернитесь с докладом к часовому Глинде Нал'Шие в Аубердин."}},
[4812] = {"Пока струится вода...", {"Наполните пустую колбу для воды в лунном колодце Аубердина, а затем исследуйте красный кристалл, находящийся в горной гряде на востоке Темного Берега."}},
[4813] = {"Скрытые фрагменты", {"Доложите о результатах ваших изысканий часовому Глинде Нал'Шие в Аубердине."}},
[4821] = {"Странное яйцо", {"Принесите неизвестное яйцо Хагару Грозовому Копыту на Заставу Вольного Ветра."}},
[4822] = {"Женщинам – цветы, детям -...", {"Купите своему подопечному клубничное мороженое. Кажется, мальчик предпочитает мороженое Тигуле и Форора."}},
[4841] = {"Упокоение кентавров", {"Убейте 12 разведчиков, 10 пастухов и 6 ветроловок из племени Галак, после чего вернитесь к дозорному утесов Долгорогу на Заставу Вольного Ветра."}},
[4842] = {"Выяснение причин", {"Последуйте указаниям Доновы Снегоноры, а затем вернитесь к ней."}},
[4861] = {"Разъяренные дикие совухи", {"Найдите разоренный лагерь Джерона Гранильщика и его припасы на юге Зимних Ключей."}},
[4862] = {"Товар на любителя", {"Отправьтесь на пик Черной горы и принесите Киблеру 15 яиц скального паука.","Судя по всему, яйца надо искать недалеко от пауков."}},
[4863] = {"Разъяренные дикие совухи", {"Найдите опрокинутый фургон Джерона Гранильщика."}},
[4864] = {"Разъяренные дикие совухи", {"Подберите припасы Джерона из снега, и найдите дикого совуха, похитившего амулет, о котором говорил Джерон.","Принесите припасы и ожерелье миних перьев Джерону Гранильщику в его дом в Зимних Ключах."}},
[4865] = {"Змеиная дикость", {"Расскажите о найденном Мотеге Огненной Гриве."}},
[4866] = {"Материнское молоко", {"Найдите в самом сердце пика Черной горы мать Дымную Паутину. Сражайтесь с ней, пока она не введет вам свой яд. Скорее всего ее также придется убить. Когда яд будет в вас, вернитесь к Джону-Оборванцу, чтобы он смог добыть яд."}},
[4867] = {"Аррок Смертный Вопль", {"Прочитайте записку Вароша. Принесите Варошу его амулет."}},
[4881] = {"Убийственный заговор", {"Предупредите Канати Серое Облако на заставе Белого Плеса, что на него готовится покушение."}},
[4882] = {"Умение хранить секреты", {"Отнесите ожерелье синих перьев Труллу Баловню Судьбы на Заставу Отравленной Крови."}},
[4883] = {"Умение хранить секреты", {"Отнесите ожерелье синих перьев Наре Буйногривой."}},
[4901] = {"Стражи алтаря", {"Защитите Раншаллу, пока она пытается активировать алтарь Элуны. Сообщите о ваших находках Эреласу Янтарному Небу в деревне Рут'теран."}},
[4902] = {"Питомцы Элуны", {"Поговорите с верховным друидом Фэндралом Оленьим Шлемом в Дарнассе."}},
[4903] = {"Приказ полководца", {"Убейте вождя Омокка, воеводу Вуна и повелителя Змейталака. Найденные при них важные бумаги Черной горы доставьте полководцу Клинозубу в Каргат."}},
[4904] = {"Долгожданная свобода", {"Выведите Лакоту Песню Ветра с Пика Темного Облака, а потом поговорите с Талией Янтарной Шкурой на Заставе Вольного Ветра."}},
[4906] = {"Снова порча!", {"Эридана Грусть Ветра из Оскверненного леса просит вас убить 8 призывателей огня, 8 предателей и 8 плутов из племени Нефритового огня, а также Ксаварика. По выполнении задания возвращайтесь к Эридане."}},
[4907] = {"Тинки Кипеллер", {"Поговорите с Тинки Кипеллер."}},
[4921] = {"Пропавшая без вести", {"Отыщите жену Манкрика и возвращайтесь к нему в Перекресток."}},
[4941] = {"Мудрость Эйтригга", {"Поговорите с Эйтриггом в Оргриммаре. После этого обратитесь к вождю Траллу.","В последний раз Эйтригг был в в зале приемов Тралла."}},
[4961] = {"Очищение шара Орахила", {"Убейте демона сферы, а потом поговорите с Табетой."}},
[4962] = {"Осколок гончей Скверны", {"Возьмите рубин Сквернолова и поймайте в него одну из гончих скверны в Пустошах. Добившись успеха, отнесите плененный дух Гончей Скверны и рубин Сквернолова Менаре Расщепительнице Бездны из Степей."}},
[4963] = {"Осколок инфернала", {"Возьмите Пылающий изумруд и поймайте в него одного из инферналов в Пустошах. Добившись успеха, отнесите плененный дух инфернала и пылающий изумруд Менаре Расщепительнице Бездны из Степей."}},
[4964] = {"Завершение шара Дар'орахила", {"Дождитесь, пока Менара Расщепительница Бездны закончит работу над шаром Дар'орахила, и еще раз поговорите с ней."}},
[4965] = {"Знание шара Орахила", {"Поговорите с Менарой Расщепительницей Бездны в Степях."}},
[4966] = {"Защитить Канати Серое Облако", {"Защитите Канати Серое Облако от нападения кентавров."}},
[4967] = {"Знание шара Орахила", {"Поговорите с Менарой Расщепительницей Бездны в Степях."}},
[4968] = {"Знание шара Орахила", {"Поговорите с Менарой Расщепительницей Бездны в Степях."}},
[4969] = {"Знание шара Орахила", {"Поговорите с Менарой Расщепительницей Бездны в Степях."}},
[4970] = {"Накормить ледопардов", {"Соберите 5 кусков мяса медведей-щеброзуба и 5 кусков мяса стылодыхов для Риверна Холодного Ветра в Зимних Ключах."}},
[4971] = {"Вопрос времени", {"Воспользуйтесь временным переместителем рядом с одним из амбаров Андорала и найдите временных паразитов.","Убейте 10 временных паразитов и верните временной переместитель Хроми в таверну Андорала в Западных Чумных землях."}},
[4972] = {"Отсчет времени", {"Найдите 5 часов Андорала, спрятанных в сундуки среди руин. Принесите их Хроми в таверну Андорала в Западных Чумных землях."}},
[4973] = {"Отсчет времени", {"Найдите 5 часов Андорала, спрятанных в сундуки среди руин. Принесите их Хроми в таверну Андорала в Западных Чумных землях."}},
[4974] = {"За Орду!", {"Пойдите в Пик Черной горы и убейте вождя Ренда Чернорука. Принесите его голову в Оргриммар."}},
[4975] = {"Завершение шара Дар'орахила", {"Дождитесь, пока Менара Расщепительница Бездны закончит работу над шаром Дар'орахила, и еще раз поговорите с ней."}},
[4976] = {"Возвращение к Менаре", {"Отнесите очищенную сферу инфернала Менаре Расщепительнице Бездны в Степи."}},
[4981] = {"Агент Блестяшка", {"Отправьтесь к пику Черной горы и выясните, что сталось с Блестяшкой."}},
[4982] = {"Вещи Блестяшки", {"Найдите вещи Блестяшки и верните их владелице. Блестяшка сказала, что она спрятала свое оборудование где-то на нижнем ярусе подземелья."}},
[4983] = {"По данным разведки", {"Передайте донесение Блестяшки гроссмейстеру Лекслорту в Каргат."}},
[4984] = {"Страдания природы", {"Убейте 8 больных волков и вернитесь к Мулгрису Глубокой Реке в Удел Страданий в Западных Чумных землях."}},
[4985] = {"Страдания природы", {"Убейте 8 болезненных гризли и вернитесь к Мулгрису Глубокой Реке в Удел Страданий в Западных Чумных землях."}},
[4986] = {"Покрытая письменами дубовая ветвь", {"Судя по магии, вложенной в покрытую письменами дубовую ветвь, друид-таурен желал отнести ее в Круг Кенария в Дарнассе. Попросите о помощи одного из тамошних друидов."}},
[4987] = {"Покрытая письменами дубовая ветвь", {"Судя по магии, записанной в покрытую письменами дубовую ветвь, друид-таурен желал отнести ее в Круг Кенария в Громовом Утесе. Попросите о помощи одного из тамошних друидов."}},
[5001] = {"Вещи Блестяшки", {"Найдите вещи Блестяшки и верните их владелице. Удачи!"}},
[5002] = {"Донесение Максвелла", {"Передайте маршалу Максвеллу информацию Блестяшки."}},
[5021] = {"Лучше поздно, чем никогда", {"Судя по сбивчивым объяснениям призрачной женщины, она хочет доставить посылку. Бедняга уверяет, что посылка лежала там, где стоял ее конь. Где именно это было, женщина не сказала."}},
[5022] = {"Лучше поздно, чем никогда", {"Выясните у королевских представителей Переписи Штормграда местонахождение Эммы Фелстоун. Офицера-переписчика можно найти в Ратуше."}},
[5023] = {"Лучше поздно, чем никогда", {"Поговорите с королевскими инспекторами Подгорода и наведите справки о Иеремии Фелстоуне. Офицера-переписчика можно найти рядом с пунктом регистрации гильдий и гербовых накидок."}},
[5041] = {"Снабжение Перекрестка", {"Найдите и верните Торку из Степей ящики с припасами из Перекрестка."}},
