[5047] = {"Пип Смышлен, к вашим услугам!", {"Переговорите с Малифоем Черномолотом в Круговзоре."}},
[5048] = {"Добросердечная Эмма", {"Найдите в Штормграде старушку Эмму и узнайте, действительно ли она – Эмма Фелстоун. Если да, то, может, ей понравится та посылочка, что собрала для нее Джанис Фелстоун."}},
[5049] = {"Грусть Иеремии", {"Найдите в Подгороде Иеремию Пэйсона и выясните, в самом ли деле он Иеремия Фелстоун. Если да, передайте ему посылку от Джанис Фелстоун."}},
[5050] = {"Оберег удачи", {"Отнесите половинку талисмана Джанис Фелстоун в Западные Чумные земли."}},
[5051] = {"Вторая половина", {"Отыщите вторую половину оберега Удачи на ферме Фелстоун в Западных Чумных землях и соберите его.","Отдайте оберег Удачи Джанис Фелстоун."}},
[5052] = {"Кровавые осколки Агамаггана", {"Принесите кровавый осколок Кривозубу в Лагерь Таурахо в Степи.","Соберите побольше кровавых осколков, и он одарит вас силой, дарованной ему Агамагганом."}},
[5054] = {"Урсиус из щербозубов", {"По просьбе Шторма Призрачное Копыто в Круговзоре в Зимних Ключах убейте Урсиуса."}},
[5055] = {"Убить Брумеран", {"Убейте Брумеран по просьбе Шторма Призрачное Копыто в Круговзоре в Зимних Ключах."}},
[5056] = {"Ши-Ротам", {"Убейте Ши-Ротам для Шторма Призрачное Копыто."}},
[5057] = {"Прежние свершения", {"Отнесите Знак Шторма Призрачное Копыто Мелору Каменное Копыто в Громовой Утес."}},
