[5060] = {"Под замком", nil},
[5061] = {"Водный облик", {"Вернитесь в Дарнасс и покажите Матренгилу Медвежьей Лапе свою подвеску Морского Льва."}},
[5062] = {"Священное пламя", {"Соберите 10 листов огненной агавы и поговорите с Магатой Зловещим Тотемом на Вершине Старейшин в Громовом Утесе."}},
[5063] = {"Шапка Алого Ученого", {"Для изготовления шапки Алого Ученого вам нужно принести Малифою Черномолоту 1 Безупречную звериную шкуру; 5 кусков потертого шовного материала поганища; 8 чародейских кристаллов; 5 зачарованных алых нитей.","Вы также должны будете уплатить 50 золотых монет за изготовление головного убора."}},
[5064] = {"Наблюдение за Зловещим Тотемом", {"Найдите и заберите три тайные записки на Пике Темного Облака."}},
[5065] = {"Утраченные таблички Мошару", {"Принесите третью и четвертую таблички Мошару геологу Железному Башмаку в Танарис."}},
[5066] = {"Призыв к оружию: Чумные земли!", {"Найдите командира Ашлама Отважного Кулака в лагере Промозглого Ветра на севере Альтеракских гор."}},
[5067] = {"Поножи Аркана", {"Для изготовления Поножей Аркана вам нужно принести Малифою Черномолоту следующее: 1 безупречную звериную шкуру; 5 кусков потертого шовного материала поганища, 5 арканитовых слитков, 5 порций бальзамировочного состава Снегошепота;","Вам придется также заплатить 50 золотых монет за изготовление этого предмета."}},
[5068] = {"Кираса кровавой жажды", {"Для изготовления кирасы кровавой жажды вам нужно будет принести Малифою Чернобойцу следующее: 1 безупречную звериную шкуру; 10 кусков потертого шовного материала поганища; 5 арканитовых слитков; 5 тенекож.","Вы также должны будете уплатить 50 золотых монет за изготовление доспеха."}},
[5081] = {"Миссия Максвелла", {"Отправляйтесь на пик Черной горы и устраните воеводу Вуна, вождя Омокка и повелителя Змейталака. По выполнении задания вернитесь к маршалу Максвеллу."}},
[5082] = {"Угроза со стороны Зимней Спячки", {"По просьбе Доновы Снегоноры из Зимних Ключей убейте 8 землепроходцев, 8 дозорных и 8 тотемистов из племени Зимней Спячки."}},
[5083] = {"Огненная вода племени Зимней Спячки", {"Принесите Донове Снегоноре пустую флягу из-под огненной воды."}},
[5084] = {"Поддавшиеся порче", {"Исследуйте поселение фурболгов из племени Мертвого Леса в северной части Оскверненного леса в поисках ключа к разгадке."}},
[5085] = {"Таинственная слизь", {"Вернитесь к Донове и сообщите ей о своих находках."}},
[5086] = {"Ядовитый Ужас", {"Соберите 3 капли ядовитого ужаса и принесите их Донове Снегоноре в Зимние Ключи."}},
[5087] = {"Гонцы Зимней Спячки", {"Найдите гонцов из племени Зимней Спячки и не позвольте им донести их груз до их деревни."}},
[5088] = {"Арикара", {"Убейте Арикару и в качестве доказательства принесите ее останки вместе с воспламеняющим порошком Мотеге Огненной Гриве на заставу Белого Плеса."}},
[5089] = {"Приказ генерала Драккисата", {"Отнесите приказ генерала Драккисата маршалу Максвеллу в Пылающие степи."}},
[5090] = {"Призыв к оружию: Чумные земли!", {"Разыщите командира Ашлама Отважного Кулака в лагере Промозглого Ветра к северу от Альтеракских гор."}},
[5091] = {"Призыв к оружию: Чумные земли!", {"Разыщите командира Ашлама Отважного Кулака в лагере Промозглого Ветра к северу от Альтеракских гор."}},
[5092] = {"Зачистка территории", {"Убейте 10 скелетов-свежевателей и 10 слюнявых вурдалаков на холме Печали.","Вернитесь к командиру Ашламу Отважному Кулаку в лагерь Промозглого Ветра в Западных Чумных землях."}},
[5093] = {"Призыв к оружию: Чумные земли!", {"Отыщите верховного палача Деррингтона. Его базовый лагерь находится у Бастиона, к востоку от Тирисфальских лугов и Подгорода."}},
[5094] = {"Призыв к оружию: Чумные земли!", {"Отыщите верховного палача Деррингтона. Его базовый лагерь находится у Бастиона, к востоку от Тирисфальских лугов и Подгорода."}},
[5095] = {"Призыв к оружию: Чумные земли!", {"Найдите верховного палача Деррингтона. Его базовый лагерь расположен в Бастионе к востоку от Тирисфальских лесов и Подгорода."}},
[5096] = {"Вылазка в стан Алого Марша", {"Отправляйтесь в лагерь Алого ордена между полем Джанис и Слезами Далсона и уничтожьте командный шатер.","Разместите в лагере знамя Плети и возвращайтесь к верховному палачу Деррингтону в Бастион, в Западные Чумные земли."}},
[5097] = {"Сторожевые башни", {"Отметьте все башни Андорала при помощи сигнального факела; задействовать факел следует, находясь в дверном проеме каждой из башен.","Отметив все четыре башни, следует вернуть сигнальный факел командиру Ашламу Отважному Кулаку в лагерь Промозглого Ветра в Западных Чумных землях."}},
[5098] = {"Сторожевые башни", {"Отметьте все башни Андорала при помощи сигнального факела; задействовать факел следует, находясь в дверном проеме каждой из башен.","Отметив все четыре башни, следует вернуть сигнальный факел командиру Ашламу Смелонраву в лагерь Холодного Ветра в Западных Чумных землях."}},
[5102] = {"Кончина генерала Драккисата", {"Отправьтесь на пик Черной горы и устраните генерала Драккисата. По выполнении задания вернитесь к маршалу Максвеллу."}},
[5103] = {"Смерть в огне", {"Наверняка в этом мире есть кто-то, кто знает, что делать с этими рукавицами."}},
[5121] = {"Верховный вождь племени Зимней Спячки", {"По просьбе Доновы Снегоноры из Зимних Ключей убейте верховного вождя племени Зимней Спячки."}},
[5122] = {"Медальон Веры", nil},
[5123] = {"Последний фрагмент мозаики", {"Отнесите неразборчиво исписанный журнал Донове Снегоноре в Зимние Ключи."}},
[5124] = {"Огненные латные рукавицы", {"Принесите Малифою Черномолоту 6 зачарованных ториевых слитков, 2 субстанции Огня и 4 звездных рубина. Вам также нужно будет расстаться с неопалимыми латными рукавицами."}},
[5125] = {"Слова Аурия", nil},
[5126] = {"История Лоракса", {"Поговорите с Лораксом. Выслушайте то, что он вам расскажет."}},
[5127] = {"Демонова кузня", {"Отправляйтесь на пик Черной горы и найдите Горалука Треснувшую Наковальню. Убейте его, а потом воткните в труп окровавленную пику. Таким образом пика вытянет его душу и окрасится ей.","Кроме того, вам нужно найти заготовку рунической кирасы.","Отнесите пику и кирасу Лораксу в Зимних Ключах."}},
[5128] = {"Слова верховного вождя", {"Отнесите неразборчиво исписанный журнал Келеку Стражу Неба в южную часть Оскверненного леса."}},
[5141] = {"Кожевничество: чешуя драконов", {"Принесите 2 прочные кирасы скорпида, 2 пары прочных перчаток скорпида и 10 потертых чешуек дракона Питеру Галену в Азшару.","По выполнении этого задания вы получите возможность обучения кожевничеству школы чешуи драконов.","Если вы выполните это задание, вы не сможете изучать кожевничество школы традиций предков и школы силы стихий."}},
[5142] = {"Малышка Памела", {"Найдите Памелу Редпат."}},
[5143] = {"Кожевничество: традиции предков", {"Принесите дикарский кожаный жилет и дикарский кожаный шлем Кариссии Лунной Охотнице из Фераласа.","Выполнив это задание, вы станете кожевником школы традиций предков.","Также вы лишитесь возможности изучать кожевничество школы чешуи драконов и школы силы стихий; убедитесь, что избрали именно этот путь, прежде чем выполнять задание."}},
[5144] = {"Кожевничество: сила стихий", {"Принесите Саре Дубильщице 2 сердца огня, 2 сферы воды, 2 ядра земли и 2 дыхания ветра.","Выполнение этого задания позволит вам изучать кожевничество школы силы стихий.","Кроме того, вы откажетесь от изучения кожевничества школ чешуи драконов и традиций предков. Нужно быть уверенным, что выбрана верная школа, прежде чем выполнять задание."}},
[5145] = {"Кожевничество: чешуя драконов", {"Принесите 2 прочные кирасы скорпида, 2 пары прочных перчаток скорпида и 10 потертых чешуек дракона Торкафу Оку Дракона в Бесплодные земли.","По выполнении этого задания вы получите возможность обучения кожевничеству школы чешуи драконов.","Если вы выполните это задание, вы не сможете изучать кожевничество школы традиций предков и школы силы стихий."}},
[5146] = {"Кожевничество: сила стихий", {"Принесите Брумну Заиндевевшему Копыту на нагорье Арати 2 сердца огня, 2 сферы воды, 2 ядра земли и 2 дыхания ветра.","Выполнение этого задания позволит вам изучать кожевничество школы силы стихий.","Кроме того, вы откажетесь от изучения кожевничества школ чешуи драконов и традиций предков. Нужно быть уверенным, что выбрана верная школа, прежде чем выполнять задание."}},
[5147] = {"Разыскивается: Арнак Зловещий Тотем", {"Убейте Арнака Зловещий Тотем и принесите доказательство дозорному утесов Долгорогу на Заставу Вольного Ветра."}},
[5148] = {"Кожевничество: традиции предков", {"Принесите дикарский кожаный жилет и дикарский кожаный шлем Кариссии Лунной Охотнице из Фераласа.","Выполнив это задание, вы станете кожевником школы традиций предков.","Также вы лишитесь возможности изучать кожевничество школы чешуи драконов и школы силы стихий; убедитесь, что избрали именно этот путь, прежде чем выполнять задание."}},
[5149] = {"Кукла Памелы", {"Найдите куклу Памелы."}},
[5150] = {"Даданга проголодалась!", nil},
[5151] = {"Механизм гипервместимости", {"Убейте зверя и принестите механизм гипервместимости и ключ от клетки пантеры Визло Фингалету."}},
[5152] = {"Тетушка Марлен", {"Поговорите с Марлен в южной части руин Андорала."}},
[5153] = {"Странный историк", {"Принесите Хроми обручальное кольцо Джозефа."}},
[5154] = {"Анналы Дарроушира", {"Принесите Анналы Дарроушира Хроми в Андорал."}},
[5155] = {"Силы Джеденара", {"Отправляйтесь в Джеденар и убейте 4 джеденарских гончих, 4 джеденарских стражей, 6 джеденарских адептов и 6 джеденарских сектантов, затем возвращайтесь к Грете Заиндевевшее Копыто в Оскверненный лес."}},
[5156] = {"Проверка на порчу", {"Найдите и уничтожьте 2 энтропических зверей и 2 энтропических ужасов при обследовании долины Рваных Ран. После проверьте, по-прежнему ли заполнены кратеры оскверненной водой. Затем возвращайтесь к Таруну Красному Перу в Оскверненный лес."}},
[5157] = {"Собрать оскверненную воду", {"Возьмите пустую флягу и наполните ее водой из лунного колодца, что находится в центре Джеденара. Возвращайтесь к Грете Замшелому Копыту в Оскверненный лес."}},
[5158] = {"В поисках духовной помощи", {"Отнесите порченую воду из Лунного колодца Ислен Заклинателю Воды в Степи."}},
[5159] = {"Чистая вода возвращается в Оскверненный лес", {"Отнесите очищенную воду из лунного колодца Грете Замшелому Копыту в Оскверненный лес."}},
[5160] = {"Матрона-защитница", {"Дойдите до Зимних Ключей и найдите Халех. Отдайте ей пластину чешуи Ауби."}},
[5161] = {"Ярость Синих драконов", {"Если у вас есть желание продолжить, поговорите снова с Халех."}},
[5162] = {"Гнев синих драконов", {"Поговорите с Джезибой в Чумных землях. Он обитает в Андорале."}},
[5163] = {"Йети где-то рядом…", {"Возьмите механического йети Уми и напугайте им ее друзей:","Легакки в Круговзоре (Зимние Ключи)","поливалку в Прибамбасске (Танарис)","Квикксиля в Укрытии Маршалла (Кратер Ун'Горо)","Когда проделаете все это, принесите механического йети обратно Уми."}},
[5164] = {"Перечень Заблудших", {"Прочитайте перечень Заблудших."}},
[5165] = {"Погасить Пламя Защиты", {"При помощи очищенной воды из лунного колодца загасите пламя в четырех жаровнях защиты внутри Оплота Теней в Оскверненном лесу. Затем возвращайтесь к Грете Замшелому Копыту в Изумрудное святилище."}},
[5166] = {"Кираса Всецветных драконов", {"Джезиба \"Скульптор\" может сковать кирасу Всецветных драконов, если вы принесете ему следующее:","1 всецветный панцирь.","10 сверкающих всецветных чешуек.","10 порций крови героев.","10 мотков потертого шовного материала."}},
[5167] = {"Ножные латы Всецветного воина", {"Джезиба 'Скульптор' может сковать ножные латы Всецветного воина, если вы принесете ему следующее:","1 всецветный панцирь.","10 сверкающих всецветных чешуек.","10 порций крови героев.","5 Шкур Тени."}},
[5168] = {"Герои Дарроушира", {"Принесите книгу Дейвила и щит Крассена Карлину Редпату."}},
[5181] = {"Негодяи Дарроушира", {"Принести череп Хоргуса и сломанный меч Мардука Карлину Редпату в Часовню Последней Надежды."}},
[5201] = {"Вторжение племени Зимней Спячки", {"По заданию Риверна Холодного Ветра убейте 5 шаманов и 5 ведмедей из клана Зимней Спячки."}},
[5202] = {"Странный красный ключ", {"Обыщите Джеденар и найдите замок, который открывается странным красным ключом."}},
[5203] = {"Спасение из Джеденара", {"Сопроводите Арко'нарин туда, где лежат меч Светлая Сталь и снаряжение Арконарин, и защитите ее при побеге из Оплота Теней. Потом разыщите Джессиру Лунную Радугу в Оскверненном лесу и расскажите ей, что случилось с ее друзьями."}},
[5204] = {"Воздаяние Света", {"Вернитесь в Джеденар и убейте суккуба Ракайю, затем отыщите останки Трея Светогорна."}},
[5206] = {"Мародеры Дарроушира", {"Принесите 5 резонирующих черепов и загадочный кристалл Карлину Редпату в Восточные Чумные земли."}},
[5210] = {"Брат Карлин", {"Отнесите дополненные Анналы Дарроушира Карлину Редпату в Восточные Чумные земли."}},
[5211] = {"Защитники Дарроушира", {"Освободите 15 духов Дарроушира и возвращайтесь к Карлину Редпату."}},
[5212] = {"Плоть не лжет", {"Принесите 20 препаратов чумной плоти из Стратхольма Бетине Биггльцинк. Предположительно, любая тварь из Стратхольма сойдет за препарат."}},
[5213] = {"Вирус чумы", {"Отправляйтесь в Стратхольм и исследуйте зиккураты. Доставьте сведения о Плети Бетине Биггльцинк."}},
[5214] = {"Великий Эзра Гримм", {"Найдите табачную лавку Эзры Гримма в Стратхольме, отыщите в ней коробку отличного табака Эзры Гримма и принесите ее Дымку ЛаРу."}},
[5215] = {"Котлы Плети", {"Поговорите с верховной жрицей Макдонелл на Берегу Промозглого Ветра в Западных Чумных землях."}},
[5216] = {"Цель: поле Джанис", {"Отправляйтесь на поле Джанис в Западных Чумных землях, найдите и одолейте Хранителя котла, взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка c поля Джанис."}},
[5217] = {"Возвращение в Лагерь Промозглого Ветра", {"Принесите полную бутылку с поля Джанис верховной жрице Макдонелл в Лагерь Промозглого Ветра, что в Западных Чумных землях."}},
[5218] = {"Котел поля Джанис", nil},
[5219] = {"Цель: Слезы Далсона", {"Отправляйтесь в Слезы Далсона в Западных Чумных землях, найдите и одолейте Хранителя котла, взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка c поля Слез Далсона."}},
