[5221] = {"Котел Слез Далсона", nil},
[5222] = {"Цель: Удел Страданий", {"Отправляйтесь в Удел Страданий в Западных Чумных землях. Найдите и одолейте Хранителя котла. Взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка с Удела Страданий."}},
[5223] = {"Возвращение в Лагерь Промозглого Ветра", {"Принесите полную бутылку с Призрачной фермы верховной жрице Макдонелл в Лагерь Промозглого Ветра, в Западные Чумные земли."}},
[5224] = {"Котел Призрачной фермы", nil},
[5225] = {"Цель: пустошь Гаррона", {"Отправляйтесь на пустошь Гаррона в Западных Чумных землях, найдите и одолейте Хранителя котла, взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка с путоши Гаррона."}},
[5226] = {"Возвращение в Лагерь Промозглого Ветра", {"Отнесите полную бутылку с пустоши Гаррона высшей жрице Макдонелл в Лагерь Промозглого Ветра, что в Западных Чумных землях."}},
[5227] = {"Котел пустоши Гаррона", nil},
[5228] = {"Котлы Плети", {"Поговорите со жрицей Тьмы Вандис в Бастионе, что в Западных Чумных землях."}},
[5229] = {"Цель: поле Джанис", {"Отправляйтесь на поле Джанис в Западных Чумных землях, найдите и одолейте Хранителя котла, взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка c поля Джанис."}},
