[5237] = {"Задание выполнено!", nil},
[5238] = {"Задание выполнено!", nil},
[5241] = {"Дядюшка Карлин", {"Найдите Карлина Редпата."}},
[5242] = {"Окончательный удар", {"Убейте Моору и Салию и принесите голову владыки теней Скверн'дана Грете Замшелому Копыту в Оскверненный лес."}},
[5243] = {"Святая вода", {"Отправляйтесь на север, в Стратхольм. Обыщите брошенные ящики с припасами и соберите 5 мер святой воды Стратхольма. Возвращайтесь к Леониду Бартоломею Чтимому, как только воды будет достаточно."}},
[5244] = {"Руины Кел'Терила", {"Поговорите с Джероном Гранильщиком в деревне Звездопада."}},
[5245] = {"Беспокойные духи Кел'Терила", {"Используйте кирку Джерона, чтобы добыть фрагменты реликвии высокорожденных, Принесите их Авроре Зову Небес в Восточные Чумные земли."}},
[5246] = {"Фрагменты прошлого", {"Найдите Священные письмена высокорожденных в Храме Зин-Малор, в Азшаре и принесите их Авроре Зову Небес к Башне Северного перевалa, в Восточных Чумных землях."}},
[5247] = {"Фрагменты прошлого", {"Принесите зачарованный слиток тория, 5 кристаллов Восстановления и фиал пугающей воды Авроре Зову Небес к башне Северного Перевала, в Восточных Чумных землях."}},
[5248] = {"Измученный воспоминаниями о прошлом", {"Отнесите кристалл Зин-Малора к озеру Кел'Терил в Зимних Ключах и найдите духа высокорожденного, который согласится с вами поговорить."}},
[5249] = {"В Зимние Ключи!", {"Найдите в Зимних Ключах деревню Звездопада и поговорите там с Вундом Ночным Охотником."}},
[5250] = {"Звездопад", {"Найдите в Зимних Ключах деревню Звездопада и поговорите там с Вундом Ночным Охотником."}},
[5251] = {"Архивариус", {"Отправляйтесь в Стратхольм и отыщите архивариуса Галфорда из Алого ордена. Убейте его и сожгите архив Алых."}},
[5252] = {"Раскаявшийся высокорожденный", {"Поговорите с Вундом Ночным Охотником в деревне Звездопада и узнайте у него, куда нужно поместить кристалл Зин-Малора."}},
[5253] = {"Кристалл Зин-Малора", {"Доставьте Кристалл Зин-Малора верховному друиду Фэндралу Оленьему Шлему в Дарнасс."}},
[5261] = {"Иган Меховщик", {"Поговорите с Иганом Меховщиком."}},
[5262] = {"Ошеломляющая истина", {"Отнесите голову Бальназара герцогу Николасу Зверенхоффу в Восточные Чумные земли."}},
[5263] = {"Быстрее, выше, сильнее", {"Отправляйтесь в Стратхольм и убейте барона Ривендера. Принесите его голову герцогу Николасу Зверенхоффу."}},
[5264] = {"Лорд Максвелл Тиросс", {"Поговорите с лордом Максвеллом Тироссом. Вы найдете его внутри храма."}},
[5265] = {"Серебряный Оплот", {"Откройте Серебряный Оплот и получите награду."}},
[5281] = {"Мятущиеся души", {"Найдите Эгана. В последний раз его видели недалеко от Стратхольма."}},
[5282] = {"Мятущиеся души", {"Стреляйте в призраков и неупокоенных на улицах Стратхольма из излучателя Эгана. Когда душа вырвется из призрачной оболочки, выстрелите в нее еще раз, и она обретет свободу.","Освободите 15 неупокоенных душ и возвращайтесь к Эгану."}},
[5283] = {"Искусство изготовления брони", {"Изготовьте и отдайте Грумнусу 4 узорчатых мифриловых шлема, 2 узорчатых мифриловых сапога и 1 узорчатую мифриловую кирасу."}},
[5284] = {"Путь оружейника", {"Сделайте и отдайте Железнусу 4 палаша лунной стали, 4 массивных железных топора, 2 тяжелых мифриловых топора и 2 большие черные палицы."}},
[5301] = {"Искусство школы Брони", {"Чтобы присоединиться к школе Брони, изготовьте и отдайте Окотосу 4 изысканных мифриловых шлема, 2 пары изысканных мифриловых сапог и 1 изысканную мифриловую кирасу."}},
[5302] = {"Путь оружейника", {"Изготовьте и отдайте Боргошу 4 палаша лунной стали, 4 массивных железных топора, 2 тяжелых мифриловых топора и 2 больших черных палицы."}},
[5305] = {"Секрет безмятежности", {"Отправляйтесь в Стратхольм и убейте Молотобойца из Багрового легиона. Возьмите его фартук и возвращайтесь к Лилит."}},
[5306] = {"Змеекамень Пленяющей Духов", {"Отправляйтесь на Пик Черной горы, убейте Темную охотницу Вос'гаджин. Заберите у нее змеекамень и принесите Килраму."}},
[5307] = {"Порча", {"Найдите в Стартхольме оружейника Черной Стражи и уничтожьте его. Возьмите его Знак Черной Стражи и принесите Сирилу Плетебою."}},
[5321] = {"Спящий пробудился", {"Сопроводите Керлониана Вечный Сумрак к Лиладрис Лунной Реке на Заставу Мейстры в Ясеневом лесу."}},
[5341] = {"Сокровище Баровых", {"Отправляйтесь в Некроситет и добудьте сокровище семьи Баровых. Оно состоит из четырех документов: на Каэр Дарроу, на Брилл, на Мельницу Таррен и на Южнобережье. После выполнения задания вернитесь к Алексию Барову."}},
[5342] = {"Последний из Баровых", {"Отправляйтесь в Лагерь Промозглого Ветра – на земли Альянса – и убейте Велдона Барова. Отнесите его голову Алексию Барову.","Соблюдайте осторожность – скорее всего, Велдона охраняет мразь Альянса."}},
[5343] = {"Сокровище Баровых", {"Отправляйтесь в Некроситет и добудьте сокровище семьи Баровых. Оно состоит из четырех документов: на Каэр Дарроу, на Брилл, на Мельницу Таррен и на Южнобережье. После выполнения задания вернитесь к Велдону Барову."}},
[5344] = {"Последний из Баровых", {"Отправляйтесь в Бастион – на земли Орды – и убейте Алексия Барова. Отнесите его голову Велдону Барову.","Соблюдайте осторожность – скорее всего, Алексия охраняет ордынская мразь."}},
[5361] = {"Семейное древо", {"Доставьте отчет стража утеса Долгорога Натаке Длинному Рогу на Заставу Скитающихся Духов в Пустоши."}},
[5381] = {"Рука Ируксоса", {"Добудьте в Крепости Громового Топора Демоническую шкатулку, а затем принесите ее и кирку демонов Тайге Мудрой Гриве в Деревню Ночных Охотников."}},
[5382] = {"Доктор Теолен Крастинов – Мясник", {"Найдите в Некроситете доктора Теолена Крастинова. Убейте его, затем сожгите останки Евы и Люсьена Саркофф. Выполнив задание, возвращайтесь к Еве Саркофф."}},
[5384] = {"Киртонос Глашатай", {"Вернитесь в Некроситет с кровью невинных. Найдите балкон и вылейте кровь в жаровню. На зов явится Киртонос.","Сражайтесь как герой, не сдавайтесь! Уничтожьте Киртоноса и возвращайтесь к Еве Саркофф."}},
[5385] = {"Останки Трея Светогорна", {"Принесите останки Трея Светогорна Джессире Лунной Радуге в Оскверненный лес."}},
[5386] = {"Дорога рыбка к обеду", {"Отправляйтесь в Деревню Ночных Охотников и поговорите с Джинар'Зилленом, а затем принесите Натаке Длинному Рогу 2 рыбы-кровобрюха."}},
