[5461] = {"Рас Ледяной Шепот – человек", {"Отправляйтесь в высокогорья Арати, в руины Стромгарда. Поищите в Стромгарде книгу Воспоминаний. Если удастся ее отыскать, отнесите ее мировому судье Мардуку."}},
[5462] = {"Рас Ледяной Шепот – гибель", {"Отправляйтесь в часовню Светлых надежд в Восточных Чумных землях и найдите Леонида Бартоломея Чтимого. Покажите ему книгу Воспоминаний и расскажите обо всем, о чем удалось узнать."}},
[5463] = {"Дар Менетила", {"Отправляйтесь в Стратхольм и отыщите Дар Менетила. Положите книгу Воспоминаний на оскверненную землю."}},
[5464] = {"Дар Менетила", {"Отнесите Книгу Души Леониду Бартоломею в Восточные Чумные земли."}},
[5465] = {"Книга Души", {"Возвращайтесь к мировому судье Мардуку в Каэр Дарроу. Покажите ему Книгу Души."}},
[5466] = {"Рас Снегошепот – лич", {"Отыщите в Некроситете Раса Снегошепота. Найдя его, воспользуйтесь Книгой Души против его посмертного облика. Если удастся превратить Раса в смертного, убейте его и заберите человеческую голову Раса Снегошепота. Отнесите голову мировому судье Мардуку."}},
[5481] = {"Задание Толстяка", {"Соберите 3 ветки мракоцвета для младшего аптекаря Холланда на кладбище Брилла."}},
[5482] = {"Погибельник", {"Соберите 10 веток погибельника для младшего аптекаря Холланда."}},
[5501] = {"Собиратель костей", {"Принесите 10 костей с Кладбища кодо в хижину Кормека и отдайте Биббли Скобкофузу."}},
[5502] = {"Опекун Орды", {"Возвратите своего подопечного в приют Оргриммара, вернув орочий сиротский свисток попечительнице сирот Батлвейл в Аллею Чести."}},
