[5517] = {"Многоцветное оплечье Рассвета", {"Принесите 25 знаков доблести Серебряного Рассвета интенданту Затвор в Часовню Последней Надежды в Восточные Чумные земли."}},
[5518] = {"Броня огров Гордока", {"Принесите 4 рулона рунной ткани, 8 кусков прочной кожи, 2 мотка рунной нити и огрскую дубильную кислоту Уззлу Наперстяку. Он прикован в секторе Гордока в Забытом Городе."}},
