[5525] = {"Освободите Нотта!", nil},
[5526] = {"Осколки сквернита", {"Отыщите Сквернит в Забытом Городе и подберите его осколок. Есть шанс, что вам удастся его добыть, только убив Алззина Вертоградаря. Крепко заприте осколок в реликварии Чистоты, затем верните его Рабину Сатурне в Ночную Гавань в Лунной поляне."}},
[5527] = {"Реликварий Чистоты", {"Отправляйтесь в Силитус и найдите реликварий Чистоты в руинах деревни Южного Ветра. Если вам удастся его найти, возвращайтесь к Рабину Сатурне в Ночную Гавань в Лунной поляне."}},
[5528] = {"Лучшее пойло Гордока", nil},
[5529] = {"Зачумленные детеныши дракона", {"Убейте 20 зачумленных детенышей дракона, затем возвращайтесь к Бетине Биггльцинк в Часовню Последней Надежды."}},
[5531] = {"Бетина Биггльцинк", {"Принесите охлажденные яйца Бетине Биггльцинк."}},
[5533] = {"Некроситет", {"Поговорите с алхимиком Арбингтоном с вершины Холодной Скалы, что в Западных Чумных землях."}},
[5534] = {"\"Пропавшее\"оборудование Ким'джаеля", {"Найдите некую руну, которую хранят наги в руинах Эльдарата и принесите ее Ким'джаелю в Азшару."}},
[5535] = {"Духовное беспокойство", {"Уничтожьте по 6 теней высокорожденных обоих полов и возвращайтесь к Ло'хату в Азшару."}},
[5536] = {"Земли, полные ненависти", {"Уничтожьте 6 сатиров, 2 плутов Халдарра и 2 скверноподданных из племени Халдарр и возвращайтесь к Ло'хату к границе Ясеневого леса."}},
[5537] = {"Фрагменты скелетов", {"Принесите 15 фрагментов скелетов алхимику Арбингтону в Берег Промозглого Ветра в Западных Чумных землях."}},
[5538] = {"Форма денег стоит", {"Отнесите Кринкелю Сталлинну из Прибамбасска прочные фрагменты скелета и 15 золотых монет."}},
[5541] = {"Боеприпасы для Громострела", {"Доставьте боеприпасы Хегнару Громострелу в Дун Мороге."}},
[5542] = {"Псы-демоны", {"Убейте 20 карликовых чумных псов, 5 чумных псов и 5 бесноватых чумных псов. По выполнении задания вернитесь к Тириону Фордрингу."}},
[5543] = {"Окровавленные небеса", {"Убейте 30 чумных нетопырей и вернитесь к Тириону Фордрингу."}},
[5544] = {"Мясо личинок-трупоедов", {"Найдите личинок-падальщиков и падальщиков-пожирателей. Убейте побольше и наберите из мяса. Возвращайтесь к Тириону Фордрингу, когда накопите 15 кусков мяса червей-трупоедов."}},
[5545] = {"Тридцать три несчастья", {"Принесите 8 вязанок дров Раэлен на Лесопилку Восточной долины."}},
[5561] = {"Отлов кодо", {"Приведите пять прирученных кодо Смиду Заржавеню в Лагерь Заржавня. Не забудьте вернуть ему энергетический инкапсулятор."}},
[5581] = {"Порталы Легиона", {"Уничтожьте 6 порталов на Поле Маннорок, а затем вернитесь к Тайге Мудрой Гриве в Деревню Ночных Охотников."}},
[5582] = {"Здоровая чешуя дракона", {"Отнесите здоровую чешую дракона Бетине Биггльцинк в Часовню Последней Надежды в Восточные Чумные земли."}},
[5601] = {"Сестренка Памела", {"Найдите Памелу Редпат в Дарроушире."}},
[5621] = {"Облачение Луны", {"Найдите стража Шайю и исцелите ее раны при помощи малого исцеления (ранг 2). Потом скажите ей Слово силы: \"Стойкость\". Затем вернитесь к Лорне Утреннему Свету в Доланаар."}},
