[5801] = {"Кузня Пламенного хребта", {"Отнесите форму для отливки отмычек и 2 ториевых слитка на вершину Вулкана Огненного Венца в Кратере Ун'Горо. Окуните форму для отливки отмычки в озеро лавы. Получится недоделанная отмычка.","Отнесите недоделанную отмычку алхимику Арбингтону на Берег Промозглого Ветра в Западные Чумные земли."}},
[5802] = {"Кузня Пламенного хребта", {"Отнесите форму для отливки отмычек и 2 ториевых слитка на вершину Вулкана Огненного Венца в Кратере Ун'Горо. Окуните форму для отливки отмычки в озеро лавы. Получится недоделанная отмычка.","Отнесите недоделанную отмычку аптекарю Дитерсу в Бастион, что в Западных Чумных землях."}},
[5803] = {"Скарабей Араджи", {"Убейте Аража Призывателя отнесите его скарабея алхимику Арбингтону на Берег Промозглого Ветра, что в Западных Чумных землях."}},
