[6081] = {"Обучение зверя", {"Поговорите с Ормаком Лютострелом в Оргриммаре."}},
[6082] = {"Приручение зверя", {"Приручите бронескорпа с помощью хлыста приручения. Потренируйтесь, потом вернитесь к Тотару в Колючем Холме."}},
[6083] = {"Приручение зверя", {"Приручите приливного краба с помощью хлыста приручения. Потренируйтесь, потом верните хлыст приручения Тотару в Колючем Холме."}},
[6084] = {"Приручение зверя", {"Приручите снежного леопарда с помощью хлыста приручения. Потренируйтесь со своим новым питомцем, а затем верните хлыст приручения Грифу Дикому Сердцу в Каранос."}},
[6085] = {"Приручение зверя", {"Приручите медведя-ледолапа с помощью хлыста приручения. Потренируйтесь со своим новым питомцем, а затем верните хлыст приручения Грифу Дикому Сердцу в Каранос."}},
[6086] = {"Обучение зверя", {"Поговорите в с Белией Громовым Гранитом в Стальгорне."}},
[6087] = {"Приручение зверя", {"Приручите охотника прерий при помощи хлыста приручения. Потренируйтесь и верните хлыст Йо Колкой Гриве в Деревне Кровавого Копыта."}},
[6088] = {"Приручение зверя", {"Приручите перепелятника при помощи хлыста приручения. Потренируйтесь и верните хлыст Йо Колкой Гриве в Деревне Кровавого Копыта."}},
[6089] = {"Обучение зверя", {"Поговорите с Хольтом Громовым Рогом в Громовом Утесе."}},
[6101] = {"Приручение зверя", {"С помощью хлыста приручения приручите ночного саблезуба-охотника. Поупражняйтесь, затем верните хлыст приручения Дазалару в Доланаар."}},
[6102] = {"Приручение зверя", {"С помощью хлыста приручите совиного крикуна. Поупражняйтесь, затем верните хлыст приручения Дазалару в Доланаар."}},
[6103] = {"Обучение зверя", {"Поговорите с Иокастой в анклаве Кенария в Дарнассе."}},
[6121] = {"Новый урок", {"Отправляйтесь в Лунную поляну и поговорите с Дендритом Блеском Звезд в селении Ночная Гавань."}},
[6122] = {"Основной источник", {"Используйте пустую колбу для взятия пробы воды из устья пещеры у водопада Скалистый.","Доставьте наполненную колбу Аландариан Песне Ночи в Аубердин, на Темный Берег."}},
[6123] = {"Сбор лекарственных ингредиентов", {"Принесите 5 корешков землекорня и 12 лунных грибов Аландариан Песне Ночи в Аубердине, чтобы она могла сделать целительную мазь."}},
[6124] = {"Исцеление больных", {"Нанесите мазь исцеления животных на 10 больных оленей, обитающих на Темном Берегу, – это должно их исцелить. По слухам, ареал обитания больных оленей – южнее реки Скалистой и севернее Аубердина, а далее – по всей области, вплоть до южных границ Темного Берега, где начинается Ясеневый лес."}},
[6125] = {"Власть над ядом", {"Вернитесь к Матренгилу Медвежьей Лапе в анклав Кенария в Дарнассе."}},
[6126] = {"Новый урок", {"Отправляйтесь в Лунную поляну и поговорите с Дендритом Блеском Звезд в селении Ночная Гавань."}},
[6127] = {"Основной источник", {"Используйте пустой пробоотборник Вершины Багрового Тумана для отбора воды из озера на вершине.","Доставьте наполненный пробоотборник Вершины Багрового Тумана Тонге Руническому Тотему в Перекресток, что в Степях."}},
[6128] = {"Создание лекарства", {"Принесите 5 корешков землекорня и 5 рогов кодо Тонге Руническому Тотему в Перекресток, и он сделает целебную мазь."}},
[6129] = {"Исцеление больных", {"Нанесите мазь исцеления животных на 10 больных газелей, обитающих в северной части Степей, – это должно их вылечить. По слухам, больные газели живут севернее дороги, проходящей через Перекресток с востока на запад."}},
[6130] = {"Власть над ядом", {"Вернитесь к Тюраку Руническому Тотему на Вершину Старейшин в Громовой Утес."}},
[6132] = {"Вытащи меня отсюда!", {"Проводите Мелиццу Жужжалит в лагерь у Кладбища кодо в Пустошах. Поговорите с Хормицем Жужжалитом."}},
[6133] = {"Охота на cледопытов", {"Отправляйтесь в северные пределы Восточных Чумных земель и заполучите реестр Кель'Таласа. Документ находится где-то в Сторожке Кель'Литиен.","Также убейте 8 следопытов, 8 разведчиц дорог и 8 лесников."}},
[6134] = {"Сбор эктоплазмы", {"Принесите 8 единиц эктоплазмы и ящик призрачных магнитов Хормицу Жужжалиту в Пустоши."}},
[6135] = {"Проклятый Тенекрыл", {"Выследите Тенекрыла и убейте его. Принесите полоску меха Тенекрыла Натаносу Гнилостеню.","Говорят, Тенекрыл рыщет в землях к северу от имения Марриса."}},
[6136] = {"Огромный червь", {"Найдите и уничтожьте Мерзкослиза в северных пустошах Восточных Чумных земель. Возвращайтесь к Натаносу Гнилостеню по выполнению задания."}},
[6141] = {"Брат Антон", {"Найдите в Пустошах брата Антона и поговорите с ним."}},
[6142] = {"Наживка из моллюска", {"Найдите 10 кусочков мяса мягкокожего моллюска и принесите Май'Лахии в Деревню Ночных Охотников."}},
[6143] = {"Смерть земноводным!", {"Убейте 7 мирмидонов, 7 наг и 5 колдуний из клана Скользящего Плавника, а затем вернитесь в деревню Сумеречной Охоты и поговорите с Друлзегаром."}},
[6144] = {"Приказ по армии", {"Отправляйтесь в Подгород и предстаньте перед Вериматасом."}},
[6145] = {"Курьер из Багрового Легиона", {"Возвращайтесь в Восточные Чумные земли и выследите курьера из Багрового Легиона. Убейте его и заберите приказ верховного рыцаря.","За дальнейшими указаниями обращайтесь к Натаносу Гнилостеню."}},
[6146] = {"Уловка Натаноса", {"Отправляйтесь в Длань Тира к юго-востоку от Поместья Маррисов. Прибыв в город, откусите от гнилого яблока. Под личиной рыцаря Алого ордена доставьте приказ верховного рыцаря лорду-рыцарю Вальдермару."}},
[6147] = {"Назад к Натаносу", {"Возвращайтесь к Натаносу и отчитайтесь о выполнении задания."}},
[6148] = {"Алый Оракул Деметрия", {"Алый орден сейчас в пути. Найдите Оракула Деметрию и ее свиту на дороге от Длани Тира в Стратхольм. Убейте Оракула и возвращайтесь к Натаносу Гнилостеню."}},
[6161] = {"Сокровища Ракмора", {"Найдите серебряный и золотой ключи Ракмора, а затем разыщите и отоприте сундук Ракмора."}},
[6162] = {"Последняя битва супруга", {"Убейте властителя Рора и принесите его коготь Дрейка'Сур на заставу Отравленной Крови в Оскверненном лесу."}},
[6163] = {"Рамштейн", {"Отправляйтесь в Стратхольм и убейте Рамштайна Ненасытного. Принесите его голову Натаносу в качестве сувенира."}},
[6164] = {"Книга рецептов Августа", {"Принесите Августу Помешанному в Долину Ужаса его книгу рецептов."}},
[6181] = {"Срочное сообщение", {"Отнесите записку Льюиса укротителю грифонов Тору."}},
[6182] = {"Первый и последний", {"Поговорите с Матиасом Шоу в Старом Городе в Штормграде. Матиас находится в казармах."}},
[6183] = {"Почитание мертвых", {"Еще раз поговорите с Матиасом Шоу, если хотите принять его задание."}},
[6184] = {"Флинт Тенемор", {"Отправляйтесь в Лагерь Промозглого Ветра в Западных Чумных землях и найдите Флинта Тенемора."}},
[6185] = {"Чума на востоке", {"Поищите в Восточных Чумных землях сведения про некоего Гнилостеня и пропавших агентов ШРУ. Если удастся найти знаки отличия ШРУ, отнесите их Флинту Тенемору в Лагерь Промозглого Ветра."}},
[6186] = {"Пришествие Гнилостеня", {"Возвращайтесь в Штормград и сообщите верховному лорду Болвару Фордрагону о судьбе Натаноса Марриса."}},
[6187] = {"Порядок должен быть восстановлен", {"Соберите войско и отправляйтесь в Восточные Чумные земли. Проведите полномасштабную атаку на Натаноса Гнилостеня и любых воинов Орды, которые могут попытаться его защитить.","Будьте осторожны, потому что Орда всеми силами будет защищать бывшего предводителя следопытов."}},
[6261] = {"Дунгар Долгопив", {"Отнесите ящик Озрика к укротителю грифонов Дунгару Долгопиву."}},
[6281] = {"Продолжение пути в Штормград", {"Оплатите укротителю грифонов Тору полет до Штормграда и принесите записку Льюиса Озрику Штрангу в магазинчик \"Ограниченная неуязвимость\", расположенный в районе Старого города."}},
[6282] = {"Угроза со стороны гарпий", {"Землепряд Маггран из Приюта у Солнечного Камня просит вас убить 7 гарпий, 7 душительниц, 7 душегубок и 7 бродяжниц из стаи Кровавой Ярости."}},
[6283] = {"Предводительница клана Кровавой Ярости", {"Маггран из Приюта у Солнечного Камня просит убить Потрошительницу из стаи Кровавой Ярости и принести ему ее останки в доказательство того, что она мертва."}},
[6284] = {"Арахнофобия", {"Убейте Бесселет и принесите клык Бесселет Магграну в Приют у Солнечного Камня."}},
[6285] = {"Возвращение к Льюису", {"Оплатите укротителю грифонов Дунгару Долгопиву полет до Сторожевого холма, а затем отнесите ящик Озрика Льюису."}},
[6301] = {"Цикл возрождения", {"Таммра Перекати-поле из Приюта у Солнечного Камня просит вас собрать 10 семян Геи."}},
[6321] = {"Поставка оружия", {"Отнесите заказ Подрига Каросу Раззоку."}},
[6322] = {"Майкл Гарретт", {"Отправляйтесь к Майклу Гарретту с ящиком Гордона."}},
[6323] = {"Полет в Подгород", {"Оплатите у дрессировщика летучих мышей Кароса Раззока полет до Подгорода, а затем отнесите заказ Подрига Гордону Уиндему в Подгороде."}},
[6324] = {"Обратно к Подригу", {"Оплатите у дрессировщика летучих мышей Майкла Гарретта полет до Гробницы, а затем отдайте ящик Гордона стражу смерти Подригу в Гробнице."}},
[6341] = {"Изобилие Тельдрассила", {"Отнесите коллекцию Нессы к Веспристу."}},
[6342] = {"Полет в Аубердин", {"Совершите полет в Аубердин на гиппогрифе укротителя Весприста и отнесите коллекцию Нессы Лайрду."}},
[6343] = {"Возвращение к Нессе", {"Принесите ответ Лайрда Нессе Песне Теней."}},
[6344] = {"Несса Песня Теней", {"Поговорите с Нессой Песня Теней."}},
[6361] = {"Связка шкур", {"Отнесите связку шкур Зарга укротителю ветрокрылов Девраку из Перекрестка."}},
[6362] = {"Дорога до Громового Утеса", {"Оплатите перелет в Громовой Утес укротителю ветрокрылов Девраку и, оказавшись на месте, отнесите связку шкур Ахану."}},
[6363] = {"Укротитель ветрокрылов Тал", {"Отнесите кожаные вещи Ахану Талу из Громового Утеса."}},
[6364] = {"Возвращение к Джахану", {"Оплатите перелет до Перекрестка у укротителя ветрокрылов Тала, а затем отнесите кожаные вещи Ахану Джахану Ястребиному Крылу."}},
[6365] = {"Мясные продукты в Оргриммар", {"Отнесите мясные продукты Зарга Девраку из Перекрестка."}},
[6381] = {"Новая жизнь", {"Посадите 10 семян Геи в земляные курганы Геи, а затем верните оставшиеся зачарованные семена Геи Таммре в Приют у Солнечного Камня."}},
[6382] = {"Большая охота", {"Поговорите с Сенани Громосерд на заставе Расщепленного Древа в Ясеневом лесу."}},
[6383] = {"Большая охота", nil},
[6384] = {"Дорога до Оргриммара", {"Оплатите полет в Оргриммар укротителю ветрокрылов Девраку и, оказавшись в Оргриммаре, отнесите мясные продукты Зарга Гришке."}},
[6385] = {"Укротитель ветрокрылов Дорас", {"Отнесите письмо Гришки Дорасу из Оргриммара."}},
[6386] = {"Возвращение на Перекресток", {"Оплатите перелет в Перекресток укротителю ветрокрылов Доресу и, оказавшись на месте, передайте письмо Гришки Заргху из Перекрестка."}},
[6387] = {"Награды ученикам", {"Отнесите список Брока Торгруму Боррелсону в Телcамаре."}},
[6388] = {"Грит Турден", {"Отнесите почетные кирки Гриту Турдену в Стальгорне."}},
[6389] = {"Чума на вашу лесопилку!", {"Выпустите термитов с Чумных земель на лесопилке Северного Кряжа и возвращайтесь к Натаниэлю Дамаху в Лагерь Промозглого Ветра в Западные Чумные земли."}},
[6390] = {"Чума на вашу лесопилку!", {"Выпустите термитов с Проклятых земель на лесопилке Северного Кряжа и возвращайтесь к Микки Левину в Бастион в Тирисфальских лесах."}},
[6391] = {"Полет в Стальгорн", {"Заплатите укротителю грифонов Торгруму Боррелсону, долетите на грифоне до Стальгорна и отдайте список Брока Голниру Твердопалу."}},
[6392] = {"Возвращение к Броку", {"Заплатите укротителю грифонов Гриту Турдену, долетите на грифоне до Телcамара и отдайте почетные кирки Броку Камнекопу."}},
[6393] = {"Война элементалей", {"Принесите 10 Огненных кристаллов Цунаману в приют Солнечного Камня."}},
[6394] = {"Кирка Тазз'рила", {"Принесите штейгеру Тазз'рилу его кирку."}},
[6395] = {"Последнее желание Марлы", {"Отнесите останки Самуэля Фиппса на могилу Марлы, а затем вернитесь к ученице Элрет."}},
[6401] = {"Кайя жива!", {"Передайте хорошие новости Таммре Перекати-поле, живущей в Приюте у Солнечного Камня."}},
[6402] = {"Встреча в Штормграде", {"Отправьтесь в Штормград и подойдите к городским воротам. Поговорите с оруженосцем Роу, чтобы он сообщил маршалу Виндзору о вашем прибытии."}},
[6403] = {"Великий Маскарад", {"Сопроводите Реджинальда Виндзора через весь Штормград. Защитите его!"}},
[6421] = {"Ущелье Камнепадов", {"Обследуйте глубины пещеры в ущелье Камнепадов и принесите 10 кристаллов резонита для исследования Мор'рогалу в Приют у Солнечного Камня."}},
[6441] = {"Рога сатира", {"Добудьте 16 рогов сатира для Пиксель на заставе Расщепленного Древа."}},
[6442] = {"Наги на Зорамском взморье", {"Принесите 20 голов наг из клана Зловещего Хвоста Марукайе на Зорамское взморье."}},
[6461] = {"Кровопийцы", {"Ксен'Зилла из Малака'джина просит вас убить 10 ползун из густого мха и 7 моховых ядоплюев."}},
[6462] = {"Оберег троллей", {"Принесите 8 оберегов троллей Митсуве в форт Зорам'гар."}},
[6481] = {"Пробуждение земельника", {"Откройте резонитовую емкость при помощи зачарованного кристалла резонита и затем убейте Гоггерока. Вернитесь к Мор'рогалу с новостями и зачарованным кристаллом резонита."}},
[6482] = {"Свободу Руулу!", {"Сопроводите Руула, затем поговорите с Ями Снежное Копыто на заставе Расщепленного Древа."}},
[6501] = {"Око дракона", {"Найдите существа, способные вернуть силу фрагменту амулета Ока дракона. Все, что об этих существах известно – это то, что они есть."}},
[6502] = {"Амулет Пламени дракона", {"Добудьте кровь могучего черного дракона генерала Драккисата, которого можно найти сидящим на троне в залах Вознесения в Пике Черной горы."}},
[6503] = {"Вестницы Ясеневого леса", {"Убейте 9 вестниц Ясеневого леса, а затем вернитесь к Курай'бину на заставу Расщепленного Древа."}},
[6504] = {"Утраченные страницы", {"Найдите 12 пропавших страниц руководства по использованию резака и соберите их по порядку глав 1, 2 и 3. Верните страницы Гурде Печати Гнева близ заставы Расщепленного Древа."}},
[6521] = {"Нечестивый союз", {"Принести голову посла Малкина Вариматрасу в Подгород."}},
[6522] = {"Нечестивый союз", {"Отнесите маленький свиток Вариматрасу в Подгород."}},
[6523] = {"Защита Кайи", {"Проводите Кайю Плоское Копыто до Лагеря Апарахе, затем вернитесь к Макабе Плоскому Копыту, находящемуся поблизости от юго-восточного края Каменного Когтя."}},
[6541] = {"Донесение Кадраку", {"Доложитесь Кадраку в сторожевой башне в северных Степях."}},
[6542] = {"Донесение Кадраку", {"Доложитесь Кадраку в сторожевой башне в северных Степях."}},
[6543] = {"Отчет Песни Войны", {"Распакуйте связку донесений. Доставьте отчеты Песни Войны разведчику, гонцу и всаднику клана Песни Войны. Возьмите у них новые донесения для Кадрака и доставьте ему в северную сторожевую башню в Степях."}},
[6544] = {"Налет Торека", {"Сопровождайте Торека при нападении на Аванпост Среброкрылых и в сражении с Дариэлем Лунным Пламенем, затем доложитесь Эртогу Лютоклыку на заставе Расщепленного Древа."}},
