[6546] = {"Сведения всадника из клана Песни Войны", nil},
[6547] = {"Сведения разведчика клана Песни Войны", nil},
[6548] = {"Отомсти за мою деревню", {"Убейте 8 бузотеров и 6 наемников из племени Зловещего Тотема, затем вернитесь к Макабе Плоскому Копыту, ожидающему вас на юго-восточной границе Когтистых гор."}},
[6561] = {"Жестокость Черных Глубин", {"Принесите голову Повелителя сумрака Келриса Башане Руническому Тотему в Громовой Утес."}},
[6562] = {"Угроза из Глубин", {"Поговорите с Дже'неу Санкри в Ясеневом лесу."}},
[6563] = {"Сущность Аку'май", {"Принесите 20 сапфиров Аку'май Дже'неу Санкри в Ясеневый лес."}},
[6564] = {"Верность Древним богам", {"Принесите отсыревшую записку Дже'неу Санкри в Ясеневый лес."}},
[6565] = {"Верность Древним богам", {"Убейте Лоргуса Джетта в Непроглядной Пучине и вернитесь к Дже'неу Санкри в Ясеневый лес."}},
[6566] = {"Что принес ветер", {"Выслушайте Тралла."}},
[6567] = {"Защитник Орды", {"Найдите Рексара. Вождь сказал, что он бродит в Пустошах, по дороге, что ведет из Когтичтых гор в Фералас."}},
[6568] = {"Завет Рексара", {"Доставьте письмо Рексара Миранде Колдунье в Западные Чумные земли."}},
[6569] = {"Иллюзии Ока", {"Отправляйтесь на пик Черной горы и добудьте 20 глаз черных драконидов. По выполнении задания вернитесь к Миранде Колдунье."}},
[6570] = {"Огнебор", {"Отправляйтесь в Драконьи топи в Пылевых топях и найдите Логово Огнебора. Оказавшись внутри, наденьте амулет Обмана драконов и поговорите с Огнебором."}},
[6571] = {"Поставка для Песни Войны", {"Соберите полностью поставку Песни Войны: лезвия для пил, веревку для бревен, масло и партию топоров Песни Войны для Локе Окарра на заставе Расщепленного Древа."}},
[6581] = {"Пилы Песни Войны", nil},
[6582] = {"Черепа: Провидец", {"Найдите в Зимних Ключах избранного бойца синих дракона по имени Провидец и убейте его. С его черепом вернитесь к Огнебору."}},
[6583] = {"Черепа: Сомнус", {"Убейте вожака зеленых драконов Сомнуса и вернитесь с его черепом к Огнебору."}},
[6584] = {"Черепа: Хроналис", {"Убейте Хроналиса, охраняющего Пещеры Времени в Пустыне Танарис и вернитесь с его черепом к Огнебору."}},
[6585] = {"Черепа: Акстроз", {"Отправьтесь в Грим Батол и убейте Акстроза, избранного бойца красных драконов. Принесите его череп Огнебору."}},
[6601] = {"Вознесение", {"Казалось бы, загадка разгадана. Вы знаете, что амулет Обмана Драконов, который сделала для вас Миранда Колдунья, не будет работать на пике Черной горы. Найдите Рокаро и расскажите о ваших затруднениях. Покажите ему амулет Угасающего Пламени Дракона. Будем надеяться, он знает что делать."}},
[6602] = {"Кровь могучего черного дракона", {"Отправляйтесь к пику Черной горы и убейте генерала Драккисата. Соберите его кровь и вернитесь к Рокаро."}},
[6603] = {"Сложности в Зимних ключах!", {"Найдите Донову Снегонору у горячего источника в Зимних ключах."}},
[6604] = {"Взбесившиеся дикие совухи", {"Поговорите с Джероном Гранильщиком в деревне Звездопада."}},
[6605] = {"Странная дамочка", {"Поговорите с Винной Хезард в Оскверненном лесу."}},
[6606] = {"Немного удачи", {"Поговорите со знахаркой Мау'ари в Кругозоре."}},
[6607] = {"Экстремальная рыбалка Ната Пэгла", {"Поймайте для Ната Пэгла следующих рыб:","Махи-махи с берега Туманных Тростников на Болоте Печали.","Ударник Сартериса с побережья Сар'Терис из Пустошей.","Фералас-ахи из реки Вердантис из Фераласа.","Синий плавник Гибельного берега из Тернистой долины.","По выполнении вернитесь к Нату Пэглу."}},
[6608] = {"Больше учить нечему", {"Похоже, Лумак хочет сказать, что ему больше нечему вас учить. Возможно, стоит найти его друга Ната Пэгла – человека, который удит рыбу на островах к югу от Терамора. Кажется, Лумак имеет в виду именно это."}},
[6609] = {"С меня больше нечего взять!", {"Отправляйтесь на Калимдор, в приморский город Терамор. Там к югу от города найдите остров. Нат Пэгл уже ждет."}},
[6610] = {"Мидии с сюрпризом", {"Могиль Быстроруб поручил принести ему следующее:","12 гигантских яиц. Их можно отыскать у рухов Танариса или других крупных птиц.","10 кусков острого мяса моллюсков. Разумеется, его добывают у моллюсков!","20 кусков альтеракского сыра. Из сыроварни!","Собрав все составляющие, возвращайтесь к Диргу."}},
[6611] = {"Ступай в Прибамбасск!", {"Отправляйтесь в Прибамбасск в пустыне Танарис и поговорите с Могилем Быстрорубом."}},
[6612] = {"Я знаю одного парня...", {"Пойдите в Прибамбасск в пустыне Танарис и поговорите с Могилем Быстрорубом насчет продвижения в поварской карьере."}},
[6621] = {"Король Гиблой Чащи", {"Водрузите знамя Каранга на тотемном кургане Гиблой Чащи. Не дайте фурболгам унитожить знамя. Одолейте вождя Мургута и принесите тотем Мургута Карангу Амаккару в Зорам'гар."}},
[6622] = {"Очередь на медобслуживание", {"Окажите помощь пациентам, используя для перевязки их ран очередные перевязочные бинты. Вы должны оказать помощь 15 пациентам прежде, чем 6 из них успеют умереть от ран.","Помните о порядке оказания помощи! Находящиеся при смерти солдаты умирают быстрее, чем тяжелораненые. Легкораненые живут дольше всех, потому им надо оказывать помощь ПОСЛЕ того, как перевяжете тех, кто при смерти, и тяжелораненых."}},
[6623] = {"Травматология", {"Пойдите в Павший Молот на нагорье Арати и поговорите с доктором Грегори Виктором о том, чтобы стать травматологом."}},
[6624] = {"Очередность перевязки", {"Окажите помощь пациентам, используя для перевязки их ран очередные перевязочные бинты. Вы должны оказать помощь 15 пациентам прежде, чем 6 из них успеют умереть от ран.","Помните о порядке оказания помощи! Находящиеся при смерти солдаты умрут быстрее, чем тяжелораненые. Легкораненые могут прожить дольше всех, потому им надо оказывать помощь ПОСЛЕ того, как перевяжете тех, кто при смерти, и тяжелораненых."}},
[6625] = {"Травматологи Альянса", {"Отправляйтесь в Терамор в Пылевых топях и поговорите с доктором Густавом ван Схлиффом насчет зачисления в травматологи Альянса."}},
[6626] = {"Воинство зла", {"Убейте 8 боевых стражей и 8 терноплетов из племени Иглошкурых, а также 8 сектанток из племени Мертвой Головы и возвращайтесь к Мириам Лунной Певице на Курганы Иглошкурых."}},
[6627] = {"Испытание знаний", {"Дайте верный ответ на вопрос Брауга Тусклого Духа и поговорите с ним еще раз. Брауга можно в любое время найти в Когтистых горах."}},
[6628] = {"Испытание знаний", {"Дайте верный ответ на вопрос Парквела Финталласа и поговорите с ним еще раз. Парквелла можно в любое время найти в Подгороде."}},
[6629] = {"Убить Грундига Темное Облако", {"Убейте Грундига Темное Облако и 6 громил из племени Зловещего Тотема и вернитесь к Макабе Плоскому Копыту, который ждет вблизи юго-восточного края Каменного Когтя."}},
[6641] = {"Ворша Хлестунья", {"Охраняйте Муглаша на его пути к жаровне. Помогите ему погасить жаровню, а затем защищайте его, ведя бой с нагами.","Если вам все удастся, возвращайтесь в форт Зорам'гар и сообщите гонцу клана Песни Войны о смерти Ворши Хлестуньи."}},
