[6761] = {"Новое пограничье", {"Поговорите с Матренгилом Медвежьей Лапой в анклаве Кенария в Дарнассе."}},
[6762] = {"Рабин Сатурна", {"Поговорите с Рабином Сатурной в селении Ночная Гавань на Лунной поляне. Лунная поляна находится между Оскверненным лесом и Зимними Ключами, туда можно добраться по дороге, идущей от крепости Древобрюхов."}},
[6781] = {"Больше обломков брони", nil},
