[6804] = {"Отравленная вода", {"Исцелите отравленных элементалей в Восточных Чумных землях с помощью аспекта Нептулона. Принесите 12 браслетов колыхающихся волноплесков и аспект Нептулона герцогу Гидраксису в Азшару."}},
[6805] = {"Буря в пустыне", {"Убейте 15 пылевихрей и 15 пустынных грохотунов, а затем возвращайтесь к герцогу Гидраксису в Азшару."}},
[6821] = {"Око Углеглаза", {"Принесите глаз Созерцателя Углей герцогу Гидраксису в Азшару."}},
[6822] = {"Огненные Недра", {"Убейте 1 Повелителя огня, 1 лавового гиганта, 1 древнюю гончую бездны и 1 лавового волноплеска и возвращайтесь к герцогу Гидраксису в Азшару."}},
[6823] = {"Агент Гидраксиса", {"Добейтесь уважительного отношеия к себе со стороны Гидраксианских Повелителей Вод, затем отправляйтесь к герцогу Гидраксису в Азшару."}},
[6824] = {"Руки врага", {"Принесите руки Люцифрона, Сульфурона, Гееннаса и Шаззраха герцогу Гидраксису в Азшару."}},
