[6847] = {"Всевидящее око мастера Рисона", {"Заберите Всевидящее око мастера Рисона из пещер Секиры Зимы и возвращайтесь к мастеру Рисону.","Будьте осторожны: говорят, что на Всевидящее око мастера Рисона наложено ужасное проклятие!"}},
[6848] = {"Всевидящее око мастера Рисона", {"Заберите Всевидящее око мастера Рисона из пещер Секиры Зимы и возвращайтесь к мастеру Рисону.","Будьте осторожны: говорят, что на Всевидящее око мастера Рисона наложено ужасное проклятие!"}},
