[6962] = {"Угощение для Дедушки Зимы", {"Принесите 5 имбирных пряников и ледяное молоко Дедушке Зиме в Оргриммар."}},
[6963] = {"Пропавшее угощение Зимнего Покрова", {"Отыщите агента Пастбищ Дымного Леса, который затерялся в снегах Альтеракских гор."}},
[6964] = {"Повод для праздника", {"Поговорите с Сагорном Гривастым Странником в Аллее Мудрости Оргриммара о празднике Зимнего Покрова."}},
[6981] = {"Светящийся осколок", {"Отправляйтесь в Кабестан и найдите там кого-нибудь, кто сможет вам рассказать об этом светящемся осколке.","Затем отнесите осколок туда, куда вас направят."}},
[6982] = {"Припасы из рудника Ледяного Зуба", {"Принесите 10 ящиков с припасами Ледяного Зуба интенданту Альянса в Дун Болдар."}},
[6983] = {"Прохладное чувство юмора", {"Отыщите украденные лакомства и верните их Каймарду Меднокнопу в Оргриммар. Лакомства должны быть у Омерзительного Гринча, который бродит где-то среди заснеженных вершин Альтеракских гор."}},
[6984] = {"\"Пастбища Дымного Леса\" благодарны тебе!", {"Поговорите с Дедушкой Зимой в Оргриммаре."}},
[6985] = {"Припасы Железного рудника", {"Доставьте 10 ящиков припасов Железного рудника интенданту Орды в крепость Северного Волка."}},
[7001] = {"Пустые стойла", {"Найдите северного волка в Альтеракской долине. Подойдите к нему на достаточное расстояние, чтобы его \"приручить\", и используйте намордник Северного Волка. После приручения северный волк проследует за вами к смотрителю стойл из клана Северного Волка. Поговорите со смотрителем, чтобы получить награду за пойманных волков."}},
[7002] = {"Упряжь из бараньей кожи", nil},
[7003] = {"Ликвидация великанов", {"Используйте ультрасжиматель Зорбина, чтобы уменьшить великанов любого вида, каких вы найдете в Фераласе. Принесите 15 остатков миниатюризации этих великанов Зорбину Дребезгуну в порт Забытого берега в Фераласе."}},
[7021] = {"Здравствуй, Дедушка Зима!", {"Поговорите с Дедушкой Зимой – он находится рядом с торговцем из Пастбищ Дымного Леса в Оргриммаре."}},
[7022] = {"Здравствуй, Дедушка Зима!", {"Поговорите с Дедушкой Зимой – он находится неподалеку от торговца из Пастбищ Дымного Леса в Стальгорне."}},
[7023] = {"Здравствуй, Дедушка Зима!", {"Поговорите с Дедушкой Зимой – он находится неподалеку от торговца из Пастбищ Дымного Леса в Стальгорне."}},
