[7025] = {"Угощение для Дедушки Зимы", {"Принесите 5 пряников и ледяного молока Дедушке Зиме в Стальгорн."}},
[7026] = {"Упряжь ездовых баранов", nil},
[7027] = {"Пустые стойла", {"Найдите в Альтеракской долине альтеракского барана. Чтобы приручить барана, используйте учебный ошейник Грозовой Вершины. Для этого надо подойти в барану поближе. Пойманный баран проследует за вами к смотрителю стойл. Поговорите со смотрителем стойл, чтобы получить вознаграждение за пойманных баранов."}},
[7028] = {"Хрустальные орнаменты", {"Соберите 25 терадрических хрустальных орнаментов для Ивы из Пустошей."}},
[7029] = {"Порча Злоязыкого", {"Наполните церуленовый фиал с покрытием в оранжевом пруду Мародона.","Полейте гнусь-лозу из полного церуленового фиала, выгнав ядовитую лозу.","Исцелите 8 растений, убивая ядовитые лозы, и вернитесь к Варку Боевому Шраму в Деревню Ночных Охотников."}},
[7041] = {"Порча Злоязыкого", {"Наполните церуленовый фиал с покрытием в оранжевом пруду Мародона.","Полейте гнусь-лозу из полного церуленового фиала, выгнав ядовитую лозу.","Исцелите 8 растений, убивая ядовитые лозы, и вернитесь к Талендрии из Высоты Найджела."}},
[7042] = {"Кража угощения Зимнего Покрова", {"Найдите агента Пастбищ Дымного Леса, пропавшего в снежных районах Альтеракских гор."}},
[7043] = {"Прохладное чувство юмора", {"Отыщите украденные лакомства и верните их Кардену Деньгозвону в Стальгорн. Лакомства должны быть у Омерзительного Гринча, который бродит где-то среди заснеженных вершин Альтеракских гор."}},
[7044] = {"Легенды Мародона", {"Добудьте две части скипетра Келебраса – жезл Келебраса и бриллиант Келебраса.","Найдите способ поговорить с Келебрасом."}},
[7045] = {"Пастбища Дымного Леса благодарят тебя!", {"Поговорите с Дедушкой Зимой в Стальгорне."}},
[7046] = {"Скипетр Келебраса", {"Помогите Селебрасу Искупленному воссоздать скипетр Селебраса.","По завершении ритуала снова обратитесь к нему."}},
[7061] = {"Праздник Зимнего Покрова", {"Прочитайте книгу \"Праздник Зимнего Покрова\". Отнесите книгу Кэрну Кровавому Копыту в Громовой Утес."}},
[7062] = {"Повод для праздника", {"Поговорите с историком Карником в лиге Исследователей в Стальгорне о празднике Зимнего Покрова."}},
[7063] = {"Праздник Зимнего Покрова", {"Прочтите книгу \"Праздник Зимнего Покрова\", узнайте побольше о происхождении праздника. Закончив читать книгу, отнесите ее королю Магни Бронзобороду в Стальгорн."}},
[7064] = {"Яблочко от яблоньки...", {"Убейте принцессу Терадрас и вернитесь к Селендре неподалеку от Деревни Ночных Охотников в Пустошах."}},
[7065] = {"Яблочко от яблоньки...", {"Убейте принцессу Терадрас и вернитесь к хранителю Марандису на Высоту Найджела в Пустоши."}},
[7066] = {"Семя Жизни", {"Найдите в Лунной поляне Ремулоса и отдайте ему Семя Жизни."}},
[7067] = {"Инструкции кентавра-парии", {"Прочтите инструкции кентавра-парии, добудьте из Мародона амулет Соединения и верните его кентавру-парии из южной части Пустошей."}},
[7068] = {"Фрагменты осколка сумрака", {"Соберите в Мародоне 10 фрагментов осколков сумрака и отнесите их Утель'наю в Оргриммар."}},
[7070] = {"Фрагменты осколка сумрака", {"Соберите в Мародоне 10 фрагментов осколков сумрака и отнесите их верховному магу Тервошу в Терамор на побережье Пылевых топей."}},
[7081] = {"Кладбища Альтеракской долины", {"Захватите кладбище, затем возвращайтесь к сержанту Даргену Грозовой Вершине в Альтеракские горы."}},
[7082] = {"Кладбища долины Альтерака", {"Захватите кладбище, затем возвращайтесь к капралу Тике Кровавому Рыку в Альтеракские горы."}},
[7101] = {"Башни и бункеры", {"Захватите башню врага и возвращайтесь к капралу Тике Кровавому Рыку в Альтеракские горы."}},
[7102] = {"Башни и бункеры", {"Уничтожьте вымпел на вражеской башне или бункере и возвращайтесь к сержанту Даргену Грозовой Вершине в Альтеракские горы."}},
[7121] = {"Интендант", {"Поговорите с интендантом клана Грозовой Вершины."}},
[7122] = {"Захват рудника", {"Захватите рудник, который не принадлежит Грозовой Вершине, и возвращайтесь к сержанту Даргену Грозовой Вершине в Альтеракские горы."}},
[7123] = {"Разговор с интендантом", {"Поговорите с интендантом клана Северного Волка."}},
[7124] = {"Захват рудника", {"Захватите рудник и возвращайтесь к капралу Тике Кровавому Рыку в Альтеракские горы."}},
[7141] = {"Битва за Альтерак", {"Отправляйтесь в Альтеракскую долину, сразитесь с генералом Орды Дрек'Таром и возвращайтесь к проспектору Камнетерке в Альтеракские горы."}},
[7142] = {"Битва за Альтерак", {"Отправляйтесь в Альтеракскую долину и убейте генерала дворфов, Вандара Грозовую Вершину. После этого возвращайтесь к Вогге Смертной Хватке в Альтеракские горы."}},
[7161] = {"Испытательные земли", {"Отправляйтесь в пещеры Дикой Лапы, расположенные на юго-востоке от основной базы в Альтеракской долине, и добудьте вымпел клана Северного Волка. Отнесите выпмел клана Северного Волка воеводе Лаггронду."}},
[7162] = {"Испытательные земли", {"Отправляйтесь в Пещеру Ледяного Крыла, которые расположены в Альтеракской долине, на юго-западе от Дун Болдара, и добудьте вымпел Грозовой Вершины. Отдайте вымпел лейтенанту Мурпу в Альтеракских горах."}},
