[7181] = {"Легенда о Корраке", {"Согласно легенде, вождь могущественного племени троллей Секиры Зимы периодически появляется на поле боя, внося хаос и опустошение в жизнь обитателей Альтеракской долины.","Если Коррак появится, убейте его и возвращайтесь к воеводе Лаггронду."}},
[7201] = {"Последняя Стихия", {"Отправляйтесь в Глубины Черной горы и добудьте 10 мер сущности Стихий. Стоит начать поиски с големов и их создателей. Вивиан Лаграв также бормотала что-то про элементалей."}},
[7202] = {"Коррак Кровопуск", {"Согласно легенде, вождь могущественного племени троллей Секиры Зимы периодически появляется на поле боя, внося хаос и опустошение в жизнь обитателей Альтеракской долины.","Если вам удастся найти Коррака, убейте его и возвращайтесь к лейтенанту Мурпу в Альтеракские горы."}},
[7221] = {"Разговор с геологом Камнетеркой", {"Поговорите с геологом Камнетеркой в гарнизоне Дун Болдара."}},
[7222] = {"Разговор с Воггой Смертобоем", {"Поговорите с Воггой в крепости Северного Волка."}},
[7223] = {"Обломки брони", {"Принесите 20 обломков брони Марготу Подземной Кузне в Дун Болдар."}},
[7224] = {"Вражеский трофей", {"Принесите 20 обломков брони кузнецу Регзару в деревню Северного Волка."}},
[7241] = {"Защита клана Северного Волка", {"Отправляйтесь в Альтеракскую долину, расположенную в Альтеракских горах. Найдите воеводу Лаггронда у входа в тоннель и поговорите с ним, чтобы начать свою военную карьеру в клане Северного Волка. Альтеракская долина находится к северу от деревни Мельница Таррен у подножия Альтеракских гор."}},
[7261] = {"Королевское право", {"Отправляйтесь в Альтеракскую долину, к предгорьям Хилсбрада. Найдите лейтенанта Мурпа рядом со входом в туннель и поговорите с ним.","Во славу Бронзеборода!"}},
[7281] = {"Братская любовь", {"Отправляйтесь на территорию Грозовой Вершины и убейте командира Карла Филипса. После выполнения задания возвращайтесь к командиру Луи Филипсу."}},
[7282] = {"Братская любовь", {"Отправляйтесь на территорию клана Северного Волка и убейте командира Луи Филипса. После выполнения задания возвращайтесь к командиру Карлу Филипсу."}},
[7301] = {"Свергнутые властители небес", {"Отправляйтесь в крепость Северного Волка и поищите командиров звена Слидора, Змейера и Ичмана. Вернитесь с отчетом к командиру Даффи."}},
[7302] = {"Свергнутые властители небес", {"Выясните, что стало с командирами звена Гусом, Джезтором и Малвериком. Вернитесь с отчетом к командиру Мулфорту."}},
[7321] = {"Нежный черепаховый суп", {"Принесите 10 кусков черепашьего мяса и немного смягчающих пряностей Кристофу Френчу на Мельницу Таррен."}},
[7341] = {"Честная сделка", nil},
[7342] = {"Стрелы – для неженок!", nil},
[7361] = {"Благосклонность племени Черного Копья", {"Вам поручили убивать в Альтеракской долине ночных эльфов.","Убив ночного эльфа, вернитесь с его отрубленной головой к Нажьяку Проклятуну в крепость Северного Волка."}},
[7362] = {"Союзник тауренов", {"Убивайте гномов в Альтеракской долине.","Убив гнома, принесите Раваку Зловещему Тотему в крепость Северного Волка пучок волос гнома."}},
[7363] = {"Человечья жизнь", {"Вам поручили убивать людей в Альтеракской долине.","Убив человека, принесите командору Луи Филипсу (который бродит между линией фронта и крепостью Северного Волка) осколок человеческой кости.","Окончить человеческие страдания совсем несложно!"}},
[7364] = {"Награда Гномрегана", {"Убивайте тауренов в Альтеракской долине.","Убив таурена, вернитесь к Дирку Надувалло в Дун Болдар с копытом таурена."}},
[7365] = {"Реквием Оленьего Шлема", {"Ваше задание – убивать в Альтеракской долине троллей.","Убейте тролля и возвращайтесь к Атраманису в Дун Болдар с амулетом тролля из племени Черного Копья."}},
[7366] = {"Милосердие архиепископа", {"Ваше задание – убивать в Альтеракской долине Отрекшихся.","Убейте Отрекшегося и возвращайтесь к Карлу Филипсу (он ходит между лесопилкой и рудником) с сердцем Отрекшегося."}},
[7367] = {"Обезвредить угрозу", {"Найдите эксперта по взрывчатым веществам из клана Северного Волка и убейте его. После завершения задания возвращайтесь к эксперту по взрывчатым веществам из клана Грозовой Вершины."}},
[7368] = {"Обезвредить угрозу", {"Найдите эксперта по взрывчатым веществам из клана Грозовой Вершины и убейте его. После завершения задания возвращайтесь к эксперту по взрывчатым веществам из клана Северного Волка."}},
[7381] = {"Возвращение Коррака", {"В случае, если Коррак Кровопуск возвратится на Поле брани, найдите его и уничтожьте.","Принесите череп Коррака воеводе Лаггронду в предгорья Хилсбрада."}},
[7382] = {"Коррак Бессмертный", {"В случае, если Коррак Кровопуск возвратится на Поле брани, найдите его и уничтожьте.","Принесите череп Коррака лейтенанту Хаггердину в предгорья Хилсбрада."}},
[7383] = {"Венец Земли", {"Наполните аметистовый фиал и принесите его Коритрасу Лунному Гневу в Доланаар."}},
