[7441] = {"Пузиллин и старейший Аж'Тордин", {"Отправляйтесь в Забытый Город и отыщите беса Пузиллина. Любыми доступными средствами убедите Пузиллина отдать вам книгу заклинаний Аж'Тордина.","Если добудете книгу, вернитесь к Аж'Тордину в павильон Лорисс в Фераласе."}},
[7461] = {"Древнее безумие", {"Перебейте стражей, которые охраняют 5 столпов, снабжающих энергией Тюрьму Бессмер'тера. После того как столпы угаснут, силовое поле, удерживающее Бессмер'тера, рассеется.","Войдите в Тюрьму Бессмер'тера и уничтожьте злого демона, который находится внутри. И, наконец, сразитесь с принцем Тортелдрином в Читальне.","Если задание будет выполнено успешно, вернитесь во двор, к прародительнице Шен'дралар."}},
[7462] = {"Сокровище Шен'дралар", {"Вернитесь в Читальню и найдите сокровище Шен'дралар. Получите свою награду!"}},
[7463] = {"Магический напиток", {"Ступайте в Квартал Криводревов Забытого Города и убейте элементаля воды Гидротварь. Вернитесь в Читальню к сказителю Лидросу и принесите ему сущность Гидротвари."}},
[7481] = {"Эльфийские легенды", {"Отыщите в Забытом Городе Тельмия Сновидца. Вернитесь в лагерь Мохаче к ведуну Королуску и сообщите ему все, что вам удалось узнать."}},
[7482] = {"Эльфийские легенды", {"Отыщите в Забытом Городе Тельмия Сновидца. Вернитесь в крепость Оперенной Луны к исследовательнице Рунический Шип и сообщите ей все, что вам удалось узнать."}},
[7483] = {"\"Манускрипт Скорости\"", {"Принесите сказителю Лидросу в Забытом Городе \"Манускрипт Скорости\", 1 безупречный черный алмаз, 2 больших сияющих осколка и 2 образца крови героев, чтобы получить магический камень стремительности."}},
[7484] = {"\"Манускрипт Средоточия\"", {"Принесите сказителю Лидросу в Забытом Городе \"Манускрипт Средоточия\", 1 безупречный черный алмаз, 4 больших сияющих осколка и 2 образца шкуры Тени, чтобы получить магический камень сосредоточения."}},
[7485] = {"\"Манускрипт Защиты", {"Принесите сказителю Лидросу в Забытом Городе \"Манускрипт Защиты\", 1 безупречный черный алмаз, 2 больших сияющих осколка и 1 потертый шовный материал поганища, чтобы получить магический камень защиты."}},
[7486] = {"Награда для героя", {"Заберите свою награду из сундука Гидраксиса."}},
[7488] = {"Сеть Лефтендрис", {"Принесите сеть Лефтендрис Латроникусу Лунному Копью в Крепость Оперенной Луны в Фераласе."}},
[7489] = {"Сеть Лефтендрис", {"Принесите сеть Лефтендрис Тало Терновому Копыту в Лагере Мохаче в Фераласе."}},
[7490] = {"Победа Орды", {"Отнесите голову Ониксии Траллу в Оргриммар."}},
[7491] = {"На виду у всех", {"Найдите властителя Рунтака в Аллее Силы."}},
[7492] = {"Лагерь Мохаче", {"Поговорите с Тало Терновым Копытом в Лагере Мохаче в Фераласе."}},
