[7508] = {"Ковка Кель-Серрара", {"Отдайте тусклый и плоский эльфийский клинок сказителю Лидросу."}},
[7509] = {"Ковка Кель-Серрара", {"Заставьте Ониксию дохнуть своим огненным дыханием на потускневший древний клинок. После этого схватите раскаленный древний клинок. Имейте в виду, что раскаленным он останется ненадолго, так что медлить не следует!","Последнее, что нужно сделать – это убить драконицу и вонзить раскаленный древний клинок в ее труп.","Сделайте это – и Кель-Серрар навеки станет вашим!"}},
[7541] = {"Служба Орде", nil},
[7562] = {"Мор'зул Кровопуск", {"Поговорите с Мор'зулом Кровопуском в Пылающих степях."}},
[7563] = {"Ярость крови", {"Принесите 30 бутылок крови яростных зверей Мор'зулу Кровопуску в Пылающие степи."}},
[7564] = {"Дикоглаз", {"Принесите ларец Крови Горзиеки Дикоглазу в Пылающие степи."}},
[7581] = {"Тюремные кандалы", {"Отправляйтесь в Забытый Город, что в Фераласе и добудьте 15 порций крови сатиров из племени Исчадий Природы, что живут в Квартале Криводревов. Вернитесь к Дайо из Гниющего шрама по выполнении поручения."}},
[7582] = {"Тюремная камера", {"Отправляйтесь к Теснине Зловещего Шепота в Зимних ключах и добудьте 5 слез Хедерины у демонов Хедерины, что населяют эту теснину. Вернитесь к Дайо из Гниющего шрама по выполнении поручения."}},
[7583] = {"Заточение", {"Отправляйтесь в Гниющий шрам и и найдите командира стражников ужаса.","При помощи светящегося кристалла заточения поймайте командира стражников ужаса. Будьте готовы к отчаянным атакам и сопротивлению, поскольку демон будет пытаться избежать заточения.","Если вы добьетесь успеха, отнесите пленного стражника ужаса Дайо в Гниющий шрам."}},
[7601] = {"Что хочет Ниби", {"Поговорите с Бесенком в Оскверненном лесу."}},
[7602] = {"Безупречная субстанция Скверны", {"Бесенок из Оскверненного леса просит вас принести ему три порции безупречной субстанции скверны из трех различных мест.","Безупречной субстанцией в Азшаре владеют сатиры из племени Легаши. В Джеденаре она есть у джеженарских легионеров. Стражи Скверны в Выжженных землях владеют этой субстанцией в своих краях.","Добудьте три типа безупречной субстанции Скверны и возвращайтесь к Бесенку в Оскверненный лес."}},
[7603] = {"Огненное ядро Крошиуса", {"Обыщите долину Рваных Ран в Оскверненном лесу и найдите останки Крошиуса. Когда вы их найдете, поднесите к ним пламень Скверны и подождать, пока Крошиус не восстанет из праха. Когда инфернал очнется, убейте его и возьмите из его тела огненное ядро Крошиуса.","Принесите огненное ядро Крошиуса Ниби Всемогущему в Оскверненный лес."}},
[7604] = {"Заключение договора", {"Подпишите договор с представителем братства Тория Локтосом Недобрым Торговцем, если вам нужен чертеж сульфуронского молота."}},
[7621] = {"Предупреждение", nil},
[7622] = {"Баланс Света и Тьмы", {"Спасите жизни 50 крестьян прежде, чем 15 из них будут убиты. Поговорите с Эрис Тайнопламень по выполнению задания.","Число убитых крестьян можно узнать из почты Смерти."}},
[7623] = {"Лорд Гиблопуст", {"Купите зелья Теней у Горзиеки в Пылающих степях.","Приняв зелья, пройдите в Джеденар и поговорите с лордом Гиблопустом."}},
[7624] = {"Улатек Предатель", {"Сразитесь с Улатеком и принесите сердце предателя лорду Гиблопусту в Джеденар."}},
[7625] = {"Зоротианская звездная пыль", {"Купите зоротианскую звездную пыль у Ур'Дана. Отнесите ее Горзиеки Дикоглазу в Пылающие степи."}},
[7626] = {"Колокол Детмуры", {"Отнесите 10 эликсиров Силы Теней Горзиеки Дикоглазу в Пылающие степи."}},
[7627] = {"Колесо Черного Похода", {"Отнесите 6 больших блестящих осколка и 25 кусков руды черного железа Дикоглазу в Пылающие степи."}},
[7628] = {"Свеча Судного Дня", {"Принесите 35 шкур черного дракона Горзиеки Дикоглазу в Пылающие степи."}},
[7629] = {"Доставка беса", {"Отнесите беса в бутылке в алхимическую лабораторию Некроситета. После создания пергамента верните бутылку Горзиеки Дикоглазу."}},
[7630] = {"Арканит", {"Принесите 3 арканитовых слитка Горзиеки в Пылающие степи."}},
[7631] = {"Ксоротианский конь погибели", {"Прочтите инструкции Мор'зула. Призовите ксоротианского коня погибели, одолейте его и подчините его дух."}},
[7632] = {"Древний лист", {"Найдите хозяина древнего окаменелого древесного листа. Удачи, <имя>, мир велик."}},
[7633] = {"Посвящение", nil},
[7634] = {"Перетянутый жилами лист древня", {"Хастат Древний просит вас принести ему жилу взрослого синего дракона. По выполнении задания возвращайтесь к Хастату в Оскверненный лес."}},
[7635] = {"Подходящая тетива", {"Стома Древний просит вас принести ему жилу взрослого черного дракона. Добыв жилу, возвращайтесь к Стоме в Оскверненный лес."}},
[7636] = {"Ветвь ходячего Древа", {"Вы должны найти и уничтожить следующих четверых демонических разносчиков порчи :","Симон Совратительницу.","Клинфрана Безумного.","Соленора Убийцу.","Арториуса Судьбоносного.","Уничтожьте этих тварей и возвращайтесь к Вартусу Древнему в Оскверненный лес с их головами.","В поисках местонахождения этих демонов сверяйтесь с окаменевшей корой.","Вы ОБЯЗАНЫ выполнить это задание в одиночку."}},
[7637] = {"Жертва необходима", {"Доберитесь до Стальгорна и получите курильницу экзорцизма у Верховного жреца Рохана. За курильницу нужно будет внести плату в размере 150 золотых монет."}},
[7638] = {"Лорд Грейсон Тенелом", {"Поговорите с лордом Грейсоном Тенеломом в Храмовом квартале Штормграда."}},
[7639] = {"Демонстрация истинного правосудия", {"Принесите курильницу экзорцизма лорду Грейсону Тенелому в Храмовый квартал Штормграда."}},
[7640] = {"Изгнание призраков из Долины Ужаса", {"Используйте курильницу экзорцизма для вызова из-под земли призраков, мучающих Долину Ужаса. Когда убьете 25 духов долины Ужаса, вернитесь к лорду Грейсону Тенелому в Храмовый квартал Штормграда."}},
[7641] = {"Работа Гриманда Элмора", {"Поговорите с Гримандом Элмором в Квартале Дворфов Штормграда."}},
[7642] = {"Сбор необходимого", {"Принесите Гриманду Элмору в Квартал Дворфов Штормграда 40 отрезов рунной ткани, 6 арканитовых слитков, 10 цветков слезы Артаса, 5 сосудов со святой водой Стратхольма и 150 золотых монет."}},
[7643] = {"Дух древнего коня", {"Купите лошадиный корм для Духа древнего коня у Меридет Карлсон в Южнобережье.","Убейте Тендриса Криводрева в башне Забытого Города в Фераласе и освободите духа. Накормите духа кормом и получите благословление для арканитового чепрака."}},
[7644] = {"Благословенный арканитовый чепрак", {"Вернитесь вместе с благословенным арканитовым чепраком к лорду Грейсону Тенелому в Храмовый квартал Штормграда."}},
[7645] = {"Обогащенный манной лошадиный корм", {"Принесите 20 витаминизированных печений из манны – ключевых составляющих обогащенного манной лошадиного корма – Меридет Карлсон в Южнобережье в предгорьях Хилсбрада. Продаются они только в Серебряном Рассвете.","Кроме того, понадобится дать ей 50 золотых, чтобы успокоить ее ущемленное самолюбие."}},
[7646] = {"Магический кристалл", {"Принесите азеротский алмаз и безупречный черный алмаз лорду Грейсону Тенелому в Храмовый квартал Штормграда."}},
[7647] = {"Суд и расплата", {"Используйте магический кристалл в центре подвала Главного склепа в Некроситете. Победите вызванных духов, а затем – рыцаря смерти Темного Терзателя, который появится после поражения духов.","Верните падшему скакуну Темного Терзателя его спасенную душу и покройте его благословенным зачарованным чепраком."}},
[7648] = {"Шедевр Гриманда", {"Покажите арканитовый чепрак лорду Грейсону Тенелому в Храмовом квартале Штормграда."}},
