[8778] = {"Взрывчатка для дружины Стальгорна", {"Принесите 6 бутылок воспламеняющей смеси, 5 бочонков гоблинского ракетного топлива и 10 порций концентрированного взрывчатого вещества чародею Пружине Форсунки возле Улья Зора в Силитусе. Кроме того, принесите тыловое задание IV."}},
[8779] = {"Материалы для гадания", {"Принесите 1 большой сияющий осколок, 1 большой светозарный осколок и 1 огромный изумруд геологу Злой Шутке в крепости Кенария в Силитусе. Также принесите тыловое задание V."}},
[8780] = {"Комплекты брони", {"Принесите 8 комплектов грубой брони и 8 накладок из толстой кожи Джанеле Твердомолот на заставу дружины Стальгорна возле улья Зора в Силитусе. Кроме того, сдайте тыловую задачу VII."}},
[8781] = {"Нехватка оружия", {"Принесите 2 палаша лунной стали Джанеле Твердомолот на заставу дружины Стальгорна возле Улья Зора в Силитусе. Кроме того, сдайте тыловую задачу VI."}},
[8782] = {"Поставки формы", {"Принесите 1 кусок луноткани, 2 рулона рунной ткани и 1 железношелковую паутину Зовущей из клана Гордый Рог в крепости Кенария в Силитусе. Кроме того сдайте тыловую задачу VIII."}},
