[1740] = {"Die Kugel von Soran'ruk", nil, {"Sucht 3 Fragmente von Soran'ruk und 1 großes Fragment von Soran'ruk und bringt sie zu Doan Karhan im Brachland."}},
[1758] = {"Foliant der Kabale", nil, {"Sprecht mit Krom Starkarm in Eisenschmiede."}},
[1781] = {"Der Foliant der Offenbarung", nil, {"Bringt das Symbol des Lebens zu Gazin Tenorm in Sturmwind."}},
