[1786] = {"Der Foliant der Offenbarung", nil, {"Nehmt das Symbol des Lebens und erweckt Henze Faulk in Elwynn wieder zum Leben."}},
[1787] = {"Der Foliant der Offenbarung", nil, {"Beschafft eine Schrift der Defias von den abtrünnigen Hexern der Defias in Elwynn und kehrt zu Gazin Tenorm in Sturmwind zurück."}},
