[1825] = {"Gespräch mit Thun'grim", nil, {"Sprecht mit Thun'grim Brandblick im Brachland."}},
[1838] = {"Brutale Rüstung", nil, {"Bringt Thun'grim Brandblick 15 rauchige Eisenblöcke, 10 pulverisierte Azurite, 10 Eisenbarren und 1 Phiole mit Phlogiston."}},
[1839] = {"Ula'elek und die brutalen Stulpen", nil, {"Sprecht mit Ula'elek in Durotar."}},
[1841] = {"Velora Nachtens und die brutalen Beinschützer", nil, {"Sprecht in Unterstadt mit Velora Nachtens."}},
[1842] = {"Satyrhufe", nil, {"Bringt 7 ungespaltene Satyrhufe zu Ula'elek in Sen'jin in Durotar."}},
[1858] = {"Die Zerschmetterte Hand", nil, {"Stehlt Tazans Schlüssel, öffnet seinen Ranzen damit und bringt den Inhalt zu Therzok in der Kluft der Schatten in Orgrimmar."}},
[1859] = {"Therzok", nil, {"Sprecht mit Therzok in der Kluft der Schatten in Orgrimmar."}},
